    "contracts/Swapper.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import "@openzeppelin/contracts-upgradeable/token/ERC20/extensions/IERC20MetadataUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/security/ReentrancyGuardUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/security/PausableUpgradeable.sol";
import "./AccessControlProxy/UUPSAccessControlUpgradeable.sol";
import "./interfaces/ISwapper.sol";


contract Swapper is ISwapper, UUPSAccessControlUpgradeable, ReentrancyGuardUpgradeable, PausableUpgradeable {

    using EnumerableSetUpgradeable for EnumerableSetUpgradeable.AddressSet;

    EnumerableSetUpgradeable.AddressSet private accounts;

    IERC20MetadataUpgradeable public token;

    mapping(address => uint256) public received;

    uint256[] public amountRaisedTillDay;
    uint256[] public uniqueHoldersTillDay;
    uint256[] public dailyTimestamp;

    string public contractName;

    uint256 public accountLimit;
    uint256 public pricePerToken;
    uint256 public startTimestamp;
    address public vaultAddress;
    uint256 public fundsRaised;
    uint256 public lastDailyUpdate;

    uint256 internal decimals;


    /// @custom:oz-upgrades-unsafe-allow constructor
    constructor() {
        _disableInitializers();
    }

//    function initialize(string memory _contractName, address _ownerAddress, address _token, uint256 _swapperStandardPrice, uint256 _accountBuyLimit, address _vaultAddress) initializer public virtual {
//        __Swapper_init(_contractName, _ownerAddress, _token, _swapperStandardPrice, _accountBuyLimit, _vaultAddress);
//    }

    /// @notice construct the presale contract
    function __Swapper_init(string memory _contractName, address _ownerAddress, address _token, uint256 _swapperStandardPrice, uint256 _accountBuyLimit, address _vaultAddress) onlyInitializing internal virtual {
        __UUPSAccessControlUpgradeable_init();
        __ReentrancyGuard_init();
        __Pausable_init();
        __Swapper_init_unchained(_contractName, _ownerAddress, _token, _swapperStandardPrice, _accountBuyLimit, _vaultAddress);
        _pause();
        transferOwnership(_ownerAddress);
    }

    function __Swapper_init_unchained(string memory _contractName, address _ownerAddress, address _token, uint256 _swapperStandardPrice, uint256 _accountBuyLimit, address _vaultAddress) onlyInitializing internal virtual {
        contractName = _contractName;
        pricePerToken = _swapperStandardPrice;
        token = IERC20MetadataUpgradeable(_token);
        startTimestamp = block.timestamp;
        decimals = uint256(IERC20MetadataUpgradeable(_token).decimals());
        accountLimit = _accountBuyLimit;
        vaultAddress = _vaultAddress;
    }

    /// @notice pause the presale
    function pause(bool _shouldPause) public onlyOwner {
        if(_shouldPause) {
            _pause();
        } else {
            _unpause();
            if (startTimestamp == 0) {
                startTimestamp = block.timestamp;
            }
        }
    }

    /// @notice get the presale data
    /// @return the price, hard limit, duration and hold limit
    function data() external view returns (uint256, uint256) {
        return (pricePerToken, accountLimit);
    }

    function setSaleToken(address _tokenAddress) external onlyOwner {
        token = IERC20MetadataUpgradeable(_tokenAddress);
        decimals = uint256(IERC20MetadataUpgradeable(_tokenAddress).decimals());
    }

    /// @notice set the hold limit
    /// @param walletLimit_ the hold limit per holder of the presale
    function setWalletLimit(uint256 walletLimit_) external onlyOwner {
        accountLimit = walletLimit_;
    }

    /// @notice set the price of the token
    /// @param price_ the price per token
    function setPrice(uint256 price_) external onlyOwner {
        pricePerToken = price_;
    }

    /// @notice withdraw the native currency stored in the contract
    function withdrawBNB(address to_, uint256 amount_) external onlyOwner {
        (bool success,) = payable(to_).call{value: amount_}("");
        if (!success) revert("payment failed");
    }

    function setVault(address _vault) external onlyOwner {
        vaultAddress = _vault;
    }

    /// @notice withdraw tokens stored in the contract
    function withdrawTokens(address token_, address to_, uint256 amount_) external onlyOwner {
        IERC20MetadataUpgradeable(token_).transfer(to_, amount_);
    }

    /// @notice lookup the amount received from a given account
    function receivedFrom(address account_) external view returns (uint256) {
        return received[account_];
    }

    receive() external payable virtual whenNotPaused {
        _receive(msg.sender, msg.value);
    }

    fallback() external {

    }

    function _calculateTokenQuantity(address _account, uint256 _value) internal view virtual returns(uint256 tokenQuantity, uint256 bnbCost){
        tokenQuantity = _value * 10 ** decimals / pricePerToken;
        uint256 availableTokens = token.balanceOf(address(this));
        tokenQuantity = tokenQuantity > availableTokens ? availableTokens : tokenQuantity;
        if(accountLimit > 0){
            tokenQuantity = tokenQuantity + received[_account] > accountLimit ? accountLimit - received[_account] : tokenQuantity;
        }
        bnbCost = tokenQuantity * pricePerToken / 10 ** decimals;
    }

    function _distributePayment(uint256 _value) internal virtual {
        (bool success,) = payable(vaultAddress).call{value: _value}("");
        if(!success){
            revert("Transfer Failed");
        }
    }

    function _receive(address account_, uint256 value_) internal virtual nonReentrant {

        (uint256 tokensRequested, uint256 bnbPaid) = _calculateTokenQuantity(account_, value_);
        bool success;

        if(tokensRequested == 0){
            revert("Insufficient token out value");
        }

        fundsRaised += bnbPaid;
        if(block.timestamp >= lastDailyUpdate + 1 days) {
            dailyTimestamp.push(block.timestamp);
            amountRaisedTillDay.push(bnbPaid);
            if(accounts.add(account_)){
                uniqueHoldersTillDay.push(1);
            }
            lastDailyUpdate = block.timestamp;
        } else {
            amountRaisedTillDay[amountRaisedTillDay.length - 1] += bnbPaid;
            if(accounts.add(account_)){
                uniqueHoldersTillDay[uniqueHoldersTillDay.length - 1] += 1;
            }
        }

        received[account_] += tokensRequested;

        token.transfer(account_, tokensRequested);
        if(value_ != bnbPaid){
            (success,) = payable(account_).call{value: value_ - bnbPaid}("");
            if(!success){
                revert("Transfer Failed");
            }
        }
        _distributePayment(bnbPaid);
    }

    function getStatsForWeek() external view returns(uint256[] memory timePeriods, uint256[] memory totalFundsRaised, uint256[] memory totalUniqueHolders) {
        uint256 historyLength = amountRaisedTillDay.length;
        uint256 arraySize = historyLength >= 7 ? 7 : historyLength;
        totalFundsRaised = new uint256[](arraySize);
        totalUniqueHolders = new uint256[](arraySize);
        timePeriods = new uint256[](arraySize);

        for(uint256 i = 0; i < arraySize; ++i){
            totalFundsRaised[i] = amountRaisedTillDay[i + historyLength - arraySize];
            totalUniqueHolders[i] = uniqueHoldersTillDay[i + historyLength - arraySize];
            timePeriods[i] = dailyTimestamp[i + historyLength - arraySize];
        }
    }

    function getTotalWalletCount() external view returns (uint256) {
        return accounts.length();
    }

    /**
     * @dev This empty reserved space is put in place to allow future versions to add new
     * variables without shifting down storage in the inheritance chain.
     * See https://docs.openzeppelin.com/contracts/4.x/upgradeable#storage_gaps
     */
    uint256[49] private __gap;
}
"
