    "contracts/core/nft/RewardNft.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts-upgradeable/token/ERC721/ERC721Upgradeable.sol";
import "@openzeppelin/contracts-upgradeable/token/ERC721/extensions/ERC721EnumerableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/token/ERC721/extensions/ERC721BurnableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/access/AccessControlUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/Initializable.sol";
import "@openzeppelin/contracts-upgradeable/proxy/utils/UUPSUpgradeable.sol";
import "@openzeppelin/contracts/utils/Counters.sol";
import "@openzeppelin/contracts/token/ERC20/extensions/IERC20Metadata.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "../libraries/LibPrice.sol";
import "../../util/IPriceFeed.sol";
import "../../util/IDebtTokenStake.sol";

abstract contract RewardNft is
    ERC721Upgradeable,
    ERC721EnumerableUpgradeable,
    ERC721BurnableUpgradeable,
    AccessControlUpgradeable,
    UUPSUpgradeable
{
    struct PriceConfig {
        uint256 max;
        uint256 min;
        uint256 price;
        int256 increase;
        uint256 threshold;
    }
    using Counters for Counters.Counter;
    using SafeERC20 for IERC20Metadata;
    bytes32 public constant MINTER_ROLE = keccak256("MINTER_ROLE");
    uint256 public maxSupply;
    IERC20Metadata[] public paymentTokens;
    PriceConfig[] public priceConfigs;
    string public contractURI;
    IDebtTokenStake public tokenStake;

    IPriceFeed private priceFeed;
    string private baseURI;
    Counters.Counter private _tokenIdCounter;
    mapping(uint256 => string) private _tokenToKey;
    mapping(string => uint256) private _keyToToken;
    bool public purchasingEnabled;

    function __RewardNft_init(
        string memory name_,
        string memory symbol_,
        uint256 maxSupply_,
        string memory baseURI_,
        string memory contractURI_,
        PriceConfig[] memory priceConfigs_,
        IERC20Metadata[] memory paymentTokens_,
        IPriceFeed priceFeed_,
        IDebtTokenStake tokenStake_,
        bool purchasingEnabled_
    ) internal onlyInitializing {
        __ERC721_init(name_, symbol_);
        __ERC721Enumerable_init();
        __ERC721Burnable_init();
        __AccessControl_init();
        __UUPSUpgradeable_init();

        _grantRole(DEFAULT_ADMIN_ROLE, msg.sender);
        _grantRole(MINTER_ROLE, msg.sender);

        for (uint256 i = 0; i < priceConfigs_.length; i++) {
            priceConfigs.push(priceConfigs_[i]);
        }
        setBaseURI(baseURI_);
        setContractURI(contractURI_);
        maxSupply = maxSupply_;
        paymentTokens = paymentTokens_;
        priceFeed = priceFeed_;
        tokenStake = tokenStake_;
        purchasingEnabled = purchasingEnabled_;
    }

    function setTokenStake(IDebtTokenStake debtTokenStake_) public onlyRole(DEFAULT_ADMIN_ROLE) {
      tokenStake = debtTokenStake_;
    }

    function _baseURI() internal view override returns (string memory) {
        return baseURI;
    }

    function setBaseURI(
        string memory baseURI_
    ) public onlyRole(DEFAULT_ADMIN_ROLE) {
        baseURI = baseURI_;
    }

    function setContractURI(
        string memory contractURI_
    ) public onlyRole(DEFAULT_ADMIN_ROLE) {
        contractURI = contractURI_;
    }

    function safeMint(
        address to,
        string calldata key,
        IERC20Metadata paymentToken
    ) public {
        require(isValidPaymentToken(paymentToken),"RewardNft: invalid payment token");
        require(purchasingEnabled, "RewardNft: purchasing is not enabled");
        require(tokenStake.maxMintable(to) > balanceOf(to), "RewardNft: maximum mints reached for this address");

        // TODO: MINT THE REWARD TOKEN
        uint256 tokenPrice = priceInTokens(paymentToken);
        paymentToken.safeTransferFrom(_msgSender(), address(this), tokenPrice);

        _safeMintWithKey(to, key);
    }

    function setPaymentTokens(
        IERC20Metadata[] calldata paymentTokens_
    ) public onlyRole(DEFAULT_ADMIN_ROLE) {
        paymentTokens = paymentTokens_;
    }

    function isValidPaymentToken(
        IERC20 paymentToken
    ) public view returns (bool) {
        for (uint256 i = 0; i < paymentTokens.length; i++) {
            if (paymentTokens[i] == paymentToken) {
                return true;
            }
        }

        return false;
    }

    // this function is here to support legacy purchasing
    function safeMintBatch(
        address to,
        string[] calldata keys,
        string calldata
    ) public onlyRole(MINTER_ROLE) {
        for (uint256 i = 0; i < keys.length; i++) {
            _safeMintWithKey(to, keys[i]);
        }
    }

    function _safeMintWithKey(address to, string calldata key) private {
        require(!_keyExists(key), "RewardNft: key already minted");
        require(totalSupply() < maxSupply, "RewardNft: max supply reached");

        _tokenIdCounter.increment();
        uint256 tokenId = _tokenIdCounter.current();
        _tokenToKey[tokenId] = key;
        _keyToToken[key] = tokenId;

        _safeMint(to, tokenId);
    }

    function setPriceConfig(
        PriceConfig[] calldata priceConfigs_
    ) public onlyRole(DEFAULT_ADMIN_ROLE) {
        delete priceConfigs;
        for (uint256 i = 0; i < priceConfigs_.length; i++) {
            priceConfigs.push(priceConfigs_[i]);
        }
    }

    function priceInUsd() public view returns (uint256) {
        uint256 totalSupply = totalSupply();
        for (uint256 i = 0; i < priceConfigs.length; i++) {
            PriceConfig memory currentConfig = priceConfigs[i];
            if (
                totalSupply >= currentConfig.min &&
                totalSupply < currentConfig.max
            ) {
                if (
                    currentConfig.threshold == 0 || currentConfig.increase == 0
                ) {
                    return currentConfig.price;
                } else {
                    uint256 incrementAmount = (totalSupply -
                        currentConfig.min) / currentConfig.threshold;
                    return
                        uint256(
                            currentConfig.increase *
                                int256(incrementAmount) +
                                int256(currentConfig.price)
                        );
                }
            }
        }
        revert("RewardNft: no matching price config");
    }

    function priceInTokens(
        IERC20Metadata paymentToken
    ) public view returns (uint256) {
        require(isValidPaymentToken(paymentToken),"RewardNft: invalid payment token");
        return
            LibPrice.calculatePrice(
                priceInUsd(),
                priceFeed.getPrice(address(paymentToken)),
                priceFeed.decimals(),
                paymentToken.decimals()
            );
    }

    function setPurchasingEnabled(
        bool purchasingEnabled_
    ) public onlyRole(DEFAULT_ADMIN_ROLE) {
        purchasingEnabled = purchasingEnabled_;
    }

    function tokensOfOwner(
        address owner
    ) public view returns (uint256[] memory) {
        uint256 tokenCount = balanceOf(owner);
        uint256[] memory tokenList = new uint256[](tokenCount);
        for (uint256 i = 0; i < tokenCount; i++) {
            tokenList[i] = tokenOfOwnerByIndex(owner, i);
        }
        return tokenList;
    }

    function keyOf(uint256 tokenId) public view returns (string memory) {
        require(_exists(tokenId), "RewardNft: invalid token ID");
        return _tokenToKey[tokenId];
    }

    function tokenByKey(string memory key) public view returns (uint256) {
        require(_keyExists(key), "RewardNft: invalid key");
        return _keyToToken[key];
    }

    function _keyExists(string memory key) private view returns (bool) {
        return _keyToToken[key] != 0;
    }

    function _burn(uint256 tokenId) internal virtual override {
        super._burn(tokenId);

        string memory key = _tokenToKey[tokenId];
        delete _keyToToken[key];
        delete _tokenToKey[tokenId];
    }

    function _authorizeUpgrade(
        address newImplementation
    ) internal override onlyRole(DEFAULT_ADMIN_ROLE) {}

    function _beforeTokenTransfer(
        address from,
        address to,
        uint256 tokenId,
        uint256 batchSize
    ) internal override(ERC721Upgradeable, ERC721EnumerableUpgradeable) {
        super._beforeTokenTransfer(from, to, tokenId, batchSize);
    }

    function supportsInterface(
        bytes4 interfaceId
    )
        public
        view
        override(
            ERC721Upgradeable,
            ERC721EnumerableUpgradeable,
            AccessControlUpgradeable
        )
        returns (bool)
    {
        return super.supportsInterface(interfaceId);
    }

