    "contracts/ApeHeroDinoHatchery.sol": {
      "content": "// SPDX-License-Identifier: UNLICENSED
pragma solidity 0.8.9;

import "@openzeppelin/contracts/access/AccessControlEnumerable.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/security/Pausable.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC721/IERC721.sol";
import "@openzeppelin/contracts/utils/Strings.sol";

interface IPOW is IERC20 {
    function burn(uint amount) external;
}

interface INFT is IERC721 {
    function batchBurn(uint[] calldata tokenIds) external;
    function mint(address account, uint amount) external;
    function ownerOfAll(address account, uint[] calldata tokenIds) external returns (bool);
    function totalSupply() external view returns (uint);
    function wData(address account) external view returns (uint[] memory);
}

contract ApeHeroDinoHatchery is Ownable, AccessControlEnumerable, Pausable, ReentrancyGuard {
    enum SpeedupRate {
        QUARTER, HALF, FULL
    }

    struct EggIncubation {
        uint id; uint incubatedOn; uint hatchedOn; uint speedups; uint timeIncubated; uint timeRemained; bool readyToHatch;
    }

    struct AccountData {
        EggIncubation[] eggs; uint allowance; bool isApproved;
    }

    struct ContractData {
        bool paused;
        uint hatchedEggs; uint incubatingEggs; uint burnableBalance; uint burnedBalance;
        address powAddress; address eggAddress; address dinoAddress;
    }

    bytes32 public constant ADMIN = keccak256("ADMIN");
    bytes32 public constant OPERATOR = keccak256("OPERATOR");
    uint public BURNABLE_BALANCE;
    uint public BURNED_BALANCE;
    uint public MAX_SUPPLY = 10000;
    uint public INC_EGGS;
    uint public HATCHED_EGGS;

    // incubation
    uint public INC_START = 250 ether;
    uint public INC_SPEED_UP_25 = 500 ether;
    uint public INC_SPEED_UP_50 = 1000 ether;
    uint public INC_SPEED_UP_100 = 2500 ether;
    uint public INC_TIME = 2592000;

    mapping(uint => uint) private INCUBATION;
    mapping(uint => uint) private SPEED_UPS;
    mapping(uint => uint) private HATCHED;

    // other contracts
    address public POWAddress;
    address public EggAddress;
    address public DinoAddress;
    IPOW POW;
    INFT EGG;
    INFT DINO;

    // events
    event Incubated(address indexed account, uint eggId, uint timestamp);
    event Hatched(address indexed account, uint[] eggIds, uint timestamp);
    event FundsReceived(address account, uint amount, uint timestamp);
    event FundsDirectlyDeposited(address account, uint amount, uint timestamp);

    constructor(address pow) Ownable() {
        _grantRole(DEFAULT_ADMIN_ROLE, owner());
        _grantRole(ADMIN, owner());
        _grantRole(OPERATOR, owner());

        POWAddress = pow;
        POW = IPOW(pow);
    }

    // --- fallback/received --- //
    receive() external payable {
        emit FundsReceived(_msgSender(), msg.value, block.timestamp);
    }

    fallback() external payable {
        emit FundsDirectlyDeposited(_msgSender(), msg.value, block.timestamp);
    }

    // --- admins --- //
    function emergencyWithdraw(uint amount) external onlyRole(ADMIN) {
        payable(owner()).transfer(amount > 0 ? amount : address(this).balance);
    }

    function emergencyWithdrawToken(address token, uint amount) external onlyRole(ADMIN) {
        IERC20(token).transfer(owner(), amount > 0 ? amount : IERC20(token).balanceOf(address(this)));
    }

    function pause() external onlyRole(ADMIN) {
        _pause();
    }

    function unpause() external onlyRole(ADMIN) {
        _unpause();
    }

    function activateHatchery(address egg, address dino) external onlyRole(ADMIN) {
        EggAddress = egg;
        EGG = INFT(egg);
        DinoAddress = dino;
        DINO = INFT(dino);
    }

    function setIncubationDetails(uint incStart, uint incTime, uint speedUp25, uint speedUp50, uint speedUp100) external onlyRole(ADMIN) {
        if (incStart != INC_START && incStart > 0) {
            INC_START = incStart;
        }
        if (incTime != INC_TIME && incTime > 0) {
            INC_TIME = incTime;
        }
        if (speedUp25 != INC_SPEED_UP_25 && speedUp25 > 0) {
            INC_SPEED_UP_25 = speedUp25;
        }
        if (speedUp50 != INC_SPEED_UP_50 && speedUp50 > 0) {
            INC_SPEED_UP_50 = speedUp50;
        }
        if (speedUp100 != INC_SPEED_UP_100 && speedUp100 > 0) {
            INC_SPEED_UP_100 = speedUp100;
        }
    }

    function burn() external onlyRole(OPERATOR) {
        if (BURNABLE_BALANCE > 0) {
            POW.burn(BURNABLE_BALANCE);
            BURNED_BALANCE += BURNABLE_BALANCE;
            BURNABLE_BALANCE = 0;
        }
    }

    // --- utils --- //
    function _timeIncubated(uint eggId) internal view returns (uint) {
        return INCUBATION[eggId] > 0 ? block.timestamp - INCUBATION[eggId] : 0;
    }

    function _timeRemaining(uint eggId) internal view returns (uint) {
        if (HATCHED[eggId] > 0) {
            return 0;
        }
        uint _incubatedTime = _timeIncubated(eggId);
        if (_incubatedTime + SPEED_UPS[eggId] >= INC_TIME) {
            return 0;
        }

        return INC_TIME - (_incubatedTime + SPEED_UPS[eggId]);
    }

    function _readyToHatch(uint eggId) internal view returns (bool) {
        return HATCHED[eggId] == 0 && _timeRemaining(eggId) == 0;
    }

    function _calSpeedupCost(SpeedupRate rate) internal view returns (uint) {
        if (rate == SpeedupRate.QUARTER) {
            return INC_SPEED_UP_25;
        } else if (rate == SpeedupRate.HALF) {
            return INC_SPEED_UP_50;
        } else {
            return INC_SPEED_UP_100;
        }
    }

    function _calSpeedupTime(uint eggId, SpeedupRate rate) internal view returns (uint) {
        uint _speedups;
        uint _remaining = _timeRemaining(eggId);
        if (rate == SpeedupRate.QUARTER) {
            _speedups = (_remaining * 2500) / 1e4;
        } else if (rate == SpeedupRate.HALF) {
            _speedups = (_remaining * 5000) / 1e4;
        } else {
            _speedups = _remaining;
        }

        return _speedups;
    }

    function _hatchEggs(address account, uint[] memory eggIds) internal {
        uint _now = block.timestamp;
        uint _totalHatching = eggIds.length;

        for (uint _idx = 0; _idx < _totalHatching; _idx++) {
            HATCHED[eggIds[_idx]] = _now;
        }
        EGG.batchBurn(eggIds);
        DINO.mint(account, _totalHatching);
        INC_EGGS -= _totalHatching;
        HATCHED_EGGS += _totalHatching;

        emit Hatched(account, eggIds, _now);
    }

    // --- write --- //
    function incubateEggs(uint[] calldata eggIds) external whenNotPaused nonReentrant {
        address _account = _msgSender();
        require(EGG.ownerOfAll(_account, eggIds), "Cannot incubate unowned eggs");

        uint _now = block.timestamp;
        uint _totalEggs = eggIds.length;
        require(POW.balanceOf(_account) >= (_totalEggs * INC_START), "Insufficient POW balance");

        uint _totalIncubated;
        for (uint _idx = 0; _idx < eggIds.length; _idx++) {
            if (HATCHED[eggIds[_idx]] == 0 && INCUBATION[eggIds[_idx]] == 0) {
                INCUBATION[eggIds[_idx]] = _now;
                _totalIncubated += 1;
                emit Incubated(_account, eggIds[_idx], _now);
            }
        }
        INC_EGGS += _totalIncubated;

        uint _totalCost = _totalIncubated * INC_START;
        if (_totalCost > 0) {
            POW.transferFrom(_account, address(this), _totalCost);
            BURNABLE_BALANCE += _totalCost;
        }
    }

    function speedupIncubation(uint[] calldata eggIds, SpeedupRate speedup) external whenNotPaused nonReentrant {
        address _account = _msgSender();
        require(EGG.ownerOfAll(_account, eggIds), "Cannot speed up unowned eggs");

        uint _totalEggs = eggIds.length;
        uint _costPerItem = _calSpeedupCost(speedup);
        require(POW.balanceOf(_account) >= (_costPerItem * _totalEggs), "Insufficient POW balance");

        uint _speedupTime;
        uint _totalSpeedups;
        for (uint _idx = 0; _idx < _totalEggs; _idx++) {
            if (INCUBATION[eggIds[_idx]] > 0) {
                _speedupTime = _calSpeedupTime(eggIds[_idx], speedup);
                if (_speedupTime > 0) {
                    SPEED_UPS[eggIds[_idx]] += _speedupTime;
                    _totalSpeedups += 1;
                }
            }
        }

        uint _totalCost = _totalSpeedups * _costPerItem;
        if (_totalCost > 0) {
            POW.transferFrom(_account, address(this), _totalCost);
            BURNABLE_BALANCE += _totalCost;
        }

        // hatch if speed up 100%
        if (speedup == SpeedupRate.FULL) {
            _hatchEggs(_account, eggIds);
        }
    }

    function hatchEggs(uint[] calldata eggIds) external whenNotPaused nonReentrant {
        address _account = _msgSender();
        require(EGG.ownerOfAll(_account, eggIds), "Cannot hatch unowned eggs");

        bool _allReadyToHatch = true;
        for (uint _idx = 0; _idx < eggIds.length; _idx++) {
            if (!_readyToHatch(eggIds[_idx])) {
                _allReadyToHatch = false;
                break;
            }
        }
        require(_allReadyToHatch, "Not all provided eggs are ready to hatch");

        _hatchEggs(_account, eggIds);
    }

    function instaHatch(address account, uint[] calldata eggIds) external onlyRole(ADMIN) {
        require(EGG.ownerOfAll(account, eggIds), "Account does not own all eggs provided");
        require(POW.balanceOf(_msgSender()) >= (eggIds.length * (INC_START + INC_SPEED_UP_100)), "Insufficient POW balance");

        uint _totalHatching;
        for (uint _idx = 0; _idx < eggIds.length; _idx++) {
            if (HATCHED[eggIds[_idx]] == 0) {
                _totalHatching += 1;
            }
        }

        if (_totalHatching > 0) {
            uint _totalCost = _totalHatching * (INC_START + INC_SPEED_UP_100);
            POW.transferFrom(_msgSender(), address(this), _totalCost);
            BURNABLE_BALANCE += _totalCost;

            _hatchEggs(account, eggIds);
        }
    }

    // --- views --- //
    function incubatedEggInfo(uint eggId) public view returns (EggIncubation memory) {
        return EggIncubation({
            id : eggId, incubatedOn : INCUBATION[eggId], hatchedOn : HATCHED[eggId], speedups : SPEED_UPS[eggId],
            timeIncubated : _timeIncubated(eggId), timeRemained : _timeRemaining(eggId),
            readyToHatch : _readyToHatch(eggId)
        });
    }

    function eggsIncubation(uint[] memory eggIds) public view returns (EggIncubation[] memory) {
        EggIncubation[] memory _eggs = new EggIncubation[](eggIds.length);

        for (uint _idx = 0; _idx < _eggs.length; _idx++) {
            _eggs[_idx] = incubatedEggInfo(eggIds[_idx]);
        }

        return _eggs;
    }

    function wData(address account) public view returns (AccountData memory) {
        return AccountData({
            eggs : eggsIncubation(EGG.wData(account)),
            allowance : POW.allowance(account, address(this)),
            isApproved : EGG.isApprovedForAll(account, address(this))
        });
    }

    function aData() public view returns (AccountData memory) {
        return wData(_msgSender());
    }

    function cData() public view returns (ContractData memory) {
        return ContractData({
            paused : paused(),
            hatchedEggs : HATCHED_EGGS, incubatingEggs : INC_EGGS,
            burnableBalance : BURNABLE_BALANCE, burnedBalance : BURNED_BALANCE,
            powAddress : POWAddress, eggAddress : EggAddress, dinoAddress : DinoAddress
        });
    }
}
