contract AGTStakingContract is Ownable, ReentrancyGuard {
    using SafeMath for uint256;

    uint256 public constant SECONDS_IN_A_MONTH = 30 days;    

    uint256 public totalStaked;
    uint256 public totalRewardsPaid;
    uint256 public rewardPerSecondPerToken;
    uint256 private rewardPerSecondPerTokenStored;
    IERC20 public agtToken;
    IERC20 public usdtToken;
    uint256 public totalSupply;
    uint256 public revenueInUSDT;
    mapping(address => bool) private _isClaiming;
    uint256 public stakingStartTime;
    uint256 public stakingEndTime;
    mapping(address => uint256) public stakedBalance;
    mapping(address => uint256) public lastClaimedTime;
    mapping(address => uint256) public stakedTokens;
    mapping(address => uint256) public rewards;
    mapping(address => bool) public hasStaked;
    mapping(address => uint256) public lastUpdateTime;
    mapping (address => uint256) public unstakedTime;
    mapping(address => uint256) public unclaimedRewards;
    mapping(address => uint256) public claimedRewards;
    bool private _notEntered;

    address[] stakeholders;

    constructor(
        IERC20 _agtToken,
        IERC20 _usdtToken
    ) {
        for (uint256 i = 0; i < stakeholders.length; i++) {
            claimedRewards[stakeholders[i]] = 0;
        }
        agtToken = _agtToken;
        usdtToken = _usdtToken;
        totalSupply = 850000000 * (10 ** 18);
        revenueInUSDT = 1000000 * (10 ** 18);
        rewardPerSecondPerToken = 412967502;
        rewardPerSecondPerTokenStored = rewardPerSecondPerToken;
    }

    function setStakingPeriod(uint256 startTime, uint256 endTime) public onlyOwner {
        require(endTime > startTime, "Invalid staking period");
        stakingStartTime = startTime;
        stakingEndTime = endTime;

        // Update stakers' timestamps to the new starting period
        for (uint i = 0; i < stakeholders.length; i++) {
            lastClaimedTime[stakeholders[i]] = stakingStartTime;
        }
    }

    function stake(uint256 amount) external nonReentrant {
        require(!hasStaked[msg.sender], "You have already staked");
        require(block.timestamp >= stakingStartTime && block.timestamp < stakingEndTime, "Staking period has ended");
        require(amount > 0, "Amount must be greater than zero");
        require(agtToken.balanceOf(msg.sender) >= amount, "Insufficient balance");

        updateUserReward(msg.sender);
        
        // Add any unclaimed rewards to the user's current reward balance
        rewards[msg.sender] = rewards[msg.sender].add(calculateReward(msg.sender));
        
        // Reset the user's lastClaimedTime to the current block timestamp
        lastClaimedTime[msg.sender] = block.timestamp;

        agtToken.transferFrom(msg.sender, address(this), amount.mul(10**18));
        stakedBalance[msg.sender] = stakedBalance[msg.sender].add(amount.mul(10**18));
        stakedTokens[msg.sender] = stakedTokens[msg.sender].add(amount.mul(10**18));
        totalStaked = totalStaked.add(amount.mul(10**18));
        hasStaked[msg.sender] = true;
        stakeholders.push(msg.sender);
        
        
        emit Staked(msg.sender, amount);
    }

    function unstake() external nonReentrant {
        require(block.timestamp >= stakingEndTime, "Staking period has not ended yet");
        uint256 userReward = (calculateReward(msg.sender)).div(10**18);
        require(userReward < 1, "Reward balance must be less than 1");
        updateUserReward(msg.sender);
        uint256 amount = stakedBalance[msg.sender];
        require(amount > 0, "No staked tokens to withdraw");
        require(agtToken.balanceOf(address(this)) >= amount, "Insufficient balance in staking contract");

        // Update the user's unstakedTime to the current block timestamp
        unstakedTime[msg.sender] = block.timestamp;
        
        agtToken.transfer(msg.sender, amount);
        stakedBalance[msg.sender] = 0;
        totalStaked = totalStaked.sub(amount);
        
        emit Unstaked(msg.sender, amount);
    }


    function claimRewards() public  nonReentrant {
        require(!_isClaiming[msg.sender], "Claim already in progress");
        uint256 reward = calculateReward(msg.sender);
        require(reward > 0, "No rewards to claim");

        _isClaiming[msg.sender] = true;
        rewards[msg.sender] = 0;
        lastClaimedTime[msg.sender] = block.timestamp;
        _isClaiming[msg.sender] = false;

        require(usdtToken.transfer(msg.sender, reward), "Transfer failed");
        
        totalRewardsPaid = totalRewardsPaid.add(reward);

        emit RewardsClaimed(msg.sender, reward);
    }

    function calculateReward(address staker) public view returns (uint256) {
        uint256 stakerRewardPerSec = rewardPerSecondPerToken.mul(stakedBalance[staker]);
        uint256 timeDiff = block.timestamp.sub(lastClaimedTime[staker]);
        uint256 reward = stakerRewardPerSec.mul(timeDiff).div(10 ** 18);
        if (unstakedTime[staker] > 0 && unstakedTime[staker] > lastClaimedTime[staker]) {
            uint256 unclaimedReward = stakerRewardPerSec.mul(unstakedTime[staker].sub(lastClaimedTime[staker])).div(10 ** 18);
            reward = reward.add(unclaimedReward);
        }
        return reward;
    }

    function updateRewardPerSecondPerToken(uint256 _newRewardPerSecondPerToken) public onlyOwner {
        uint256 previousRewardPerSecondPerToken = rewardPerSecondPerToken;
        rewardPerSecondPerToken = _newRewardPerSecondPerToken;

        // Calculate the total rewards accumulated so far
        uint256 totalRewardsAccumulated = rewardPerSecondPerTokenStored.mul(block.timestamp.sub(lastUpdateTime[address(0)]));
        totalRewardsPaid = totalRewardsPaid.add(totalRewardsAccumulated);

        // Update the stored rewardPerSecondPerToken and emit the event
        rewardPerSecondPerTokenStored = _newRewardPerSecondPerToken;
        emit RewardPerSecondPerTokenUpdated(previousRewardPerSecondPerToken, _newRewardPerSecondPerToken);

        // Update the rewards for all stakers
        for (uint256 i = 0; i < stakeholders.length; i++) {
            address staker = stakeholders[i];
            updateUserReward(staker);
            uint256 newEarnedRewards = calculateReward(staker);
            uint256 previousEarnedRewards = rewards[staker];
            rewards[staker] = newEarnedRewards;
            unclaimedRewards[staker] = unclaimedRewards[staker].add(newEarnedRewards).sub(previousEarnedRewards);
        }
    }

    function updateUserReward(address user) internal {
        uint256 stakedAmount = stakedTokens[user];
        if (stakedAmount > 0) {
            uint256 previousReward = rewards[user];
            uint256 timeDelta = block.timestamp - lastUpdateTime[user];
            uint256 newReward = stakedAmount * rewardPerSecondPerToken * timeDelta.div(10 ** 18);
            rewards[user] = previousReward + newReward;
            lastUpdateTime[user] = block.timestamp;
            emit userRewardUpdated(user, stakedAmount, previousReward, rewards[user]);
        }
    }

    function recoverERC20(address tokenAddress, uint256 tokenAmount, address recipient) external onlyOwner {
        require(tokenAddress != address(agtToken), "Cannot withdraw the staking token");
        IERC20(tokenAddress).transfer(recipient, tokenAmount);
        emit Recovered(tokenAddress, tokenAmount, recipient);
    }



    event RevenueUpdated(uint256 previousRevenue, uint256 newRevenue);
    event userRewardUpdated(address indexed user, uint256 stakedAmount, uint256 previousReward, uint256 newReward);
    event Staked(address indexed user, uint256 amount);
    event Unstaked(address indexed user, uint256 amount);
    event RewardsClaimed(address indexed user, uint256 reward);
    event RewardPerSecUpdated(uint256 previousRewardPerSec, uint256 newRewardPerSec);
    event RewardPerSecondPerTokenUpdated(uint256 previousRewardPerSecondPerToken, uint256 newRewardPerSecondPerToken);
    event Recovered(address token, uint256 amount, address recipient);
    event LogUnstake(address indexed account, uint256 amount);



}
