      "content": "// SPDX-License-Identifier: BSD-3-Clause

pragma solidity = 0.8.19;

import "@openzeppelin/contracts@4.9.0/utils/Address.sol";
import "@openzeppelin/contracts@4.9.0/token/ERC20/extensions/IERC20Permit.sol";
import "@openzeppelin/contracts@4.9.0/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts@4.9.0/token/ERC20/utils/SafeERC20.sol";

/**
* Gyrowin Buyback, Burn & Reward Contract
* https://gyro.win
*/
interface GyrowinInternal {
    // GyrowinContract
    function burn(uint256 amount) external returns (bool);
    function totalSupply() external view returns (uint256);
    function MAX_TOTAL_SUPPLY() external view returns (uint256);

    // IUniswapV2Router
    function swapExactTokensForTokensSupportingFeeOnTransferTokens(uint256 amountIn, uint256 amountOutMin, address[] calldata path, address to, uint256 deadline) external;
}

contract GyrowinBuybackBurnReward {

    address private _owner;
    address private _operator;

    /// @notice address of the gyrowin token
    address public constant GYROWIN_ADDRESS = 0x77774A06271d6A305CAccDBc06f847DEF05c7777;
    GyrowinInternal constant PANCAKESWAP_V2_ROUTER = GyrowinInternal(0x10ED43C718714eb63d5aA57B78B54704E256024E);

    uint8 private constant DECIMALS = 18;

    uint256 private maxTotalSupply;
    uint256 public maxSupplyBurnLimit;

    uint256 public _buybackTotal; 
    uint256 public _burnTotal; 
    uint256 public _rewardTotal; 

    // treasury account
    address private _treasury;


    /**
     * @notice Construct a new Buyback & Burn
    */
    bool private _initialize;
    function initialize(address owner, address treasury) external {
        require(owner == address(0x05803c32E393765a204e22fCF560421729cbCA42), "GW: !owner");
        require(_initialize == false, "GW: initialized");
        _owner = owner;
        _operator = owner;

        // 5 billion
        maxTotalSupply = GyrowinInternal(GYROWIN_ADDRESS).MAX_TOTAL_SUPPLY();
        // 2 billion
        maxSupplyBurnLimit = maxTotalSupply * 40 / 100;

        // initial buyback usdt amount from old gw contract
        _buybackTotal = 40477 * 10 ** DECIMALS; 
        // initial burn gw amount from old gw contract
        _burnTotal = 25439655 * 10 ** DECIMALS; 

        _treasury = treasury;

        _initialize = true;
    }

    event BuybackBurnReward(uint256 indexed buyback, uint256 indexed burn, uint256 indexed reward, uint256 time);

    receive() payable external {}

    modifier onlyOwner() {
        require(_owner == _msgSender(), "GW: !owner"); _;
    }

    modifier onlyOperator() {
        require(_operator == _msgSender(), "GW: !operator"); _;
    }

    using SafeERC20 for IERC20;


    /// @notice buyback, burn and reward
    /// @dev operator can be contract or wallet address
    function buybackBurnReward(uint256 amountIn, uint256 amountOutMin, uint256 burnPercent, address[] calldata path, uint256 deadline) external onlyOperator() {
        require(amountIn <= IERC20(path[0]).balanceOf(address(this)), "GW: insufficient funds");

        uint256 balanceBefore = IERC20(path[1]).balanceOf(address(this));

        IERC20(path[0]).safeIncreaseAllowance(address(PANCAKESWAP_V2_ROUTER), amountIn);

        // buyback
        PANCAKESWAP_V2_ROUTER.swapExactTokensForTokensSupportingFeeOnTransferTokens(amountIn, amountOutMin, path, address(this), deadline);

        _buybackTotal += amountIn;

        // burn
        uint256 currentBurnAmount = IERC20(path[1]).balanceOf(address(this)) - balanceBefore;
        uint256 burnableAmount = GyrowinInternal(GYROWIN_ADDRESS).totalSupply() - maxSupplyBurnLimit;
        uint256 rewardAmount = 0;

        uint256 burned;
        ///@dev when burn amount excesds the 3 billion burn threshold
        if (currentBurnAmount > burnableAmount) {
            GyrowinInternal(GYROWIN_ADDRESS).burn(burnableAmount);
            _burnTotal += burnableAmount;

            // reward
            rewardAmount = currentBurnAmount - burnableAmount;
            IERC20(GYROWIN_ADDRESS).safeTransfer(_treasury, rewardAmount);
            _rewardTotal += rewardAmount;

            burned = burnableAmount;

        ///@dev buyback & burn without reward
        } else if (burnPercent == 10000) {
            // burn
            GyrowinInternal(GYROWIN_ADDRESS).burn(currentBurnAmount);
            _burnTotal += currentBurnAmount;

            burned = currentBurnAmount;

        ///@dev buyback & burn with reward
        } else if (burnPercent < 10000) {
            uint256 amountToBurn = (currentBurnAmount * burnPercent) / 10000;
            rewardAmount = currentBurnAmount - amountToBurn;

            // burn
            GyrowinInternal(GYROWIN_ADDRESS).burn(amountToBurn);
            _burnTotal += amountToBurn;

            // reward
            IERC20(GYROWIN_ADDRESS).safeTransfer(_treasury, rewardAmount);
            _rewardTotal += rewardAmount;

            burned = amountToBurn;
        }

        emit BuybackBurnReward(amountIn, burned, rewardAmount, block.timestamp);
    }


    /// @dev call by the owner modifier
    function isOwner() external view returns (address) {
        return _owner;
    }

    function isOperator() external view returns (address) {
        return _operator;
    }

    function isTreasury() external view returns (address) {
        return _treasury;
    }


    // update owner
    function updateOwner(address account) external onlyOwner() {
        _owner = account;
    }

    function updateOperator(address account) external onlyOwner() {
        _operator = account;
    }

    function updateTreasury(address account) external onlyOwner() {
        _treasury = account;
    }


    function _msgSender() internal view virtual returns (address payable) {
        return payable(msg.sender);
    }


    // transfer native token
    function transferNativeToken(uint256 amount, address payable to) external onlyOwner() {
        require(amount > 0, "GW: zero amount");
        require(to != address(0), "GW: can't be zero address");
        require(amount <= address(this).balance, 'insufficient funds');
        to.transfer(amount);
    }


    // transfer ERC-20 token 
    function transferERC20Token(address token, address to, uint256 amount) external payable onlyOwner() {
        require(amount > 0, "GW: zero amount");
        require(to != address(0), "GW: can't be zero address");
        require(amount <= IERC20(token).balanceOf(address(this)), 'insufficient funds');
        IERC20(token).safeTransfer(to, amount);
    }
}"
