{{
  "language": "Solidity",
  "sources": {
    "contracts/Dollar.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.4;

import "@openzeppelin/contracts/utils/math/SafeMath.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/utils/structs/EnumerableSet.sol";

import "./types/ERC20.sol";
import "./interfaces/IDollar.sol";
import "./interfaces/IRewardsPool.sol";
import "./libraries/PancakePairLib.sol";
import "./interfaces/IPancakeSwapPair.sol";
import "./types/DollarAccessControlled.sol";
import "./interfaces/IPancakeSwapRouter.sol";
import "./interfaces/IPancakeSwapFactory.sol";

interface IPriceProtector {
    function swapFee2UsdtToRewardPool(uint256 _fee) external;
}

interface IRoles {
    function isBizWhitelist(address _account) external view returns (bool);

    function isPurchaseWhitelist(address _account) external view returns (bool);

    function isRobot(address _account) external view returns (bool);

    function grantRobot(address _account) external;
}

contract Dollar is ERC20, IDollar, DollarAccessControlled {
    using SafeMath for uint256;
    using SafeERC20 for IERC20;
    using PancakePairLib for IPancakeSwapPair;
    using EnumerableSet for EnumerableSet.AddressSet;

    uint256 public constant BASE = 1e15;
    uint256 public constant MAX_SLIPPAGE_NUMERATOR = 16;

    IPancakeSwapPair public pair;
    IPancakeSwapRouter public router;
    IERC20 public usdtToken;
    IRewardsPool public rewardsPool;
    IPriceProtector public priceProtector;
    IRoles public roles;
    uint256 public limitPurchaseEndAt;
    uint256 public limitRobotBlockNumStartAt;
    // max is 16
    uint16 public slippageNumerator;

    /** price protection related **/
    uint256 public lastPriceWithBase;
    uint256 public priceExpiredAt;

    // default is 100 USDT
    uint256 public lotteryThresholdInUsdt;

    modifier requireNonZeroAddr(address _address) {
        require(_address != address(0), "Dollar: mustn't be zero address");
        _;
    }

    event TakeFee(
        address indexed payer,
        uint256 indexed receivable,
        uint256 indexed fee
    );

    event ChangeSlippage(uint16 current, uint16 pre);

    event ChangeLotteryThreshold(uint256 currentThreshold, uint256 pre);

    constructor(
        address _authority,
        address _iPancakeSwapRouter,
        address _usdtToken,
        address _rewardsPool,
        address _priceProtector,
        address _roles
    )
        ERC20("Dollar", "Dollar")
        DollarAccessControlled(IDollarAuthority(_authority))
        requireNonZeroAddr(_authority)
        requireNonZeroAddr(_iPancakeSwapRouter)
        requireNonZeroAddr(_usdtToken)
        requireNonZeroAddr(_rewardsPool)
        requireNonZeroAddr(_priceProtector)
        requireNonZeroAddr(_roles)
    {
        router = IPancakeSwapRouter(_iPancakeSwapRouter);
        address pairAddr = IPancakeSwapFactory(router.factory()).createPair(
            _usdtToken,
            address(this)
        );
        pair = IPancakeSwapPair(pairAddr);

        usdtToken = IERC20(_usdtToken);

        rewardsPool = IRewardsPool(_rewardsPool);

        lotteryThresholdInUsdt = uint256(100).mul(
            10**IERC20Metadata(address(usdtToken)).decimals()
        );

        priceProtector = IPriceProtector(_priceProtector);

        roles = IRoles(_roles);
    }

    function mint(address account_, uint256 amount_)
        external
        override
        onlyVault
    {
        _mint(account_, amount_);
    }

    function burn(uint256 amount) external override {
        _burn(msg.sender, amount);
    }

    function burnFrom(address account_, uint256 amount_) external override {
        _spendAllowance(account_, _msgSender(), amount_);
        _burn(account_, amount_);
    }

    function _transfer(
        address from,
        address to,
        uint256 amount
    ) internal virtual override {
        require(from != address(0), "ERC20: transfer from the zero address");
        require(to != address(0), "ERC20: transfer to the zero address");

        _beforeTokenTransfer(from, to, amount);

        uint256 fromBalance = _balances[from];
        require(
            fromBalance >= amount,
            "ERC20: transfer amount exceeds balance"
        );

        unchecked {
            _balances[from] = fromBalance - amount;
        }

        // take fee when user sells Dollars to pancake
        if (isValidDollarSeller(from, to, amount)) {
            // 0.08 dollarAmount is fee which will be transferred to reward pool
            uint256 fee = amount.mul(slippageNumerator).div(100);
            if (fee > 0) {
                // take fee
                amount = amount - fee;
                _balances[address(priceProtector)] += fee;

                emit Transfer(from, address(priceProtector), fee);
                emit TakeFee(from, amount, fee);

                priceProtector.swapFee2UsdtToRewardPool(fee);
            }
        }

        _balances[to] += amount;

        emit Transfer(from, to, amount);

        _afterTokenTransfer(from, to, amount);
    }

    function _beforeTokenTransfer(
        address from,
        address to,
        uint256 amount
    ) internal virtual override {
        // protect when the tx is in pancake only
        if (isDealInPancake(from, to)) {
            protectPrice();
        }

        forbiddenPurchase(from, to);

        forbiddenRobot(from, to);

        // participate lottery when user buy Dollars
        if (isValidDollarBuyer(from, to) && beyondLotteryThreshold(amount)) {
            rewardsPool.participateLottery(to, amount);
        }
    }

    // forbidden buying when time is not ready
    function forbiddenPurchase(address _from, address _to) private view {
        if (
            _from == address(pair) &&
            block.timestamp < limitPurchaseEndAt &&
            !roles.isPurchaseWhitelist(_to)
        ) {
            revert("Dollar: forbidden purchase");
        }
    }

    function forbiddenRobot(address _from, address _to) private {
        if (roles.isRobot(_from)) {
            revert("Dollar: forbidden robot");
        }

        // lazy setup limitRobotBlockNumStartAt
        if (
            limitRobotBlockNumStartAt == 0 &&
            limitPurchaseEndAt != 0 &&
            block.timestamp >= limitPurchaseEndAt
        ) {
            limitRobotBlockNumStartAt = block.number;
        }

        // avoid wrong forbidden
        if (
            roles.isPurchaseWhitelist(_from) ||
            roles.isBizWhitelist(_from) ||
            _from == address(priceProtector) ||
            _to == address(rewardsPool) ||
            _from == address(0) ||
            _to == address(0)
        ) {
            return;
        }

        if (
            limitRobotBlockNumStartAt > 0 &&
            block.number < limitRobotBlockNumStartAt + 4 &&
            block.timestamp >= limitPurchaseEndAt &&
            _from == address(pair)
        ) {
            // aim at receiver , dollar buyer
            roles.grantRobot(_to);
        }
    }

    function isDealInPancake(address _from, address _to)
        private
        view
        returns (bool)
    {
        return _from == address(pair) || _to == address(pair);
    }

    function protectPrice() private {
        (uint256 priceWithBase, ) = pair.spotPrice(address(this), BASE);
        if (priceWithBase == 0) {
            return;
        }

        if (priceExpiredAt <= today()) {
            lastPriceWithBase = priceWithBase;

            // expired tomorrow
            priceExpiredAt = today().add(1);

            // reset slippageNumerator
            uint16 pre = slippageNumerator;
            slippageNumerator = 8;
            emit ChangeSlippage(slippageNumerator, pre);
        }

        if (
            priceWithBase < lastPriceWithBase &&
            // use multiply instead of divide
            lastPriceWithBase.sub(priceWithBase).mul(100) >
            lastPriceWithBase.mul(15) &&
            slippageNumerator < MAX_SLIPPAGE_NUMERATOR
        ) {
            uint16 pre = slippageNumerator;
            slippageNumerator = slippageNumerator + 8;
            emit ChangeSlippage(slippageNumerator, pre);
        }
    }

    function updateLotteryThreshold(uint256 _amountInUsdt)
        external
        onlyGovernor
    {
        uint256 pre = lotteryThresholdInUsdt;
        lotteryThresholdInUsdt = uint256(_amountInUsdt).mul(
            10**IERC20Metadata(address(usdtToken)).decimals()
        );

        emit ChangeLotteryThreshold(lotteryThresholdInUsdt, pre);
    }

    function decimals() public view virtual override returns (uint8) {
        return 9;
    }

    // to is pair ,when user sells Dollar
    function isValidDollarSeller(
        address _sender,
        address _recipient,
        uint256 _amount
    ) internal view returns (bool) {
        return
            _recipient == address(pair) &&
            _sender != address(priceProtector) &&
            _sender != address(this) &&
            // whitelist should not be taken fee
            !roles.isBizWhitelist(_sender) &&
            _amount > 0;
    }

    // from is pair ,when user buy Dollar
    function isValidDollarBuyer(address _sender, address _recipient)
        internal
        view
        returns (bool)
    {
        return
            _sender == address(pair) &&
            // whitelist should not participate lottery
            !roles.isBizWhitelist(_recipient) &&
            // dollar does not participate lottery
            _recipient != address(this) &&
            _recipient != address(0);
    }

    function beyondLotteryThreshold(uint256 _amount)
        private
        view
        returns (bool)
    {
        (uint256 priceWithBase, ) = pair.spotPrice(address(this), BASE);
        return _amount.mul(priceWithBase).div(BASE) >= lotteryThresholdInUsdt;
    }

    function today() internal view returns (uint256) {
        return day(block.timestamp);
    }

    function day(uint256 timestamp) internal pure returns (uint256) {
        return timestamp / 1 days + 1;
    }

    function updateRewardsPool(address _rewardsPool)
        external
        onlyGovernor
        requireNonZeroAddr(_rewardsPool)
    {
        rewardsPool = IRewardsPool(_rewardsPool);
    }

    function updatePriceProtector(address _priceProtector)
        external
        onlyGovernor
        requireNonZeroAddr(_priceProtector)
    {
        priceProtector = IPriceProtector(_priceProtector);
    }

    function updateRoles(address _roles)
        external
        onlyGovernor
        requireNonZeroAddr(_roles)
    {
        roles = IRoles(_roles);
    }

    function updateLimitPurchaseEndAt(uint256 _limitPurchaseEndAt)
        external
        onlyGovernor
    {
        require(
            _limitPurchaseEndAt > limitPurchaseEndAt,
            "Dollar: must be greater"
        );

        limitPurchaseEndAt = _limitPurchaseEndAt;
    }
}
"
