    "contracts/libraries/PancakePairLib.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "../interfaces/IPancakeSwapPair.sol";
import "@openzeppelin/contracts/utils/math/SafeMath.sol";

library PancakePairLib {
    using SafeMath for uint256;

    function spotPrice(
        IPancakeSwapPair tradePair,
        address token,
        uint256 base
    ) internal view returns (uint256 price, bool isToken0) {
        (uint256 reserve0, uint256 reserve1, ) = tradePair.getReserves();
        if (reserve0 == 0 || reserve1 == 0) {
            return (0, false);
        }

        if (tradePair.token0() == address(token)) {
            price = reserve1.mul(base).div(reserve0);
            isToken0 = true;
        } else if (tradePair.token1() == address(token)) {
            price = reserve0.mul(base).div(reserve1);
            isToken0 = false;
        } else {
            revert("PancakePairLib: wrong pair");
        }
    }

    function isEnoughLiquidity(IPancakeSwapPair tradePair)
        internal
        view
        returns (bool)
    {
        (uint256 reserve0, uint256 reserve1, ) = tradePair.getReserves();
        return reserve1 > 0 && reserve0 > 0;
    }
}
"
    },
    "contracts/interfaces/IPancakeSwapPair.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

interface IPancakeSwapPair {
    event Approval(
        address indexed owner,
        address indexed spender,
        uint256 value
    );
    event Transfer(address indexed from, address indexed to, uint256 value);

    function name() external pure returns (string memory);

    function symbol() external pure returns (string memory);

    function decimals() external pure returns (uint8);

    function totalSupply() external view returns (uint256);

    function balanceOf(address owner) external view returns (uint256);

    function allowance(address owner, address spender)
        external
        view
        returns (uint256);

    function approve(address spender, uint256 value) external returns (bool);

    function transfer(address to, uint256 value) external returns (bool);

    function transferFrom(
        address from,
        address to,
        uint256 value
    ) external returns (bool);

    function DOMAIN_SEPARATOR() external view returns (bytes32);

    function PERMIT_TYPEHASH() external pure returns (bytes32);

    function nonces(address owner) external view returns (uint256);

    function permit(
        address owner,
        address spender,
        uint256 value,
        uint256 deadline,
        uint8 v,
        bytes32 r,
        bytes32 s
    ) external;

    event Mint(address indexed sender, uint256 amount0, uint256 amount1);
    event Burn(
        address indexed sender,
        uint256 amount0,
        uint256 amount1,
        address indexed to
    );
    event Swap(
        address indexed sender,
        uint256 amount0In,
        uint256 amount1In,
        uint256 amount0Out,
        uint256 amount1Out,
        address indexed to
    );
    event Sync(uint112 reserve0, uint112 reserve1);

    function MINIMUM_LIQUIDITY() external pure returns (uint256);

    function factory() external view returns (address);

    function token0() external view returns (address);

    function token1() external view returns (address);

    function getReserves()
        external
        view
        returns (
            uint112 reserve0,
            uint112 reserve1,
            uint32 blockTimestampLast
        );

    function price0CumulativeLast() external view returns (uint256);

    function price1CumulativeLast() external view returns (uint256);

    function kLast() external view returns (uint256);

    function mint(address to) external returns (uint256 liquidity);

    function burn(address to)
        external
        returns (uint256 amount0, uint256 amount1);

    function swap(
        uint256 amount0Out,
        uint256 amount1Out,
        address to,
        bytes calldata data
    ) external;

    function skim(address to) external;

    function sync() external;

    function initialize(address, address) external;
}
"
    },
    "contracts/types/DollarAccessControlled.sol": {
      "content": "// SPDX-License-Identifier: MIT-only
pragma solidity ^0.8.0;

import "../interfaces/IDollarAuthority.sol";

abstract contract DollarAccessControlled {
    /* ========== EVENTS ========== */

    event AuthorityUpdated(IDollarAuthority indexed authority);

    string UNAUTHORIZED = "UNAUTHORIZED"; // save gas

    /* ========== STATE VARIABLES ========== */

    IDollarAuthority public authority;

    /* ========== Constructor ========== */

    constructor(IDollarAuthority _authority) {
        authority = _authority;
        emit AuthorityUpdated(_authority);
    }

    /* ========== MODIFIERS ========== */

    modifier onlyGovernor() {
        require(msg.sender == authority.governor(), UNAUTHORIZED);
        _;
    }

    modifier onlyGuardian() {
        require(msg.sender == authority.guardian(), UNAUTHORIZED);
        _;
    }

    modifier onlyPolicy() {
        require(msg.sender == authority.policy(), UNAUTHORIZED);
        _;
    }

    modifier onlyVault() {
        require(msg.sender == authority.vault(), UNAUTHORIZED);
        _;
    }

    /* ========== GOV ONLY ========== */

    function setAuthority(IDollarAuthority _newAuthority)
        external
        onlyGovernor
    {
        authority = _newAuthority;
        emit AuthorityUpdated(_newAuthority);
    }
}
"
