  function setNftEvolutionsAndNutsConsumed(uint256 nftId, uint256 evos, uint256 nuts) external;
}

// File contracts/interfaces/IMhdaoSquad.sol

// Original license: SPDX_License_Identifier: MIT

interface IMhdaoSquad {
  struct Squad {
    address owner; // squad owner
    uint256 type_; // squad type: 1 for mouse, 2 for ghost, 3 for mixed
    uint256 size; // how many NFTs can be in the squad
    uint256 baseFarmPerBlock; // farm per block without synergy bonus
    uint256 synergyBonus; // synergy bonus
    uint256 squadBonus; // squad bonus
    uint256 squadTrait; // a trait that the squad has and can synergize with all NFTs there
    uint256 farmPerBlock; // farm per block with synergy bonus
    uint256 currentQuest; // current quest this squad is engaged in
    uint256 questStartedAt; // when the squad started farming this quest
    uint256 questEndsAt; // when the squad should finish this quest
    uint256[] nftIds; // a dynamic list of NFT ids in the squad
    uint256[] traits; // a dynamic list of possibly sinergetic traits in the squad
    uint256[] collections; // a dynamic list of possibly sinergetic collections in the squad
  }
}

// File contracts/interfaces/IBkFarmV18P9.sol

// Original license: SPDX_License_Identifier: MIT

interface IBkFarmV18P9 {
  // write the interface for the contract ../features/BkFarmV18.sol:
  function registerPlayer(address mentor) external;

  function addToSquad(uint256[] memory nftIds, uint256 squadId) external;

  function removeFromSquad(uint256[] memory nftIds, uint256 squadId) external;

  function clearSquad(uint256 squadId) external;

  function withdraw(uint256 amount) external;

  function setMentor(address mentorAddress) external;

  function transfer(address to, uint256 bct, uint256 eBct, uint256[] memory _resources) external;

  function transferEbct(address from, address to, uint256 amount) external;

  //################
  // UPDATER functions
  function startQuestFor(uint256 squadId, uint256 questId) external;

  function finishQuestFor(uint256 squadId) external;

  function cancelQuestFor(uint256 squadId) external;

  function addSquadToPlayer(address playerAddress, uint256 type_) external returns (uint256);

  function increaseSquadSize(uint256 squadId) external;

  function payWithBalance(
    address account,
    uint256 bctAmount,
    uint256[] memory _resources
  ) external returns (uint256[] memory);

  function spendBctFrom(address account, uint256 amount, bool ethereal) external;

  function transferInGameBctAndLootFrom(
    address from,
    address to,
    uint256 bctAmount,
    uint256[] memory _resources
  ) external;

  function recalculateSquadFarming(uint256 squadId) external;

  function addTo(address playerAddress, uint256 amount, uint256[] memory _resources) external;

  function setSquadType(uint256 squadId, uint256 type_) external;

  function setSquadTrait(uint256 squadId, uint256 squadTrait) external;

  function burnBct(uint256 amount) external;

  function transferFrom(address from, address to, uint256 bct, uint256 eBct, uint256[] memory _resources) external;

  function setQuestRewardsCalculator(address questRewardsCalculator_) external;

  function addBctToClaim(address playerAddress, uint256 amount) external;

  function getSquad(uint256 squadId) external view returns (IMhdaoSquad.Squad memory);
}

