// Exactly the same as OpoenZeppelin's, but without require() return strings:

interface IRecoverableErc20 {
  function transfer(address recipient, uint256 amount) external returns (bool);
}

contract AdministrableUpgradableP9 is Initializable, AccessControlUpgradeable, PausableUpgradeable {
  bytes32 internal constant OPERATOR_ROLE = keccak256("OPERATOR_ROLE");
  bytes32 internal constant UPDATER_ROLE = keccak256("UPDATER_ROLE");
  bytes32 internal constant VIP_ROLE = keccak256("VIP_ROLE");

  function __Administrable_init(address _operator) public initializer {
    __AccessControl_init();
    __Pausable_init();

    _setupRole(DEFAULT_ADMIN_ROLE, _operator);
    _setupRole(OPERATOR_ROLE, _operator);
    _setupRole(UPDATER_ROLE, _operator);
  }

  function _onlyAccountNotPausedOrUpdater(address account) internal view {
    require((account == msg.sender && !paused()) || hasRole(UPDATER_ROLE, msg.sender));
  }

  ////////////////////
  // Operator Functions:
  function pause() external onlyRole(OPERATOR_ROLE) {
    _pause();
  }

  function unpause() external onlyRole(OPERATOR_ROLE) {
    _unpause();
  }

  function recoverERC20(address tokenAddress, address to, uint256 amount) external onlyRole(OPERATOR_ROLE) {
    IRecoverableErc20(tokenAddress).transfer(to, amount);
  }
}

