contract BartStaking is Ownable {

  using SafeMath for uint256;
  using Address for address;

  address _stakingTokenAddress;
  IERC20 _stakingToken;
  uint256 _stakingMagnitude;

  address _rewardTokenAddress;
  IERC20 _rewardToken;

  string _name;

  constructor(string memory pName) {
    _name = pName;
  }

  function stakingToken() public view returns (address) {
    return _stakingTokenAddress;
  }

  function rewardToken() public view returns (address) {
    return _rewardTokenAddress;
  }

  function name() public view returns (string memory) {
    return _name;
  }

  mapping(address => uint256) _stakedAmount;
  mapping(address => uint256) _stakeEntry;
  mapping(address => uint256) _accured;
  uint256 _totalStaked;
  uint256 _totalReward;
  uint256 _totalAccured;

  uint256 _totalWalletsStaked;

  function totalStakedTokens() public view returns (uint256) {
      return _totalStaked;
  }

  function totalUnclaimedRewards() public view returns (uint256) {
      return _totalReward;
  }

  function totalWalletsStaked() public view returns (uint256) {
    return _totalWalletsStaked;
  }

  function setRewardToken(address _rewardAddr) external onlyOwner {
    _rewardTokenAddress = _rewardAddr;
    _rewardToken = IERC20(_rewardTokenAddress);
  }

  function setStakingToken(address _stakingAddr) external onlyOwner {
    _stakingTokenAddress = _stakingAddr;
    _stakingToken = IERC20(_stakingAddr);
    _stakingMagnitude = _stakingToken.totalSupply();
  }

  function rewardDeposited (uint256 rewardReceived) external {
    require(msg.sender == _rewardTokenAddress, "Can only be called directly by the reward token.");
    require(_totalStaked > 0, "No one has staked yet");

    deposited(rewardReceived);
  }

  function deposit(uint256 newReward) public {
    require(_totalStaked > 0, "No one has staked yet");

    uint256 initialRewardBalance = _rewardToken.balanceOf(address(this));
    _rewardToken.transferFrom(msg.sender, address(this), newReward);

    uint256 newRewardBalance = _rewardToken.balanceOf(address(this));
    uint256 rewardReceived = newRewardBalance - initialRewardBalance;

    deposited(rewardReceived);
  }

  function deposited(uint256 rewardReceived) internal {
    _totalReward = _totalReward + rewardReceived;
    _totalAccured = _totalAccured + rewardReceived * _stakingMagnitude / _totalStaked;
  }

  function stake(uint256 stakeAmount) public {
    require(_stakingToken.balanceOf(msg.sender) >= stakeAmount, "Insufficient balance");
    require(stakeAmount <= maxLeftToStake(msg.sender), "Staked cannot be > 50% of total holdings");

    uint256 initialStakedBalance = _stakingToken.balanceOf(address(this));
    _stakingToken.transferFrom(msg.sender, address(this), stakeAmount);
    uint256 newStakedBalance = _stakingToken.balanceOf(address(this));
    uint256 stakedReceived = newStakedBalance - initialStakedBalance;

    if(_stakedAmount[msg.sender] > 0) {
      _accured[msg.sender] = currentRewards(msg.sender);
    } else {
      _totalWalletsStaked = _totalWalletsStaked + 1;
    }

    _stakedAmount[msg.sender] = _stakedAmount[msg.sender] + stakedReceived;
    _totalStaked = _totalStaked + stakedReceived;

    _stakeEntry[msg.sender] = _totalAccured;
  }

  function unstakeAll() public {
    require(_stakedAmount[msg.sender] > 0, "You have no stake");

    _accured[msg.sender] = currentRewards(msg.sender);

    _stakingToken.transfer(msg.sender, _stakedAmount[msg.sender]);
    _totalStaked = _totalStaked - _stakedAmount[msg.sender];
    _stakedAmount[msg.sender] = _stakedAmount[msg.sender] - _stakedAmount[msg.sender];

    _stakeEntry[msg.sender] = _totalAccured;

    _totalWalletsStaked = _totalWalletsStaked - 1;
  }

  function harvest() public {
    require(currentRewards(msg.sender) > 0, "Insufficient accured reward");
    require(_stakingToken.balanceOf(msg.sender) >= _stakedAmount[msg.sender], "Insufficient holding tokens");

    _accured[msg.sender] = currentRewards(msg.sender);

    _rewardToken.transfer(msg.sender, _accured[msg.sender]);
    _totalReward = _totalReward - _accured[msg.sender];
    _accured[msg.sender] = _accured[msg.sender] - _accured[msg.sender];

    _stakeEntry[msg.sender] = _totalAccured;
  }

  function currentRewards(address addy) public view returns (uint256) {
    return _accured[addy] + _calculateReward(addy);
  }

  function currentStake(address addy) public view returns (uint256) {
    return _stakedAmount[addy];
  }

  function maxStakeAmount(address addy) public view returns (uint256) {
      return (_stakingToken.balanceOf(addy).add(_stakedAmount[addy])).div(2);
  }

  function maxLeftToStake(address addy) public view returns (uint256) {
      if(_stakingToken.balanceOf(addy) <= _stakedAmount[addy])
        return 0;
      return maxStakeAmount(addy).sub(_stakedAmount[addy]);
  }

  function percentageOfStakePool(address addy) public view returns (uint256) {
    return _percentageOfStakePool(_stakedAmount[addy], _totalStaked);
  }

  function percentageOfNewStake(address addy, uint256 amount) public view returns (uint256) {
    return _percentageOfStakePool(_stakedAmount[addy] + amount, _totalStaked + amount);
  }

  function _percentageOfStakePool(uint256 amount, uint256 total) private pure returns (uint256) {
    if(total == 0)
      return 0;
    return amount * (10 ** 4) / total;
  }

  function _calculateReward(address addy) private view returns (uint256) {
    return _stakedAmount[addy] * (_totalAccured - _stakeEntry[addy]) / _stakingMagnitude;
  }
}
