pragma solidity 0.8.4;

import "./Pausable.sol";
import "./Ownable.sol";
import "./INFTAirdrop.sol";
import "./INFTAirdropReceiver.sol";

contract NFTManager is Ownable, Pausable {
    INFTAirdrop internal nftAirdrop;

    address public holder;
    mapping(address => bool) public isAdmin;
    uint256 public defaultId;
    uint256 public defaultAmount;

    modifier onlyAdmin() {
        require(isAdmin[_msgSender()], "Not allowed");
        _;
    }

    constructor(address _nftAirdrop, address _holder, uint256 _defaultId, uint256 _defaultAmount) Ownable() Pausable() {
        nftAirdrop = INFTAirdrop(_nftAirdrop);
        holder = _holder;

        defaultId = _defaultId;
        defaultAmount = _defaultAmount;

        isAdmin[msg.sender] = true;

    }

    function setAdmin(address _manager, bool _status) public onlyOwner {
        isAdmin[_manager] = _status;
    }

    function setNFTAirdrop(address _nftAirdrop) external onlyOwner {
        nftAirdrop = INFTAirdrop(_nftAirdrop);
    }

    function setHolderNFTAirdrop(address _holder) external onlyOwner {
        holder = _holder;
    }

    function setDefaultId(uint256 _defaultId) external onlyOwner {
        defaultId = _defaultId;
    }

    function setDefaultAmount(uint256 _defaultAmount) external onlyOwner {
        defaultAmount = _defaultAmount;
    }

    function airdropTransfer(
        address[] calldata _recipients
    ) external onlyAdmin {
        for (uint256 i = 0; i < _recipients.length; i++) {
            address recipient = _recipients[i];

            // Check if the recipient already owns the token
            if (nftAirdrop.balanceOf(recipient, defaultId) == 0) {
                // Mint the token only if the recipient doesn't own it
                nftAirdrop.safeTransferFrom(holder, recipient, defaultId, defaultAmount, "");
            }
        }
    }
}
