   * @param _projectOwner Project owner address
   * @param _tgeAt TGE datetime in epoch
   * @param _tgeRatio_d2 TGE ratio in percent (2 decimal)
   * @param _startEndLinearDatetime Start & end Linear datetime in epoch
   */
  function createVesting(
    address _token,
    address _stable,
    address _projectOwner,
    uint256 _tokenPrice,
    uint256 _lastRefundAt,
    uint128 _tgeAt,
    uint128 _tgeRatio_d2,
    uint128[2] calldata _startEndLinearDatetime
  ) public virtual override onlyOwner whenNotPaused returns (address vesting) {
    bytes memory data = abi.encodeWithSelector(
      ILinearVestingV3.init.selector,
      _token,
      _stable,
      _projectOwner,
      _tokenPrice,
      _lastRefundAt,
      _tgeAt,
      _tgeRatio_d2,
      _startEndLinearDatetime
    );

    vesting = address(new BeaconProxy(beacon, data));

    allVestings.push(vesting);
    isKnown[vesting] = true;

    emit VestingCreated(vesting, allVestings.length - 1);
  }

  function setVestingGateway(address _vestingGateway) external virtual onlyOwner {
    require(_vestingGateway != address(0), 'bad');
    vestingGateway = _vestingGateway;
  }

  /**
   * @dev Set new stable token to be accepted
   * @param _stable New stable token address
   */
  function setStable(address _stable) external virtual override onlyOwner {
    require(_stable != address(0) && !isStableSupported(_stable), 'bad');

    allStables.push(_stable);
    stableIndex[_stable] = allStables.length - 1;
  }

  /**
   * @dev Remove stable token as refund
   * @param _stable Token address
   */
  function removeStable(address _stable) external virtual override onlyOwner {
    require(isStableSupported(_stable), '!found');

    uint256 indexToDelete = stableIndex[_stable];
    address addressToMove = allStables[allStables.length - 1];

    allStables[indexToDelete] = addressToMove;
    stableIndex[addressToMove] = indexToDelete;

    allStables.pop();
    delete stableIndex[_stable];
  }

  /**
   * @dev Pause factory activity
   */
  function togglePause() external virtual onlyOwner {
    if (paused()) {
      _unpause();
    } else {
      _pause();
    }
  }
}
