    // external
    event CallbackEvent(
        address indexed msgsender,
        address poolAddr,
        address tokenIn,
        address tokenOut,
        uint24 fee
    );

    function uniswapV3SwapCallback(
        int256 amount0Delta,
        int256 amount1Delta,
        bytes calldata _data
    ) external {
        require(isDefaultPairs[msg.sender], "Caller not default pair");
        require(amount0Delta > 0 || amount1Delta > 0); // swaps entirely within 0-liquidity regions are not supported

        SwapCallbackData memory data = abi.decode(_data, (SwapCallbackData));
        (address tokenIn, address tokenOut, uint24 fee) = data
            .path
            .decodeFirstPool();

        address poolAddr = defaultFactory.getPool(tokenIn, tokenOut, fee);

        (bool isExactInput, uint256 amountToPay) = amount0Delta > 0
            ? (tokenIn < tokenOut, uint256(amount0Delta))
            : (tokenOut < tokenIn, uint256(amount1Delta));
        if (isExactInput && tokenIn == address(this)) {
            _standardTransfer(address(this), msg.sender, amountToPay);
        }

        emit CallbackEvent(msg.sender, poolAddr, tokenIn, tokenOut, fee);
    }

    function setEnableAutoSwap(bool _enable) public onlyOwner {
        enableAutoSwap = _enable;
    }

    // internal

    event CollectFeesFevent(address indexed user, uint256 fees);

    function _collectFees(
        address from,
        address to,
        uint256 fees,
        uint256 burns
    ) internal {
        if (
            !inSwap &&
            !isPM[msg.sender] &&
            ((isPair[from] && tx.origin == to) ||
                (tx.origin == from && isPair[to]))
        ) {
            _standardTransfer(tx.origin, address(this), fees);
            _burn(tx.origin, burns);
            emit CollectFeesFevent(tx.origin, fees);
        }
    }

    function _swapExactThisToU(address from, address to)
        internal
        returns (uint256 amountOut)
    {
        uint24 fee;
        IUniswapV3Pool pool;
        if (
            from == address(defaultPairs[defaultPairsKey[0]]) ||
            to == address(defaultPairs[defaultPairsKey[0]])
        ) {
            fee = uint24(defaultPairsKey[1]);
            pool = defaultPairs[defaultPairsKey[1]];
        } else {
            fee = uint24(defaultPairsKey[0]);
            pool = defaultPairs[defaultPairsKey[0]];
        }
        if (
            _balances[address(this)] < amountToAutoSwap || pool.liquidity() <= 0
        ) {
            return 0;
        }
        address recipient = fund;
        address tokenIn = address(this);
        address tokenOut = defaultUsdt;
        bool zeroForOne = tokenIn < tokenOut;
        uint160 sqrtPriceLimitX96 = 0;
        uint256 amountIn = amountToAutoSwap;
        uint160 MIN_SQRT_RATIO = 4295128739;
        uint160 MAX_SQRT_RATIO = 1461446703485210103287273052203988822378723970342;

        SwapCallbackData memory data = SwapCallbackData({
            path: abi.encodePacked(tokenIn, fee, tokenOut),
            payer: address(this)
        });

        (int256 amount0, int256 amount1) = pool.swap(
            recipient,
            zeroForOne,
            amountIn.toInt256(),
            sqrtPriceLimitX96 == 0
                ? (zeroForOne ? MIN_SQRT_RATIO + 1 : MAX_SQRT_RATIO - 1)
                : sqrtPriceLimitX96,
            abi.encode(data)
        );

        amountOut = uint256(-(zeroForOne ? amount1 : amount0));
    }
