pragma solidity ^0.8.17;








// 因为 DEX Swap 时，除了主链币（BNB，ETH）外，其他币种，例如 USDT，不能兑换到代币合约地址，所以需要这个中转合约接收兑换的代币
contract TokenDistributor {
    constructor (address token) {
        //将代币全部授权给合约部署者，在这里是代币合约，让代币合约分配兑换到的代币资产
        IERC20(token).approve(msg.sender, type(uint256).max);
    }
}

contract CFLToken is ERC20, Ownable {
    using SafeMath for uint256;
    string private _name = 'CFL';
    string private _symbol = 'CFL';
    address private A_ADDRESS=0x691A330444ff73345F4D0A980Be2cC33AFd08c1A;  //交易分红百分之1指定地址
    address private airdropAddress=0x427Dd4f2E1133fBa59Efa19d5D8839Df7511865F; // 空投地址
    address private technologyAddress=0x57FABE8FBF295AA2054430B75Af3a4514386fe16; // 技术
    address private miningAddress=0xbfC604fc2a6d6139F05422ff0EF83F568a92A01d;//挖矿
    address private whiteListAddress=0x18c46CA1c81D03553E52e718f49d547B93e14567; // 白名单

    address private pairContractAddress; //交易所配对合约地址
    uint private transactionRate = 3; //百分比
    uint256 private _totalSupply = 90000000 * 10**uint256(decimals()); //发币总量
    address private usdtContractAddress = 0x55d398326f99059fF775485246999027B3197955;//0x4A82908FC685A88d4FBED73787f935773889A9C7;
    address private routerAddress = 0x10ED43C718714eb63d5aA57B78B54704E256024E;//0xD99D1c33F9fC3444f8101754aBC46c52416550D1;
    mapping (address => bool) private _isExcludedFromFees;
    IPancakeswapV2Router02 immutable _pancakeswapV2Router02;
    IPancakeswapV2Pair immutable _pancakeswapV2Pair;
    TokenDistributor private _tokenDistributor;
    address[] UserArray;
    mapping(address => uint) liquidity;
    mapping(address => uint256) userLpUsdt;
    mapping(address => uint256) userLpCfl;
    uint256 bonusAmount; // 分红金额
    bool private inSwap;
    modifier lockTheSwap {
        inSwap = true;
        _;
        inSwap = false;
    }
    


    constructor() ERC20(_name,_symbol) {
        
        _tokenDistributor = new TokenDistributor(usdtContractAddress);
        _pancakeswapV2Router02 = IPancakeswapV2Router02(routerAddress); //测试
        //IPancakeswapV2Router02 _pancakeswapV2Router = IPancakeswapV2Router02(0x10ED43C718714eb63d5aA57B78B54704E256024E);
        pairContractAddress = IPancakeswapV2Factory(_pancakeswapV2Router02.factory()).createPair(address(this), address(usdtContractAddress));
        _approve(address(this),address(_pancakeswapV2Router02),type(uint256).max);
        IERC20(usdtContractAddress).approve(address(_pancakeswapV2Router02), type(uint256).max);
       // _pancakeswapV2Router02 = IPancakeswapV2Router02(0x10ED43C718714eb63d5aA57B78B54704E256024E); // 正式
        _pancakeswapV2Pair = IPancakeswapV2Pair(pairContractAddress);
        _mint(_msgSender(), _totalSupply.mul(3).div(100));  //底池
        _mint(airdropAddress, _totalSupply.mul(3).div(100)); //空投地址
        _mint(whiteListAddress, _totalSupply.mul(3).div(100)); //捐赠
        _mint(miningAddress, _totalSupply.mul(88).div(100)); //挖矿
        _mint(technologyAddress, _totalSupply.mul(3).div(100));// 技术锁仓
        UserArray.push(_msgSender());
    }

    event ExcludeFromFees(address account , bool excluded);

    function excludeFromFees(address account, bool excluded) public onlyOwner {
        if(_isExcludedFromFees[account] != excluded){
            _isExcludedFromFees[account] = excluded;
            emit ExcludeFromFees(account, excluded);
        }
    }

    function contractTransfer(address to, uint256 amount) public onlyOwner returns(bool){
        super._transfer(address(this), to, amount);
        return true;
    }

    function getpairContractAddress() public view returns(address){
        return pairContractAddress;
    }

    function getBonusAmount() public view returns(uint256) {
        return bonusAmount;
    }

    function getAdministratorsAddress() view public returns(address){
        return owner();
    }

    function setAdministratorsAddress(address _addr)  public onlyOwner {
        transferOwnership(_addr);
    }

    function burn(uint256 amount) external {
        _burn(_msgSender(), amount);
    }

    function burnFrom(address from_, uint amount_) external {
        _approve(from_, _msgSender(), allowance(from_, _msgSender()).sub(amount_, "CFLToken: burn amount exceeds balance"));
        _burn(from_, amount_);
    }

    function _transfer(address from,address to,uint256 amount) internal virtual override {
        require(from != address(0), "ERC20: transfer from the zero address");
        require(to != address(0), "ERC20: transfer to the zero address");
        uint256 _amount = amount;//到账金额
        uint256 totalFee = amount.mul(transactionRate).div(100);
        uint256 buyFee = amount.mul(2).div(100);
        if(to == pairContractAddress || from == pairContractAddress){
            if(!_isExcludedFromFees[to] && !_isExcludedFromFees[from]){
                
                _amount = amount.sub(totalFee); 
                if(totalSupply() - amount.mul(1).div(100) >= 999999 * 10 ** 18){
                    _burn(from,amount.mul(1).div(100));
                }
                
                super._transfer(from,address(this),buyFee);
            }
        }
    
        super._transfer(from, to, _amount);
    }


    function handServiceFee() public {
        _approve(address(this),address(_pancakeswapV2Router02),balanceOf(address(this)));
        swapTokenForFund(balanceOf(address(this)));

    }




    //兑换成 USDT
    function swapTokenForFund(uint256 tokenAmount) private lockTheSwap {
        address[] memory path = new address[](2);
        path[0] = address(this);
        path[1] = address(usdtContractAddress);
        _pancakeswapV2Router02.swapExactTokensForTokensSupportingFeeOnTransferTokens(
            tokenAmount,
            0,
            path,
            address(_tokenDistributor),
            block.timestamp
        );

        //按照比例分配
        IERC20 USDT = IERC20(usdtContractAddress);
        uint256 usdtBalance = USDT.balanceOf(address(_tokenDistributor));
        USDT.transferFrom(address(_tokenDistributor), A_ADDRESS, usdtBalance.div(2));
        USDT.transferFrom(address(_tokenDistributor), address(this), usdtBalance.div(2));
        bonusAmount = bonusAmount + usdtBalance.div(2);
        if(UserArray.length > 0){
            uint256 hAmount = bonusAmount.div(UserArray.length);
            for (uint i = 0; i < UserArray.length; i++) {
                USDT.transfer(UserArray[i], hAmount);
                bonusAmount = bonusAmount.sub(hAmount);
            }
        }
    }

    function getUintPrice() public view returns(uint256){

        address tokenA = address(usdtContractAddress);
        address tokenB = address(address(this));
        (address token0,) = tokenA < tokenB ? (tokenA, tokenB) : (tokenB, tokenA);
        (uint _reserve0, uint _reserve1,) = _pancakeswapV2Pair.getReserves();
        uint256 uPrice;
        if (token0 == tokenA) {
           uPrice = _pancakeswapV2Router02.getAmountOut(1000000000000000000, _reserve1, _reserve0);
        } else {
           uPrice = _pancakeswapV2Router02.getAmountOut(1000000000000000000, _reserve0, _reserve1);
        }
        return uPrice.mul(9995).div(10000);
    }


    //获取USDT 单价
    function getCflPrice() public view returns(uint256){
        address tokenA = address(usdtContractAddress);
        address tokenB = address(address(this));
        (address token0,) = tokenA < tokenB ? (tokenA, tokenB) : (tokenB, tokenA);
        (uint _reserve0, uint _reserve1,) = _pancakeswapV2Pair.getReserves();
        uint256 cflPrice;
        if (token0 == tokenA) {
            cflPrice = _pancakeswapV2Router02.getAmountOut(1000000000000000000, _reserve0, _reserve1);
        } else {
            cflPrice = _pancakeswapV2Router02.getAmountOut(1000000000000000000, _reserve1, _reserve0);
        }
        return cflPrice.mul(9995).div(10000);
    }
    event donationLpUsdtLog(address _addr, uint amount , uint amountB);
    //组LP - 质押 USDT
    function donationLpUsdt(uint256 amount ,uint256 cAmount) public {     
        require(amount > 0,'Input error of donation amount');
        uint256 cflPrice = cAmount;
        
        require(amount <= IERC20(usdtContractAddress).allowance(_msgSender(), address(this)), "USDT Access denied");
        require(cflPrice <= allowance(_msgSender(), address(this)), "CFL Access denied");
        IERC20(usdtContractAddress).transferFrom(_msgSender(),address(this), amount);
        IERC20(address(this)).transferFrom(_msgSender(),address(this), cflPrice);
        
        for(uint i=0;i<UserArray.length ;i++){
            if(UserArray[i] == _msgSender()){
                delete UserArray[i];
            }
        }
       
        UserArray.push(_msgSender());
        (uint amountA, uint amountB, uint liquidit)  = _pancakeswapV2Router02.addLiquidity(
             usdtContractAddress, address(this), amount, cflPrice, 0, 0, _msgSender(), block.timestamp);
        userLpUsdt[_msgSender()] += amount;
        userLpCfl[_msgSender()] += cflPrice;
        liquidity[_msgSender()] = liquidit;
        emit donationLpUsdtLog(_msgSender(),amountA,amountB);
    } 

    //取消lp质押
    function clearDonationLp() public {
        uint pairLiquidity = getliquidity(_msgSender());
        liquidity[_msgSender()] = 0;
        removeLiquidityUsdt(pairLiquidity);
    }

    function removeLiquidityUsdt(uint pairLiquidity) public {
        require(IPancakeswapV2Pair(pairContractAddress).allowance(_msgSender(), address(this)) >= pairLiquidity ,"Authorization amount error");
        IPancakeswapV2Pair(pairContractAddress).transferFrom(_msgSender(), pairContractAddress, pairLiquidity);
        IPancakeswapV2Pair(pairContractAddress).burn(_msgSender());
    }

    function getliquidity(address addr) public view returns(uint){
        return liquidity[addr];
    }

   function getReserves() public view returns (uint256 amount0 ,uint256 amount1) {
        uint balance0 = IERC20(usdtContractAddress).balanceOf(pairContractAddress);
        uint balance1 = IERC20(address(this)).balanceOf(pairContractAddress);
        uint _pairTotalSupply = IERC20(pairContractAddress).totalSupply();
        amount0 = getliquidity(_msgSender()).mul(balance0) / _pairTotalSupply; // using balances ensures pro-rata distribution
        amount1 = getliquidity(_msgSender()).mul(balance1) / _pairTotalSupply; // using balances ensures pro-rata distribution
    }

    function getDonationLp() public view returns(uint256 [] memory arr){
        arr = new uint256[](3);
        (uint256 amount0, uint256 amount1) = getReserves();
        arr[0] = amount0;
        arr[1] = amount1;
        arr[2] = IERC20(pairContractAddress).balanceOf(_msgSender());
    }
}
