    "contracts/FlipCoin.sol": {
      "content": "// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.17;


import "@openzeppelin/contracts/utils/math/SafeMath.sol";
import "@chainlink/contracts/src/v0.8/VRFV2WrapperConsumerBase.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";
import "hardhat/console.sol";



contract FlipCoin is VRFV2WrapperConsumerBase{

    using SafeMath for uint256;

    event FlipCoinRequest(uint256 requestId);
    event FlipCoinResult(uint256 requestId , bool isWin);



    uint256 public contractbalance = address(this).balance;

    address public flipCoinOwner;
    uint256 public FeeRequireMin = 0.01 ether;
    uint256 public FeeRequireMax = 1.5 ether;
    enum BetOptions{ HEADS , TALES }

    //chainlink address for testnet sepolia  0x779877A7B0D9E8603169DdbD7836e478b4624789
    //chainlinkvrfaddress for testnet sepolia 0xab18414CD93297B0d12ac29E63Ca20f515b3DB46

    //chainlink address for testnet bsc  	0x84b9B910527Ad5C03A9Ca831909E21e236EA7b06
    //chainlinkvrfaddress for testnet bsc 	0x699d428ee890d55D56d5FC6e26290f3247A762bd

    //chainlink address for mainnet  bsc 	0x404460C6A5EdE2D891e8297795264fDe62ADBB75
    //chainlinkvrfaddress for mainnet bsc   0x721DFbc5Cfe53d32ab00A9bdFa605d3b8E1f3f42

    address constant chainlinkaddress = 0x404460C6A5EdE2D891e8297795264fDe62ADBB75;
    address constant vrfWrapperAddress = 	0x721DFbc5Cfe53d32ab00A9bdFa605d3b8E1f3f42;
    LinkTokenInterface link = LinkTokenInterface(chainlinkaddress);

    uint32 constant callBackGasLimit = 600000;
    uint32 constant numWords = 1;
    uint16 constant requestConfirmations = 3;

    struct ETHPool
    {
        bool isAdded;
        uint256 ETHBalance;
        uint256 AddedAt;
    }
    mapping(uint256 => ETHPool) public EthereumPool;

    struct flipCoinDetails
    {
        uint256 BettingFee;
        uint256 randomWords;
        address player;
        bool isWin;
        bool fullFilled;
        uint256 id;
        uint256 amount;
        BetOptions input;
    }

    struct storeAll {
          flipCoinDetails[] All;
    }

    mapping(uint256 => flipCoinDetails) public bettingDetails;

    //leader boards
    flipCoinDetails[] winnersLeaders;

    //users investment list
    mapping(address => storeAll)  history;

    //ids for pool
    mapping(address => uint256) public pool;
    //referrals
    mapping(address => address) public referral;

    //for the raffel

    struct Raffel {
      address player;
      uint256 round;
      uint256 pot; 
      uint256 price;
      bool win;
      uint dateEnd;
    }

    Raffel[] totalRaffels;

    uint256 public round;
    uint256 public ticketPrice;
    uint256 public winningPot;
    uint256 public tickets = 0;
    uint256 public endDate;

    mapping (address => mapping(uint256 => uint256)) public ticketsByOwner;
    //tracking winner should be player ticketId maps address maps index
    mapping(uint256 => mapping(address => uint256)) public trackWinner;
    bool public isRaffleOpen;

    event RaffleOpened(uint256 ticketPrice);
    event RaffleClosed();
    event TicketPurchased(address indexed buyer);
    event WinnerSelected(address winner);




    constructor() payable  VRFV2WrapperConsumerBase(chainlinkaddress, vrfWrapperAddress)
    {
        flipCoinOwner = msg.sender;
    }



    function changeBettingFee(uint256 _max, uint _min) public returns(string memory _response)
    {
        require(msg.sender==flipCoinOwner , "You are not auhteroized to change the Betting fee");
        FeeRequireMax = _max;
        FeeRequireMin = _min;
        return "Fees changed scucessfully";
    }

    function AddEthToContract(uint256 id) public payable returns(bool)
    {
        require(msg.sender == flipCoinOwner , "You ae not autheroized to send the ETH to contract");
        require(msg.value > 0, "0 value can't be added");

        EthereumPool[id] = ETHPool({
            isAdded: true,
            ETHBalance: msg.value,
            AddedAt: block.timestamp
        });

        return true;
    }

    // userside functions mappings , structs etc.
    function flipTheCoin(BetOptions selection, address _referral) external payable returns(uint256 requestValue)
    {   
        //require( FeeRequireMin <= msg.value, "Amount too small");
        //require(FeeRequireMax <= msg.value, "Amount too large");
        require(msg.value >= FeeRequireMin && msg.value <= FeeRequireMax, "Amount Error");
        uint256 requestId = requestRandomness(callBackGasLimit, requestConfirmations, numWords);


        bettingDetails[requestId] = flipCoinDetails({
            BettingFee: VRF_V2_WRAPPER.calculateRequestPrice(callBackGasLimit),
            randomWords: 0,
            player: msg.sender,
            isWin: false,
            fullFilled: false,
            id: requestId,
            amount: msg.value,
            input: selection
        });
        pool[msg.sender] = requestId;
        if(_referral != address(0)) {
           referral[msg.sender] = _referral;
        }
        emit FlipCoinRequest(requestId);

        return requestId;
    }




    function fulfillRandomWords(uint256 requestId, uint256[] memory randomwords) internal override
    {
        //require(bettingDetails[requestId].BettingFee > 0 , "Request Not Found");
       if(bettingDetails[requestId].BettingFee > 0 ) {
        bettingDetails[requestId].fullFilled = true;
        bettingDetails[requestId].randomWords = randomwords[0];

        BetOptions result = BetOptions.HEADS;

                if(randomwords[0] % 2 == 0)
                {
                    result = BetOptions.TALES;
                } 

                //bettingDetails[requestId].input == result 
                //randomwords[0] % 10 < 4
              if(randomwords[0] % 10 < 4) {     
                if(bettingDetails[requestId].input == result) {
                        bettingDetails[requestId].isWin=true;
                        winnersLeaders.push(bettingDetails[requestId]);
                        if(referral[bettingDetails[requestId].player] != address(0)) {
                        //divide winning by referrer and referred
                        uint256 amountPlayed = bettingDetails[requestId].amount*2;
                        uint256 earnedRef =  amountPlayed.mul(3)/100;

                        payable(referral[bettingDetails[requestId].player]).transfer(earnedRef);
                        payable(bettingDetails[requestId].player).transfer(bettingDetails[requestId].amount*2);
                        //push to referrer history
                        history[referral[bettingDetails[requestId].player]].All.push(bettingDetails[requestId]);

                    } else {

                        payable(bettingDetails[requestId].player).transfer(bettingDetails[requestId].amount*2);

                    }
                }
              }


        history[bettingDetails[requestId].player].All.push(bettingDetails[requestId]);
        //push to referrer history
        history[referral[bettingDetails[requestId].player]].All.push(bettingDetails[requestId]);
        emit FlipCoinResult(requestId,bettingDetails[requestId].isWin);
       } else {
        //for raffle
        uint256 winningId = (randomwords[0] % tickets).mul(1);

        totalRaffels[trackWinner[winningId][msg.sender]].win == true;
        totalRaffels[trackWinner[winningId][msg.sender]].pot == winningPot;
        //winner = ticketOwners[randomNum];
        payable(totalRaffels[trackWinner[winningId][msg.sender]].player).transfer(winningPot);

        emit WinnerSelected(totalRaffels[trackWinner[winningId][msg.sender]].player);
       }

    }



    function getStatus(uint256 requestId) public view returns(flipCoinDetails memory)
    {
        return bettingDetails[requestId];
    }

     function withdrawAllBalance() public 
    {
        require(msg.sender == flipCoinOwner , "You are not the admin");
        payable(flipCoinOwner).transfer(address(this).balance);

    }

    //for raffel draw

    function openRaffle(uint256 _ticketPrice) public {
        require(msg.sender == flipCoinOwner , "You are not the admin");
        require(!isRaffleOpen, "Raffle is already open");
        uint256 twoDaysInSeconds = 2 * 24 * 60 * 60;
        isRaffleOpen = true;
        round ++;
        ticketPrice = _ticketPrice;
        winningPot = 0;
        tickets = 1;
        endDate = block.timestamp + twoDaysInSeconds;

        emit RaffleOpened(_ticketPrice);

    }



    function closeRaffle() public {
        require(isRaffleOpen, "Raffle is not open");
        //require(msg.sender == flipCoinOwner , "You are not the admin");
        requestRandomness(callBackGasLimit, requestConfirmations, numWords);
        isRaffleOpen = false;
        emit RaffleClosed();
    }


    function buyTickets(uint256 _numTickets) public payable {
        require(isRaffleOpen, "Raffle is not open");
        if(block.timestamp > endDate) {
            //selectWinner();
            closeRaffle();
            payable(msg.sender).transfer(msg.value);
        } else {
            uint256 loopval = _numTickets + tickets;

            // Update mapping and array with new tickets
            for (uint256 i = tickets; i < loopval; i++) {

                ticketsByOwner[msg.sender][round]++;

                trackWinner[i][msg.sender] = totalRaffels.length;

                Raffel memory userRaf = Raffel({
                player: msg.sender,
                round: round,
                pot: 0,
                price: ticketPrice,
                win: false,
                dateEnd: endDate
                });

                totalRaffels.push(userRaf);
                winningPot += ticketPrice;
                tickets++;
            }

            emit TicketPurchased(msg.sender);
        }

    }



   /*
    function selectWinner() public  {
        require(!isRaffleOpen, "Raffle is still open");

        // Generate random number using block hash and ticketOwners array length
        //uint256 randomNum = uint256(keccak256(abi.encodePacked(blockhash(block.number - 1), ticketOwners.length))) % ticketOwners.length;

        // Assign winner and transfer balance to winner
        //trackWinner[winningId][msg.sender] = totalRaffels;
        totalRaffels[trackWinner[winningId][msg.sender]].win == true;
        totalRaffels[trackWinner[winningId][msg.sender]].pot == winningPot;
        //winner = ticketOwners[randomNum];
        payable(totalRaffels[trackWinner[winningId][msg.sender]].player).transfer(winningPot);

        emit WinnerSelected(totalRaffels[trackWinner[winningId][msg.sender]].player);
    }
    */


    function onGoingRaffle() public view returns(bool _isRaffleOpen, uint256 _round, uint256 _ticketPrice, uint256 _winningPot, uint256 _endDate, uint256 _tickets ) {

        return(isRaffleOpen, round, ticketPrice, winningPot, endDate, tickets);
    }



    function raffleHistory() public view returns( Raffel[] memory ){
        return totalRaffels;
    }



    function self() public view returns( flipCoinDetails[] memory ){
        return history[msg.sender].All;
    }


    function leaders() public view returns( flipCoinDetails[] memory ){
