    {
        require(msg.sender == flipCoinOwner , "You are not the admin");
        uint256 bal = link.balanceOf(address(this));
        return bal;
    }


    function transaferOwnership(address _address) public {
        require(msg.sender == flipCoinOwner , "You are not the admin");
        require(_address != address(0), "Address is not correct");
        flipCoinOwner = _address;
    }
