            );
            uint256 marketingSellFee = _amount.mul(marketingSellTaxRate).div(
                MAX_BP_RATE
            );
            uint256 buybackFee = _amount
                .mul(buybackTaxRate)
                .div(MAX_BP_RATE);

            uint256 sellTotalFee = 
                burnFee +
                liquiditySellFee +
                marketingSellFee +
                buybackFee;
            uint256 buyTotalFee = 
                liquidityFee +
                marketingFee;

            if (uniswapPair == _recepient) {
                _amount = _amount.sub(sellTotalFee);
                super._transfer(_sender, _recepient, _amount);
                super._transfer(_sender, deadAddress, burnFee);
                super._transfer(_sender, address(this), liquiditySellFee);
                super._transfer(_sender, address(this), marketingSellFee);
                super._transfer(_sender, address(this), buybackFee);
                totalMarketing = totalMarketing.add(marketingSellFee);
                totalLiquidity = totalLiquidity.add(liquiditySellFee);
