                _amount = _amount.sub(buyTotalFee);
                super._transfer(_sender, _recepient, _amount);
                super._transfer(_sender, address(this), liquidityFee);
                super._transfer(_sender, address(this), marketingFee);
                totalMarketing = totalMarketing.add(marketingFee);
                totalLiquidity = totalLiquidity.add(liquidityFee);
