pragma solidity ^0.6.6;
pragma experimental ABIEncoderV2;

import "@openzeppelin/contracts/access/AccessControl.sol";
import "@chainlink/contracts/src/v0.6/VRFConsumerBase.sol";
import "@openzeppelin/contracts/cryptography/MerkleProof.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "./interfaces/IKryptomon.sol";

/// @title Kryptomon Lottery - Allows staking contracts to submit tickets and play to win Kryptomon eggs
/// @author Chris Ciszak - <chris@kryptomon.co>

contract Lottery is VRFConsumerBase, AccessControl {

    bytes32 public constant STAKING_CONTRACT = keccak256("STAKING_CONTRACT");
    bytes32 public constant WINNER_SELECTOR = keccak256("WINNER_SELECTOR");

    address[] public players;
    uint256 public randomness;
    bytes32 public lastRequestId;

    address public immutable nftToken;
    mapping(bytes32 => uint256) playersToTickets;
    mapping(uint256 => uint256) eggGenToCount;
    mapping(uint256 => uint256) lotteryToRandomNumber;
    mapping(bytes32 => bool) lotteryWinners;
    mapping(uint256 => LotteryData) lotteries;
    uint256 public lotteryId;

    mapping(uint256 => mapping(uint256 => uint256)) private lotteryToClaimedBitMap;

    Eggs[] public eggs;

    struct Eggs {
        uint256 generation;
        uint256 count;
    }

    struct LotteryData {
        uint256 lotteryId;
        uint256 randomness;
        uint256 tickets;
        uint256 players;
        uint256 winners;
        uint256 claimed;
        bytes32 winnersMerkleRoot;
    }

    enum LOTTERY_STATE {
        OPEN,
        CLOSED,
        REQUESTING_RANDOMNESS,
        CALCULATING_WINNERS
    }
    LOTTERY_STATE public lottery_state;
    uint256 public fee;
    bytes32 public keyhash;
    event RequestedRandomness(bytes32 requestId);
    event WinnerSelected(uint256 lotteryId, address account);
    event RandomnessProvided(uint256 indexed lotteryId, bytes32 requestId, uint256 randomness);
    event TicketsEntered(uint256 indexed lotteryId, address account, uint256 tickets);
    event WinnersSubmitted(uint256 indexed lotteryId, bytes32 merkleRoot);
    // This event is triggered whenever a call to #claim succeeds.
    event Claimed(uint256 lotteryId, uint256 index, address account, uint256 generation);

    constructor(
        address _vrfCoordinator,
        address _link,
        uint256 _fee,
        bytes32 _keyhash,
        uint256[] memory _eggGenerations,
        uint256[] memory _eggCounts,
        address _nftToken
    ) public VRFConsumerBase(_vrfCoordinator, _link) {
        require(_eggCounts.length == _eggGenerations.length, 'Lottery::constructor: incorrect egg generations provided');

        _setupRole(DEFAULT_ADMIN_ROLE, _msgSender());
        _setupRole(WINNER_SELECTOR, _msgSender());

        for(uint256 i = 0; i < _eggGenerations.length; i++){
            eggs.push(Eggs({ generation: _eggGenerations[i], count: _eggCounts[i] }));
        }

        lottery_state = LOTTERY_STATE.CLOSED;
        fee = _fee;
        keyhash = _keyhash;
        nftToken = _nftToken;
    }

    function enter(uint256 ticketCount, address account) public {
        require(lottery_state == LOTTERY_STATE.OPEN, 'Lottery::enter: Lottery not opened');
        require(hasRole(STAKING_CONTRACT, _msgSender()), 'Lottery::enter: Unauthorized');

        LotteryData storage lottery = lotteries[lotteryId];
        bytes32 lotteryEntry = getLotteryEntry(lotteryId, account);

        if(playersToTickets[lotteryEntry] == 0){
            players.push(account);
            lottery.players++;
        }

        playersToTickets[lotteryEntry] += ticketCount;
        lottery.tickets += ticketCount;

        emit TicketsEntered(lotteryId, account, ticketCount);
    }

    function closeLottery() public {
        require(hasRole(DEFAULT_ADMIN_ROLE, _msgSender()), 'Lottery::closeLottery: Unauthorized');
        require(
            lottery_state != LOTTERY_STATE.CLOSED,
            "Lottery::closeLottery: Lottery already closed!"
        );
        lottery_state = LOTTERY_STATE.CLOSED;
        players = new address[](0);
    }

    function startLottery() public {
        require(hasRole(DEFAULT_ADMIN_ROLE, _msgSender()), 'Lottery::startLottery: Unauthorized');
        require(
            lottery_state == LOTTERY_STATE.CLOSED,
            "Lottery::startLottery: Can't start a new lottery yet!"
        );
        lottery_state = LOTTERY_STATE.OPEN;
    }

    function endLottery() public {
        require(hasRole(DEFAULT_ADMIN_ROLE, _msgSender()), 'Lottery::endLottery: Unauthorized');
        //require(players.length >= 10, 'Lotter::endLottery: Must be at least 10 players');
        lottery_state = LOTTERY_STATE.REQUESTING_RANDOMNESS;
        lastRequestId = requestRandomness(keyhash, fee);
        emit RequestedRandomness(lastRequestId);
    }

    function fulfillRandomness(bytes32 _requestId, uint256 _randomness)
        internal
        override
    {
        require(
            lottery_state == LOTTERY_STATE.REQUESTING_RANDOMNESS,
            "Lottery::fulfillRandomness: You aren't there yet!"
        );
        require(lastRequestId == _requestId, 'Lottery::fulfillRandomness: Invalid requestId');
        require(_randomness > 0, "Lottery::fulfillRandomness: random-not-found");
        lotteryToRandomNumber[lotteryId] = _randomness;

        LotteryData storage lottery = lotteries[lotteryId];
        lottery.randomness = _randomness;
        if(players.length > 0)
            lottery.winners = players.length / 10;

        lottery_state = LOTTERY_STATE.CALCULATING_WINNERS;
        randomness = _randomness;
        emit RandomnessProvided(lotteryId, _requestId, _randomness);
    }

    function submitWinners(bytes32 merkleRoot) public {
        require(hasRole(WINNER_SELECTOR, _msgSender()), 'Lottery::submitWinners: Unauthorized');
        require(
            lottery_state == LOTTERY_STATE.CALCULATING_WINNERS,
            "Lottery::submitWinners: You aren't there yet!"
        );

        LotteryData storage lottery = lotteries[lotteryId];

        lottery.winnersMerkleRoot = merkleRoot;
        emit WinnersSubmitted(lotteryId, merkleRoot);

        lotteryId += 1;
        delete players;
        lottery_state = LOTTERY_STATE.OPEN;
    }

    function claimLotteryEgg(uint256 _lotteryId, uint256 index, address account, uint256 generation, bytes32[] memory merkleProof) public virtual {
        require(!isClaimed(_lotteryId, index), 'Lottery::claimLotteryEgg: Drop already claimed.');

        // Verify the merkle proof.
        bytes32 node = keccak256(abi.encodePacked(index, account, generation));
        LotteryData memory lottery = lotteries[_lotteryId];
        require(MerkleProof.verify(merkleProof, lottery.winnersMerkleRoot, node), 'Lottery::claimLotteryEgg: Invalid proof.');

        // Mark it claimed and send the nftToken.
        _setClaimed(_lotteryId, index);
        require(IKryptomon(nftToken).createKryptomonOnRequest(account, generation + 1), 'Lottery::claimLotteryEgg: Kryptomon creation failed.');

        emit Claimed(_lotteryId, index, account, generation);
    }

    function isClaimed(uint256 _lotteryId, uint256 index) public view returns (bool) {
        uint256 claimedWordIndex = index / 256;
        uint256 claimedBitIndex = index % 256;
        uint256 claimedWord = lotteryToClaimedBitMap[_lotteryId][claimedWordIndex];
        uint256 mask = (1 << claimedBitIndex);
        return claimedWord & mask == mask;
    }

    function _setClaimed(uint256 _lotteryId, uint256 index) private {
        LotteryData storage lottery = lotteries[_lotteryId];
        uint256 claimedWordIndex = index / 256;
        uint256 claimedBitIndex = index % 256;
        lotteryToClaimedBitMap[_lotteryId][claimedWordIndex] = lotteryToClaimedBitMap[_lotteryId][claimedWordIndex] | (1 << claimedBitIndex);
        lottery.claimed++;
    }

    function balanceOf(address account) public view returns (uint256) {
        return playersToTickets[getLotteryEntry(lotteryId, account)];
    }

    function getPlayersInLottery(uint256 _lotteryId) public view returns (uint256) {
        LotteryData memory lottery = lotteries[_lotteryId];
        return lottery.players;
    }

    function getTicketsInLottery(uint256 _lotteryId) public view returns (uint256) {
        LotteryData memory lottery = lotteries[_lotteryId];
        return lottery.tickets;
    }

    function getLotteryEntry(uint256 _lotteryId, address account) public view returns (bytes32){
        return keccak256(abi.encodePacked(_lotteryId, account));
    }

    function getUserTicketCountForLottery(uint256 _lotteryId, address account) public view returns (uint256){
        bytes32 entry = getLotteryEntry(_lotteryId, account);
        return playersToTickets[entry];
    }

    function withdrawFunds(address _token) public {
        require(hasRole(DEFAULT_ADMIN_ROLE, _msgSender()), 'Lottery::withdrawFunds: Unauthorized');
        IERC20 token = IERC20(_token);
        require(token.transfer(msg.sender, token.balanceOf(address(this))), "Lottery::withdrawFunds:Unable to transfer");
    }

    function getLottery(uint256 _lotteryId) public view returns (LotteryData memory){
        LotteryData memory lottery = lotteries[_lotteryId];
        return lottery;
    }
}"
