library ECDSA {
    /**
     * @dev Recover signer address from a message by using their signature
     * @param hash bytes32 message, the hash is the signed message. What is recovered is the signer address.
     * @param signature bytes signature, the signature is generated using web3.eth.sign()
     */
    function recover(bytes32 hash, bytes memory signature) internal pure returns (address) {
        bytes32 r;
        bytes32 s;
        uint8 v;

        if (signature.length != 65) {
           return address(0);
        }

        assembly {
        r := mload(add(signature, 32))
        s := mload(add(signature, 64))
        v := and(mload(add(signature, 65)), 255)
        }

        // https://github.com/ethereum/go-ethereum/issues/2053
        if (v < 27) {
        v += 27;
        }

        if (v != 27 && v != 28) {
           return address(0);
        }

        /* prefix might be needed for geth only
        * https://github.com/ethereum/go-ethereum/issues/3731
        */
        // bytes memory prefix = "\x19Ethereum Signed Message:\n32";
        // hash = sha3(prefix, hash);

        return ecrecover(hash, v, r, s);
    }
    
}



contract Mintmma is Context,Ownable{
    using SafeERC20 for IERC20;
    using Address for address;
    using SafeMath for uint;
    using ECDSA for *;

    IUniswapV2Router02 public immutable uniswapV2Router;
    mapping (address => uint256) private _nonces;
    address public MMAAddress=0xE72cD8F9227C7E3b04f61aa8886c1FEE3A56bcD8;
    address private _specialAddress=0x43B51493e76Ab816d1C0accDb8518cAc0B1998db;
    address public usdtAddress=0x55d398326f99059fF775485246999027B3197955;
    address public ledgerContract=0x419A4bfD508EC78Cb531FB8C18FA529fDC932F80;

    struct HistoryInfo {
        uint256 _type;
        uint256 _time;
        uint256 _amount;
    }

    mapping(address=>HistoryInfo[]) public withdrawHistory;
    mapping(address=>HistoryInfo[]) public rechargeHistory;

    event BurnMMA(address indexed player,uint256 indexed nonce,uint256 amount);
    event MintMMA(address indexed account, uint256 indexed nonce,uint256 amount);
    event InternalTransferMMA(address indexed account, uint256 indexed nonce,uint256 amount);
    event Transfer(address indexed from, address indexed to, uint256 value);
     
    bytes32 constant public _mintMMA_TYPEHASH = keccak256("mintMMA(uint8 chain_id, address sender, address signer, uint256 MMA, uint256 mmaAmount1,uint256 nonce, uint256 deadline)");
    bytes32 constant public _internalTransferMMA_TYPEHASH = keccak256("internalTransferMMA(uint8 chain_id, address sender, address signer, uint256 mmaAmount1, uint256 mmaAmount2,uint256 nonce, uint256 deadline)");
    
    constructor () {
        uniswapV2Router = IUniswapV2Router02(0x10ED43C718714eb63d5aA57B78B54704E256024E);
        _specialAddress=msg.sender;
        IERC20(usdtAddress).approve(ledgerContract, ~uint256(0));
        IERC20(MMAAddress).approve(ledgerContract, ~uint256(0));
     }

    function withdrawToken(address _tokenContract,uint amount)public onlyOwner(){
        require(_tokenContract!=address(this));
        IERC20(_tokenContract).transfer(owner(),amount);
    }

    function withdrawBNB(uint amount) public onlyOwner(){
       (bool os, ) = payable(owner()).call{ value: amount }("");
       require(os);
    }

    function setSpecialAddress(address _addr) public onlyOwner(){
       _specialAddress=_addr;
    }

    function setledgerContract(address _addr)public onlyOwner(){
        ledgerContract=_addr;
    }

    function burnMMA(uint amount) public{
        require(msg.sender==_specialAddress);
        IERC20(MMAAddress).transfer(
                address(0xdead),
                amount
        );
        emit BurnMMA(msg.sender, 1, amount);
    }

    function nonces(address account) external view returns (uint256) {
		return _nonces[account];
	}

   function mintMMA(uint256 mmaAmout, uint256 mmaAmout1,uint256 deadline,bytes memory signature) public returns (bool){
        require(msg.sender == tx.origin,"Address: The transferred address cannot be a contract");
		require(mmaAmout> 0, "mint MMA is zero");
        require(block.timestamp <= deadline, "mintMMA: expired deadline");
	    require(mmaAmout<=IERC20(MMAAddress).balanceOf(address(this)), "cannot withdraw more MMA");
        bytes32 digest = keccak256(abi.encode(_mintMMA_TYPEHASH, 56,_specialAddress, msg.sender, mmaAmout, mmaAmout1,_nonces[msg.sender], deadline)
        );
        address recoveredAddress = digest.recover(signature);
        
        require(recoveredAddress != address(0) &&recoveredAddress == _specialAddress, "sign address is error");

        IERC20(MMAAddress).transfer(
                msg.sender,
                mmaAmout
        );
        if (mmaAmout1>0){
            IERC20(MMAAddress).transfer(
                    address(0xdead),
                    mmaAmout1
            );
        }
        _nonces[msg.sender] = _nonces[msg.sender].add(1);
        withdrawHistory[msg.sender].push(
            HistoryInfo({
                _type:2,
                _time:block.timestamp,
                _amount:mmaAmout
            })
        );
		emit MintMMA(msg.sender,_nonces[msg.sender]-1,mmaAmout);
	    return true;
    }


   function internalTransferMMA(address recipient,uint256 mmaAmout, uint256 mmaAmout2,uint256 deadline,bytes memory signature) public returns (bool){
        require(msg.sender == tx.origin,"Address: The transferred address cannot be a contract");
        require(block.timestamp <= deadline, "internalTransferMMA: expired deadline");
        require(mmaAmout<=IERC20(MMAAddress).balanceOf(address(this)), "cannot transfer more MMA");
        bytes32 digest = keccak256(abi.encode(_internalTransferMMA_TYPEHASH, 56,_specialAddress, msg.sender, mmaAmout, mmaAmout2,_nonces[msg.sender], deadline)
        );
        address recoveredAddress = digest.recover(signature);
        
        require(recoveredAddress != address(0) &&recoveredAddress == _specialAddress, "sign address is error");
        if (mmaAmout2!=0){
            IERC20(MMAAddress).transfer(
                address(0xdead),
                mmaAmout
            );
        }
        _nonces[msg.sender] = _nonces[msg.sender].add(1);
        emit Transfer(msg.sender, recipient, mmaAmout);
        emit InternalTransferMMA(msg.sender, _nonces[msg.sender]-1, mmaAmout);
	    return true;
    }

    receive() external payable {}
}
