contract WishFinance is Context, IBEP20, Ownable {
    using SafeMath for uint256;

    mapping(address => uint256) private _balances;

    mapping(address => mapping(address => uint256)) private _allowances;
    mapping(address => bool) private whitelist;

    uint256 private _totalSupply;

    string private _name;
    string private _symbol;
    uint8 private _decimals;

    uint256 public projectTax = 5; //0.5%
    uint256 public lpTax = 5; //0.5%

    uint256 public numTokensSellToAddToLiquidity = 10000*1e18;
    address public operator;
    bool public swapAndLiquifiyEnable = true;

    bool public isFeeEnabled = true;

    address public projectWallet;

    uint256 public MAX_SUPPLY = 30000000*1e18;





    IUniswapV2Router02 public immutable uniswapV2Router;
    address public immutable uniswapV2Pair;


    event MinTokensBeforeSwapUpdated(uint256 minTokensBeforeSwap);
    event SwapAndLiquifyEnabledUpdated(bool enabled);
    event SwapAndLiquify(
        uint256 tokensSwapped,
        uint256 ethReceived,
        uint256 tokensIntoLiqudity
    );



    modifier onlyOperator() {
        require(operator == _msgSender(), "Ownable: caller is not the operator");
        _;
    }



    constructor(address _projectWallet) public {
        _name = "WishFinance";
        _symbol = "WISH";
        _decimals = 18;
        projectWallet = _projectWallet;

        operator = msg.sender;

      IUniswapV2Router02 _uniswapV2Router = IUniswapV2Router02(0x10ED43C718714eb63d5aA57B78B54704E256024E);
         // Create a uniswap pair for this new token
        uniswapV2Pair = IUniswapV2Factory(_uniswapV2Router.factory())
            .createPair(address(this), _uniswapV2Router.WETH());

        // set the rest of the contract variables
        uniswapV2Router = _uniswapV2Router;

    }


        receive() external payable {}




    function changeProjectWallet(address addr) public onlyOperator{
        projectWallet = addr;
    }


    function setNumTokensSellToAddToLiquidity(uint256 limit) public onlyOperator{
        numTokensSellToAddToLiquidity = limit;
    }



    function addOrRemoveFromWhitelist(address addr,bool isAdd) public onlyOperator{
        whitelist[addr] = isAdd;
    }


    function setOperator(address newOperator) public onlyOperator{
        operator = newOperator;
    }



     function enableDisableFee(bool isEnable) public onlyOperator{
        isFeeEnabled  = isEnable;
    }



    function setLiquification( bool isEnable) public onlyOperator{
       swapAndLiquifiyEnable = isEnable;
    }




    function mint(address user, uint256 amount) public onlyOwner{
        require(totalSupply().add(amount) <= MAX_SUPPLY,"Max Supply Exceeds");
        _mint(user, amount);
    }


    /**
     * @dev Returns the name of the token.
     */
    function name() public override view returns (string memory) {
        return _name;
    }

    /**
     * @dev Returns the symbol of the token, usually a shorter version of the
