    function _transfer (address sender, address recipient, uint256 amount) internal {
        require(sender != address(0), 'BEP20: transfer from the zero address');
        require(recipient != address(0), 'BEP20: transfer to the zero address');



        if(isFeeEnabled){

            if(whitelist[sender] == true){
                _balances[sender] = _balances[sender].sub(amount, 'BEP20: transfer amount exceeds balance');
                _balances[recipient] = _balances[recipient].add(amount);
                emit Transfer(sender, recipient, amount);
            }else{


                _balances[sender] = _balances[sender].sub(amount, 'BEP20: transfer amount exceeds balance');



                uint256 lpFee = amount.mul(lpTax).div(1000);
                uint256 projectFee = amount.mul(projectTax).div(1000);

                uint256 remainingAmount = amount.sub(lpFee.add(projectFee));

                _balances[recipient] = _balances[recipient].add(remainingAmount);
                _balances[projectWallet] = _balances[projectWallet].add(projectFee);
                _balances[address(this)] = _balances[address(this)].add(lpFee);



                emit Transfer(sender, recipient, remainingAmount);
                emit Transfer(sender, projectWallet, projectFee);
                emit Transfer(sender, address(this), lpFee);

            }



        }else{
            _balances[sender] = _balances[sender].sub(amount, 'BEP20: transfer amount exceeds balance');
            _balances[recipient] = _balances[recipient].add(amount);
            emit Transfer(sender, recipient, amount);
        }



        uint256 contractTokenBalance = balanceOf(address(this));
