      "content": "// SPDX-License-Identifier: UNLICENSED
pragma solidity ^0.8.13;

import "openzeppelin-contracts/token/ERC20/IERC20.sol";
import "openzeppelin-contracts/token/ERC20/utils/SafeERC20.sol";
// import "openzeppelin-contracts/access/Ownable.sol";
import "openzeppelin-contracts/security/ReentrancyGuard.sol";
import "openzeppelin-contracts/proxy/utils/Initializable.sol";

contract BTLMine is Initializable,ReentrancyGuard{
    using SafeERC20 for IERC20;
    address public owner;

    address public btl;
    address public usdt;
    address private cSigner;
    address public vault;

    uint256 public minStakeAmount;
    uint256 public duration;
    uint256 public minBuyAmount;

    mapping(address => uint256) public mineNonces;
    mapping(address => uint256) public shareNonces;

    struct StakeInfo{
        uint256 amount;
        uint256 stakeTime;
        uint256 duration;
        uint256 releaseTime;
    }
    mapping(address => mapping(uint256 => StakeInfo)) public stakes;
    mapping(address => uint32) public stakeSize;

    bytes32 public constant DOMAIN_TYPEHASH = keccak256("EIP712Domain(string name,string version,uint256 chainId,address verifyingContract)");
    bytes32 constant public HARVEST_TYPEHASH = keccak256("harvest(address miner,uint256 amount,uint256 nonce)");
    bytes32 constant public STAKE_TYPEHASH = keccak256("stake(address staker,uint256 minAmount,uint256 duration)");
    bytes32 constant public SHARE_TYPEHASH = keccak256("share(address staker,uint256 amount,uint256 nonce))");

    event HARVEST(address miner, uint256 amount, uint256 nonce, uint256 timestamp);
    event STAKE(address staker, uint256 amount, uint256 minAmount, uint256 duration, uint256 timestamp, uint256 nonce);
    event UNSTAKE(address staker, uint256 amount, uint256 stakeTime, uint256 duration, uint256 timestamp, uint256 nonce);
    event SHARE(address staker, uint256 amount, uint256 nonce, uint256 timestamp);
    event SETMINSTAKE(address owner, uint256 oldMinStakeAmount, uint256 newMinStakeAmount);
    event SETDURATION(address owner,uint256 oldDuration, uint256 newDuration);
    event SETVAULT(address oldVault, address newVault);
    event BuyPoints(address from,uint256 amount,uint256 timestamp);
    event SetMinBuyAmount(address owner, uint256 oldMinBuyAmount, uint256 newMinBuyAmount);

    event OwnershipTransferred(address indexed previousOwner, address indexed newOwner);

    modifier onlyOwner() {
        require(owner == msg.sender, "Ownable: caller is not the owner");
        _;
    }

    constructor(address _btl, address _usdt, address _signer, address _vault, address _owner){
    // function initialize(address _btl, address _usdt, address _signer, address _vault, address _owner) public initializer{
        btl = _btl;
        usdt = _usdt;
        cSigner = _signer;
        vault = _vault;
        owner = _owner;
        minStakeAmount = 100000e18;
        duration = 30 * 24 * 3600;
        minBuyAmount = 10e18;

        emit SETMINSTAKE(msg.sender,0,minStakeAmount);
        emit SETDURATION(msg.sender,0,duration);
        emit SetMinBuyAmount(msg.sender, 0, minBuyAmount);
    }

    function setSigner(address _signer) public onlyOwner{
        require(_signer!=address(0),"zero address");
        cSigner = _signer;
    }

    function setMinStake(uint256 _amount) public onlyOwner{
        uint256 oldMinStakeAmount = minStakeAmount;
        minStakeAmount = _amount;
        emit SETMINSTAKE(msg.sender,oldMinStakeAmount,minStakeAmount);
    }

    function setDuration(uint256 _duration) public onlyOwner{
        uint256 oldDuration = duration;
        duration = _duration;
        emit SETDURATION(msg.sender,oldDuration,_duration);
    }

    function setMinBuyAmount(uint256 _minBuyAmount) public onlyOwner{
        emit SetMinBuyAmount(msg.sender, minBuyAmount, _minBuyAmount);
        minBuyAmount = _minBuyAmount;
    }

    function setVault(address _vault) external{
        require(msg.sender==vault || msg.sender == owner, "only owner or vault");

        emit SETVAULT(vault, _vault);
        vault = _vault;
    }

    function buyPoints(uint256 amount,uint8 v, bytes32 r, bytes32 s) external nonReentrant{
        require(amount > 10e18, "amount >= 10");
        bytes32 domainSeparator = keccak256(abi.encode(DOMAIN_TYPEHASH, keccak256(bytes("BTLMine")), keccak256(bytes("1")),block.chainid, address(this)));
        bytes32 structHash = keccak256(abi.encode(HARVEST_TYPEHASH, msg.sender, 1, 1));
        bytes32 digest = keccak256(abi.encodePacked("\x19\x01", domainSeparator, structHash));

        require(ecrecover(digest, v, r, s) == cSigner, "Invalid merchant");

        IERC20(usdt).safeTransferFrom( msg.sender, vault, amount);
        emit BuyPoints(msg.sender, amount, block.timestamp);
    }

    function harvest(uint256 amount, uint8 v, bytes32 r, bytes32 s) external nonReentrant{
        bytes32 domainSeparator = keccak256(abi.encode(DOMAIN_TYPEHASH, keccak256(bytes("BTLMine")), keccak256(bytes("1")),block.chainid, address(this)));
        bytes32 structHash = keccak256(abi.encode(HARVEST_TYPEHASH, msg.sender, amount, 1+mineNonces[msg.sender]++));
        bytes32 digest = keccak256(abi.encodePacked("\x19\x01", domainSeparator, structHash));

        require(ecrecover(digest, v, r, s) == cSigner, "Harvest: Invalid signer");

        IERC20(btl).safeTransfer( msg.sender, amount);
        emit HARVEST(msg.sender, amount, mineNonces[msg.sender], block.timestamp);
    }

    function stake(uint256 amount) external {
        require(amount >= minStakeAmount,"stake amount < min stake amount");
        uint32 id = 1 + stakeSize[msg.sender]++;
        StakeInfo storage staker = stakes[msg.sender][id];        

        IERC20(btl).safeTransferFrom(msg.sender,address(this),amount);
        staker.amount = amount;
        staker.stakeTime = block.timestamp;
        staker.duration = duration;

        emit STAKE(msg.sender, amount, minStakeAmount, duration, block.timestamp,id);

    }

    function unstake(uint32[] memory ids) external nonReentrant{
        for(uint8 i=0;i<ids.length;i++){
            StakeInfo storage staker = stakes[msg.sender][ids[i]];
            require(staker.amount > 0,"no stake");//error id
            require(staker.releaseTime == 0,"already unstaked");//unstaked
            require(staker.stakeTime+staker.duration <= block.timestamp,"20012");//stake has not expired

            staker.releaseTime = block.timestamp;

            IERC20(btl).safeTransfer( msg.sender, staker.amount);
            emit UNSTAKE(msg.sender, staker.amount, staker.stakeTime, staker.duration, staker.releaseTime, ids[i]);
        }

    }

    function lens(address wallet) external view returns(uint256 minStake_,uint256 duration_,uint256 pending_, StakeInfo[] memory stakes_){
        minStake_ = minStakeAmount;
        duration_ = duration;
        stakes_ = new StakeInfo[](stakeSize[wallet]);

        for(uint32 i=stakeSize[wallet];i>0;i--){
            StakeInfo memory info = stakes[wallet][i];
            stakes_[stakeSize[wallet] - i] = info;
            if(info.stakeTime+info.duration <= block.timestamp && info.releaseTime==0)pending_ += info.amount;
        }
    }

    function share(uint256 amount, uint8 v, bytes32 r, bytes32 s) external nonReentrant{
        bytes32 domainSeparator = keccak256(abi.encode(DOMAIN_TYPEHASH, keccak256(bytes("BTLMine")), keccak256(bytes("1")),block.chainid, address(this)));
        bytes32 structHash = keccak256(abi.encode(SHARE_TYPEHASH, msg.sender, amount, 1+shareNonces[msg.sender]++));
        bytes32 digest = keccak256(abi.encodePacked("\x19\x01", domainSeparator, structHash));

        require(ecrecover(digest, v, r, s) == cSigner, "Share: Invalid signer");

        IERC20(usdt).safeTransfer(msg.sender, amount);
        emit SHARE(msg.sender, amount, shareNonces[msg.sender], block.timestamp);
    }

    /**
     * @dev Transfers ownership of the contract to a new account (`newOwner`).
     * Internal function without access restriction.
     */
    function _transferOwnership(address newOwner) internal virtual {
        address oldOwner = owner;
        owner = newOwner;
        emit OwnershipTransferred(oldOwner, newOwner);
    }

     /**
     * @dev Leaves the contract without owner. It will not be possible to call
     * `onlyOwner` functions anymore. Can only be called by the current owner.
     *
     * NOTE: Renouncing ownership will leave the contract without an owner,
     * thereby removing any functionality that is only available to the owner.
     */
    function renounceOwnership() public virtual onlyOwner {
        _transferOwnership(address(0));
    } 

    function transferOwnership(address newOwner) public virtual onlyOwner {
        require(newOwner != address(0), "Ownable: new owner is the zero address");
        _transferOwnership(newOwner);
    }
}"
