    "contracts/GrannyBronk.sol": {
      "content": "pragma solidity ^0.8.0;

// SPDX-License-Identifier: Unlicensed

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/utils/Context.sol";
import "@openzeppelin/contracts/utils/math/SafeMath.sol";
import "@openzeppelin/contracts/utils/Address.sol";

import "./interfaces/IUniswapV2Factory.sol";
import "./interfaces/UniSwapRouter.sol";
import "./interfaces/IUniswapV2Pair.sol";

// Contract implementation
contract GrannyBronk is Context, IERC20, Ownable {
    using SafeMath for uint256;
    using Address for address;

    event MinTokensBeforeSwapUpdated(uint256 minTokensBeforeSwap);
    event SwapEnabledUpdated(bool enabled);

    event ExchangeAdded(address account);
    event ExchangedRemoved(address account);

    struct TValues {
        uint256 tTransferAmount;
        uint256 tFee;
        uint256 tMarketing;
    }

    struct RValues {
        uint256 rAmount;
        uint256 rTransferAmount;
        uint256 rFee;
        uint256 rMarketing;
    }

    uint256 private constant MAX = ~uint256(0);
    address private constant DEAD_ADDRESS = 0x000000000000000000000000000000000000dEaD;
    uint256 private constant MARKETING_WALLET_PERCENATGE = 2;
    uint256 private constant BURN_PERCENATGE = 10;

    modifier lockTheSwap() {
        inSwap = true;
        _;
        inSwap = false;
    }

    modifier setCorrectFee(bool takeFee, bool takePenaltyFee) {
        if (!takeFee || takePenaltyFee) {
            uint256 prevMarketingFee = marketingFee;
            uint256 prevTaxFee = taxFee;

            if (!takeFee) {
                marketingFee = 0;
                taxFee = 0;
            } else if (takePenaltyFee) {
                marketingFee = penaltyMarketingFee;
                taxFee = penaltyTaxFee;
            }

            _;

            marketingFee = prevMarketingFee;
            taxFee = prevTaxFee;
        } else {
            _;
        }
    }

    /// @notice Accumulatively log tnx tokens
    struct TokenTnxLog {
        uint256 window;
        uint256 amount;
    }

    //stores blacklisted accounts
    mapping(address => bool) public _isBlacklisted;
    mapping(address => bool) public _isExcludedFromPenaltyFee;
    mapping(address => bool) public _isExcludedFromHoldingLimit;
    mapping(address => bool) public _isExchangeAddress;

    mapping(address => uint256) private _rOwned;
    mapping(address => uint256) private _tOwned;

    mapping(address => mapping(address => uint256)) private _allowances;
    mapping(address => bool) private _isExcludedFromFee;

    //accounts excluded from Tax
    mapping(address => bool) private _isExcluded;
    address[] private _excluded;

    /// @notice track received tokens over receivedTokenSellLimitDuration
    mapping(address => TokenTnxLog) private _tokenReceivedLog;

    uint256 private _tTotal = 10 * 1000 * 10**9 * 10**9;
    uint256 private _rTotal = (MAX - (MAX % _tTotal));
    uint256 private _tFeeTotal;

    string private _name = "GrannyBronk";
    string private _symbol = "GRAN";
    uint8 private _decimals = 9;

    // Marketing and fees will start at 0 so we don't have a big impact when deploying to Uniswap
    // Marketing wallet address is null but the method to set the address is exposed
    uint256 public taxFee = 1;
    uint256 public marketingFee = 1;

    uint256 private penaltyTaxFee = 25;
    uint256 private penaltyMarketingFee = 25;

    uint256 public receivedTokenSellLimitDuration = 1 hours;

    address payable public marketingWalletAddress;

    IUniswapV2Router02 public immutable uniswapV2Router;
    address public immutable uniswapV2Pair;

    bool private inSwap = false;
    bool public swapEnabled = true;

    /// @notice maximum transaction size
    uint256 public maxTxAmount = _tTotal / 100; // 1% of the total supply
    uint256 public maxHoldingLimit = (_tTotal / 100) * 2; // 2% of the total supply

    /// @notice  We will set a minimum amount of tokens to be swaped => 5M
    uint256 private _numOfTokensToExchangeForMarketing = 5 * 10**3 * 10**9;

    constructor(address payable _marketingWalletAddress, address uniswapV2RouterAddress) {
        marketingWalletAddress = _marketingWalletAddress;

        _rOwned[_msgSender()] = _rTotal - (_rTotal / 100) * (MARKETING_WALLET_PERCENATGE + BURN_PERCENATGE);
        _rOwned[DEAD_ADDRESS] = (_rTotal / 100) * BURN_PERCENATGE;
        _rOwned[marketingWalletAddress] = (_rTotal / 100) * MARKETING_WALLET_PERCENATGE;

        IUniswapV2Router02 _uniswapV2Router = IUniswapV2Router02(uniswapV2RouterAddress); // bsc pcs v2
        // Create a uniswap pair for this new token
        address _uniswapV2Pair = IUniswapV2Factory(_uniswapV2Router.factory()).createPair(
            address(this),
            _uniswapV2Router.WETH()
        );

        // Exclude owner and this contract from fee
        _isExcludedFromFee[owner()] = true;
        _isExcludedFromFee[address(this)] = true;
        _isExcludedFromFee[marketingWalletAddress] = true;

        _isExcludedFromPenaltyFee[owner()] = true;
        _isExcludedFromPenaltyFee[address(this)] = true;
        _isExcludedFromPenaltyFee[marketingWalletAddress] = true;

        _isExcludedFromHoldingLimit[owner()] = true;
        _isExcludedFromHoldingLimit[address(this)] = true;
        _isExcludedFromHoldingLimit[marketingWalletAddress] = true;

        addExchangeAddress(_uniswapV2Pair);

        // set the rest of the contract variables
        uniswapV2Router = _uniswapV2Router;
        uniswapV2Pair = _uniswapV2Pair;

        emit Transfer(
            address(0),
            _msgSender(),
            _tTotal - (_tTotal / 100) * (MARKETING_WALLET_PERCENATGE + BURN_PERCENATGE)
        );
        emit Transfer(address(0), marketingWalletAddress, (_tTotal / 100) * MARKETING_WALLET_PERCENATGE);
        emit Transfer(address(0), DEAD_ADDRESS, (_tTotal / 100) * BURN_PERCENATGE);
    }

    function name() public view returns (string memory) {
        return _name;
    }

    function symbol() public view returns (string memory) {
        return _symbol;
    }

    function decimals() public view returns (uint8) {
        return _decimals;
    }

    function totalSupply() public view override returns (uint256) {
        return _tTotal;
    }

    function balanceOf(address account) public view override returns (uint256) {
        if (_isExcluded[account]) return _tOwned[account];
        return tokenFromReflection(_rOwned[account]);
    }

    function transfer(address recipient, uint256 amount) public override returns (bool) {
        _transfer(_msgSender(), recipient, amount);
        return true;
    }

    function allowance(address owner, address spender) public view override returns (uint256) {
        return _allowances[owner][spender];
    }

    function approve(address spender, uint256 amount) public override returns (bool) {
        _approve(_msgSender(), spender, amount);
        return true;
    }

    function transferFrom(
        address sender,
        address recipient,
        uint256 amount
    ) public override returns (bool) {
        _transfer(sender, recipient, amount);
        _approve(
            sender,
            _msgSender(),
            _allowances[sender][_msgSender()].sub(amount, "ERC20: transfer amount exceeds allowance")
        );
        return true;
    }

    function increaseAllowance(address spender, uint256 addedValue) public virtual returns (bool) {
        _approve(_msgSender(), spender, _allowances[_msgSender()][spender].add(addedValue));
        return true;
    }

    function decreaseAllowance(address spender, uint256 subtractedValue) public virtual returns (bool) {
        _approve(
            _msgSender(),
            spender,
            _allowances[_msgSender()][spender].sub(subtractedValue, "ERC20: decreased allowance below zero")
        );
        return true;
    }

    //checks if address is excluded
    function isExcluded(address account) public view returns (bool) {
        return _isExcluded[account];
    }

    function setExcludeFromFee(address account, bool excluded) external onlyOwner {
        _isExcludedFromFee[account] = excluded;
    }

    function totalFees() public view returns (uint256) {
        return _tFeeTotal;
    }

    function logReceivedTokensForPenaltyFee(address receiver, uint256 amount) private {
        uint256 limitFrame = getReceivedTokenSellLimitWindow();
        TokenTnxLog storage log = _tokenReceivedLog[receiver];
        log.amount = log.window == limitFrame ? log.amount + amount : amount;
        log.window = limitFrame;
    }

    function getReceivedTokenSellLimitWindow() public view returns (uint256) {
        return block.timestamp / receivedTokenSellLimitDuration;
    }

    function tokenFromReflection(uint256 rAmount) public view returns (uint256) {
        require(rAmount <= _rTotal, "GRAN::tokenFromReflection: Amount must be less than total reflections");
        uint256 currentRate = _getRate();
        return rAmount.div(currentRate);
    }

    function isExchangeAddress(address account) public view returns (bool) {
        return _isExchangeAddress[account];
    }

    function addExchangeAddress(address account) public onlyOwner {
        require(!_isExchangeAddress[account], "GRAN::addExchangeAddress: already included");

        _isExchangeAddress[account] = true;
        _isExcludedFromHoldingLimit[account] = true;
        _isExcludedFromPenaltyFee[account] = true;

        emit ExchangeAdded(account);
    }

    function removeExchangeAddress(address account) public onlyOwner {
        require(_isExchangeAddress[account], "GRAN::removeExchangeAddress: not an exchange");

        _isExchangeAddress[account] = false;
        _isExcludedFromHoldingLimit[account] = true;
        _isExcludedFromPenaltyFee[account] = false;

        emit ExchangedRemoved(account);
    }

    function excludeFromHoldingLimit(address account) public onlyOwner {
        require(!_isExcludedFromHoldingLimit[account], "GRAN::excludeFromHoldingLimit: already included");

        _isExcludedFromHoldingLimit[account] = true;
    }

    function includeFromHoldingLimit(address account) public onlyOwner {
        require(_isExcludedFromHoldingLimit[account], "GRAN::includeFromHoldingLimit: already removed");

        _isExcludedFromHoldingLimit[account] = false;
    }

    function isExcludedFromHoldingLimit(address account) public view returns (bool) {
        return _isExcludedFromHoldingLimit[account];
    }

    function excludeAccount(address account) external onlyOwner {
        require(!_isExcluded[account], "GRAN::excludeAccount: Account is already excluded");
        if (_rOwned[account] > 0) {
            _tOwned[account] = tokenFromReflection(_rOwned[account]);
        }
        _isExcluded[account] = true;
        _excluded.push(account);
    }

    function includeAccount(address account) external onlyOwner {
        require(_isExcluded[account], "GRAN::includeAccount: Account is already excluded");
        for (uint256 i = 0; i < _excluded.length; i++) {
            if (_excluded[i] == account) {
                _excluded[i] = _excluded[_excluded.length - 1];
                _tOwned[account] = 0;
                _isExcluded[account] = false;
                _excluded.pop();
                break;
            }
        }
    }

    function isExcludedFromFee(address account) public view returns (bool) {
        return _isExcludedFromFee[account];
    }

    function _approve(
        address owner,
        address spender,
        uint256 amount
    ) private {
        require(owner != address(0), "ERC20: approve from the zero address");
        require(spender != address(0), "ERC20: approve to the zero address");

        _allowances[owner][spender] = amount;
        emit Approval(owner, spender, amount);
    }

    function _transfer(
        address sender,
        address recipient,
        uint256 amount
    ) private {
        //blacklisted addresses can not buy! If you have ever used a bot, or scammed anybody, then you're wallet address will probably be blacklisted.
        require(!_isBlacklisted[sender] && !_isBlacklisted[recipient], "This address is blacklisted");
        require(sender != address(0), "ERC20: transfer from the zero address");
        require(recipient != address(0), "ERC20: transfer to the zero address");
        require(amount > 0, "GRAN::_transfer: Transfer amount must be greater than zero");

        if (!_isExcludedFromHoldingLimit[recipient]) {
            enforceHoldingLimit(recipient, amount);
        }

        if (
            sender != owner() &&
            recipient != owner() &&
            sender != marketingWalletAddress &&
            recipient != marketingWalletAddress
        ) {
            require(amount <= maxTxAmount, "GRAN::_transfer: Transfer amount exceeds the maxTxAmount.");
        }

        // is the token balance of this contract address over the min number of
        // tokens that we need to initiate a swap?
        // also, don't swap if sender is uniswap pair.
        uint256 contractTokenBalance = balanceOf(address(this));

        if (contractTokenBalance >= maxTxAmount) {
            contractTokenBalance = maxTxAmount;
        }

        bool overMinTokenBalance = contractTokenBalance >= _numOfTokensToExchangeForMarketing;

        if (!inSwap && swapEnabled && overMinTokenBalance && sender != uniswapV2Pair) {
            // We need to swap the current tokens to ETH and send to the Marketing wallet
            swapTokensForEth(contractTokenBalance);

            uint256 contractETHBalance = address(this).balance;
            if (contractETHBalance > 0) {
                sendETHToMarketing(address(this).balance);
            }
        }

        //indicates if fee should be deducted from transfer
        bool takeFee = isSell(recipient);

        //if any account belongs to _isExcludedFromFee account then remove the fee
        if (_isExcludedFromFee[sender] || _isExcludedFromFee[recipient]) {
            takeFee = false;
        }

        bool takePenaltyFee = takeFee && !_isExcludedFromPenaltyFee[sender] && isPenaltyFeeApplicable(sender, amount);

        //transfer amount, it will take tax and Marketing fee
        _tokenTransfer(sender, recipient, amount, takeFee, takePenaltyFee);

        if (!_isExcludedFromPenaltyFee[recipient]) {
            logReceivedTokensForPenaltyFee(recipient, amount);
        }
    }

    function enforceHoldingLimit(address receiver, uint256 amount) private view {
        uint256 balance = balanceOf(receiver) + amount;
        require(balance <= maxHoldingLimit, "GRAN::enforceHoldingLimit: receiver holding limit exceeded");
    }

    function swapTokensForEth(uint256 tokenAmount) private lockTheSwap {
        // generate the uniswap pair path of token -> weth
        address[] memory path = new address[](2);
        path[0] = address(this);
        path[1] = uniswapV2Router.WETH();

        _approve(address(this), address(uniswapV2Router), tokenAmount);

        // make the swap
        uniswapV2Router.swapExactTokensForETHSupportingFeeOnTransferTokens(
            tokenAmount,
            0, // accept any amount of ETH
            path,
            address(this),
            block.timestamp
        );
    }

    function sendETHToMarketing(uint256 amount) private {
        marketingWalletAddress.transfer(amount);
    }

    // We are exposing these functions to be able to manual swap and send
    // in case the token is highly valued and 5M becomes too much
    function manualSwap() external onlyOwner {
        uint256 contractBalance = balanceOf(address(this));
        swapTokensForEth(contractBalance);
    }

    function manualSend() external onlyOwner {
        uint256 contractETHBalance = address(this).balance;
        sendETHToMarketing(contractETHBalance);
    }

    function setSwapEnabled(bool enabled) external onlyOwner {
        swapEnabled = enabled;
    }

    function isPenaltyFeeApplicable(address seller, uint256 amount) private view returns (bool) {
        uint256 limitFrameWindow = getReceivedTokenSellLimitWindow();
        TokenTnxLog memory log = _tokenReceivedLog[seller];
        if (log.window == limitFrameWindow) {
            uint256 balanceOfSeller = balanceOf(seller);

            // seller doesn't have tokens bought in
            // last ReceivedTokenSellLimitWindow left after this sell
            return balanceOfSeller - amount < log.amount;
        }
        return false;
    }

    function isSell(address recepient) private view returns (bool) {
        return (_isExchangeAddress[recepient]);
    }

    function _tokenTransfer(
        address sender,
        address recipient,
        uint256 amount,
        bool takeFee,
        bool takePenaltyFee
    ) private setCorrectFee(takeFee, takePenaltyFee) {
        if (_isExcluded[sender] && !_isExcluded[recipient]) {
            _transferFromExcluded(sender, recipient, amount);
        } else if (!_isExcluded[sender] && _isExcluded[recipient]) {
            _transferToExcluded(sender, recipient, amount);
        } else if (!_isExcluded[sender] && !_isExcluded[recipient]) {
            _transferStandard(sender, recipient, amount);
        } else if (_isExcluded[sender] && _isExcluded[recipient]) {
            _transferBothExcluded(sender, recipient, amount);
        } else {
            _transferStandard(sender, recipient, amount);
        }
    }

    function _transferStandard(
        address sender,
        address recipient,
        uint256 tAmount
    ) private {
        (TValues memory tValues, RValues memory rValues) = _getValues(tAmount);

        _rOwned[sender] = _rOwned[sender].sub(rValues.rAmount);
        _rOwned[recipient] = _rOwned[recipient].add(rValues.rTransferAmount);

        _takeMarketing(tValues.tMarketing, rValues.rMarketing);
        _reflectFee(rValues.rFee, tValues.tFee);

        emit Transfer(sender, recipient, tValues.tTransferAmount);
    }

    function _transferToExcluded(
        address sender,
        address recipient,
        uint256 tAmount
    ) private {
        (TValues memory tValues, RValues memory rValues) = _getValues(tAmount);

        _rOwned[sender] = _rOwned[sender].sub(rValues.rAmount);
        _tOwned[recipient] = _tOwned[recipient].add(tValues.tTransferAmount);
        _rOwned[recipient] = _rOwned[recipient].add(rValues.rTransferAmount);

        _takeMarketing(tValues.tMarketing, rValues.rMarketing);
        _reflectFee(rValues.rFee, tValues.tFee);

        emit Transfer(sender, recipient, tValues.tTransferAmount);
    }

    function _transferFromExcluded(
        address sender,
        address recipient,
        uint256 tAmount
    ) private {
        (TValues memory tValues, RValues memory rValues) = _getValues(tAmount);

        _tOwned[sender] = _tOwned[sender].sub(tAmount);
        _rOwned[sender] = _rOwned[sender].sub(rValues.rAmount);

        _rOwned[recipient] = _rOwned[recipient].add(rValues.rTransferAmount);
        _takeMarketing(tValues.tMarketing, rValues.rMarketing);
        _reflectFee(rValues.rFee, tValues.tFee);

        emit Transfer(sender, recipient, tValues.tTransferAmount);
    }

    function _transferBothExcluded(
        address sender,
        address recipient,
        uint256 tAmount
    ) private {
        (TValues memory tValues, RValues memory rValues) = _getValues(tAmount);

        _tOwned[sender] = _tOwned[sender].sub(tAmount);
        _rOwned[sender] = _rOwned[sender].sub(rValues.rAmount);
        _tOwned[recipient] = _tOwned[recipient].add(tValues.tTransferAmount);
        _rOwned[recipient] = _rOwned[recipient].add(rValues.rTransferAmount);

        _takeMarketing(tValues.tMarketing, rValues.rMarketing);
        _reflectFee(rValues.rFee, tValues.tFee);

        emit Transfer(sender, recipient, tValues.tTransferAmount);
    }

    function _takeMarketing(uint256 tMarketing, uint256 rMarketing) private {
        _rOwned[address(this)] = _rOwned[address(this)].add(rMarketing);
        if (_isExcluded[address(this)]) {
            _tOwned[address(this)] = _tOwned[address(this)].add(tMarketing);
        }
    }

    function _reflectFee(uint256 rFee, uint256 tFee) private {
        _rTotal = _rTotal.sub(rFee);
        _tFeeTotal = _tFeeTotal.add(tFee);
    }

    //to recieve ETH from uniswapV2Router when swaping
    receive() external payable {}

    function _getValues(uint256 tAmount) private view returns (TValues memory, RValues memory) {
        uint256 currentRate = _getRate();

        TValues memory tValues = _getTValues(tAmount);
        RValues memory rValues = _getRValues(
            tAmount,
            tValues.tTransferAmount,
            tValues.tFee,
            tValues.tMarketing,
            currentRate
        );

        return (tValues, rValues);
    }

    function _getTValues(uint256 tAmount) private view returns (TValues memory) {
        uint256 tFee = tAmount.mul(taxFee).div(100);
        uint256 tMarketing = tAmount.mul(marketingFee).div(100);
        uint256 tTransferAmount = tAmount.sub(tFee).sub(tMarketing);
        return TValues({ tFee: tFee, tMarketing: tMarketing, tTransferAmount: tTransferAmount });
    }

    function _getRValues(
        uint256 tAmount,
        uint256 tTransferAmount,
        uint256 tFee,
        uint256 tMarketing,
        uint256 currentRate
    ) private pure returns (RValues memory) {
        return
            RValues({
                rAmount: tAmount.mul(currentRate),
                rTransferAmount: tTransferAmount.mul(currentRate),
                rFee: tFee.mul(currentRate),
                rMarketing: tMarketing.mul(currentRate)
            });
    }

    function _getRate() private view returns (uint256) {
        (uint256 rSupply, uint256 tSupply) = _getCurrentSupply();
        return rSupply.div(tSupply);
    }

    function _getCurrentSupply() private view returns (uint256, uint256) {
        uint256 rSupply = _rTotal;
        uint256 tSupply = _tTotal;
        for (uint256 i = 0; i < _excluded.length; i++) {
            if (_rOwned[_excluded[i]] > rSupply || _tOwned[_excluded[i]] > tSupply) return (_rTotal, _tTotal);
            rSupply = rSupply.sub(_rOwned[_excluded[i]]);
            tSupply = tSupply.sub(_tOwned[_excluded[i]]);
        }
        if (rSupply < _rTotal.div(_tTotal)) return (_rTotal, _tTotal);
        return (rSupply, tSupply);
    }

    function _getETHBalance() public view returns (uint256 balance) {
        return address(this).balance;
    }

    function _setTaxFee(uint256 _taxFee) external onlyOwner {
        require(_taxFee >= 0 && _taxFee <= 99, "GRAN::_setTaxFee: taxFee should be in 0 - 99");
        taxFee = _taxFee;
    }

    function _setMarketingFee(uint256 MarketingFee) external onlyOwner {
        require(MarketingFee <= 50, "GRAN::_setMarketingFee should be in 1 - 50");
        marketingFee = MarketingFee;
    }

    function _setMarketingWallet(address payable _marketingWalletAddress) external onlyOwner {
        marketingWalletAddress = _marketingWalletAddress;
    }

    function _setMaxTxAmount(uint256 _maxTxAmount) external onlyOwner {
        maxTxAmount = _maxTxAmount;
    }

    function _setPenaltyFees(uint256 _penaltyTaxFee, uint256 _panaltyMarketingFee) external onlyOwner {
        penaltyTaxFee = _penaltyTaxFee;
        penaltyMarketingFee = _panaltyMarketingFee;
    }

    //removeFromBlackList
    function removeFromBlackList(address account) external onlyOwner {
        _isBlacklisted[account] = false;
    }

    //adding multiple address to the blacklist - used to manually block known bots and scammers
    function addToBlackList(address[] calldata addresses) external onlyOwner {
        for (uint256 i; i < addresses.length; ++i) {
            _isBlacklisted[addresses[i]] = true;
        }
    }
}
"
