pragma solidity ^0.8.23;








contract QuantumK9 is
    ERC721A,
    ERC721AQueryable,
    Ownable,
    ReentrancyGuard,
    Pausable
{
    using Address for address;

    uint8 public constant MAX_NFT_PER_WALLET = 10;
    uint16 public constant MAX_NFT_SUPPLY = 10000;
    uint256 public mintFee = 0.01 ether;
    uint256 public tokenFee = 1e6;

    address payable public constant FEE_ADDRESS = payable(0x2597482AA28C49c9A2D17B144853920EE1B0D7eD);
    address public constant TOKEN_ADDRESS = 0x8183575e170b2cB27Cb64163615047021B550147;
    
    string private baseUri = "ipfs://bafybeieqi2ddgmf64wisdm4tvb7pnkfhri5cjiwdw5tvbdbinu6gpbnjp4/";

    mapping(address => uint256) private _mintsPerWallet;
    mapping(address => bool) private _allowList;

    event NFTMinted(address indexed owner, uint256 indexed tokenId);
    event MintPriceUpdated(uint256 newMintPrice);
    event MintTokenPriceUpdated(uint256 newTokenPrice);

    error InsufficientBnb(uint256 bnbProvided, uint256 mintFee);
    error MaxMintsReached();
    error EmptyString();
    error TotalSupplyExceeded();
    error BnbLessThanMinimum();

    constructor() ERC721A("QuantumK9s", "QK9") Ownable(msg.sender) {}

    function mintNft() external payable whenNotPaused nonReentrant {
        bool takeFee = true;
        if (totalSupply() >= MAX_NFT_SUPPLY) revert TotalSupplyExceeded();

        if (_allowList[msg.sender]) {
            takeFee = false;
        }

        if (takeFee) {
            if (msg.value < mintFee) revert InsufficientBnb(msg.value, mintFee);
            if (_mintsPerWallet[msg.sender] >= MAX_NFT_PER_WALLET) revert MaxMintsReached();

            require(
                IERC20(TOKEN_ADDRESS).transferFrom(msg.sender, FEE_ADDRESS, tokenFee), 
                "Token transfer failed"
            );

            _mintsPerWallet[msg.sender]++;
            Address.sendValue(FEE_ADDRESS, msg.value);
        }

        uint256 tokenId = _nextTokenId();
        _mint(msg.sender, 1);

        emit NFTMinted(msg.sender, tokenId);
    }

    function changeBaseUri(string memory newBaseUri) external onlyOwner {
        if (bytes(newBaseUri).length == 0) revert EmptyString();
        baseUri = newBaseUri;
    }

    function addToAllowList(address user, bool state) external onlyOwner {
        _allowList[user] = state;
    }

    function changeMintFee(uint256 newFeeInWei) external onlyOwner {
        mintFee = newFeeInWei;
        emit MintPriceUpdated(newFeeInWei);
    }

    function changeTokenFee(uint256 newFeeInWei) external onlyOwner {
        tokenFee = newFeeInWei;
        emit MintTokenPriceUpdated(newFeeInWei);
    }

    function pause() external onlyOwner {
        _pause();
    }

    function unpause() external onlyOwner {
        _unpause();
    }

    function withdrawETH() external onlyOwner {
        Address.sendValue(payable(msg.sender), address(this).balance);
    }

    function withdrawERC20(address tokenAddress) external onlyOwner {
        uint256 amount = IERC20(tokenAddress).balanceOf(address(this));
        require(amount > 0, "no tokens on contract");
        IERC20(tokenAddress).transfer(_msgSender(), amount);
    }

    /**
     * @dev See {IERC721Metadata-tokenURI}.
     */
    function tokenURI(uint256 tokenId)
        public
        view
        override(ERC721A, IERC721A)
        returns (string memory)
    {
        if (!_exists(tokenId)) revert URIQueryForNonexistentToken();
        return
            bytes(baseUri).length != 0
                ? string(abi.encodePacked(baseUri, _toString(tokenId), ".json"))
                : "";
    }
}
