contract AmazyToken is
    ERC20,
    ERC20Burnable,
    ERC20Snapshot,
    AccessControl,
    Pausable,
    ERC20Permit,
    ERC20Votes,
    ERC20FlashMint
{
    bytes32 public constant SNAPSHOT_ROLE = keccak256('SNAPSHOT_ROLE');
    bytes32 public constant PAUSER_ROLE = keccak256('PAUSER_ROLE');

    IAntisnipe public antisnipe = IAntisnipe(address(0));
    bool public antisnipeDisable;

    constructor() ERC20('Amazy Token', 'AZY') ERC20Permit('Amazy Token') {
        _grantRole(DEFAULT_ADMIN_ROLE, msg.sender);
        _grantRole(SNAPSHOT_ROLE, msg.sender);
        _grantRole(PAUSER_ROLE, msg.sender);
        _mint(msg.sender, 1000000000 * 10**decimals());
    }

    function multiTransfer(address[] memory to, uint256[] memory values)
        external
        returns (uint256)
    {
        require(to.length == values.length, 'Array not equal.');
        require(to.length <= 100, 'Array must <= 100.');
        uint256 i = 0;
        for (i; i < to.length; i++) {
            _transfer(_msgSender(), to[i], values[i]);
        }
        return (to.length);
    }

    function snapshot() public onlyRole(SNAPSHOT_ROLE) {
        _snapshot();
    }

    function pause() public onlyRole(PAUSER_ROLE) {
        _pause();
    }

    function unpause() public onlyRole(PAUSER_ROLE) {
        _unpause();
    }

    function _beforeTokenTransfer(
        address from,
        address to,
        uint256 amount
    ) internal override(ERC20, ERC20Snapshot) whenNotPaused {
        super._beforeTokenTransfer(from, to, amount);
        if (from == address(0) || to == address(0)) return;
        if (!antisnipeDisable && address(antisnipe) != address(0))
            antisnipe.assureCanTransfer(msg.sender, from, to, amount);
    }

    // The following functions are overrides required by Solidity.

    function _afterTokenTransfer(
        address from,
        address to,
        uint256 amount
