    "contracts/Supernova.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity 0.8.17;

import "./libs/IDao.sol";
import "./libs/IM87.sol";
import "./libs/MTTToken.sol";
import "@openzeppelin/contracts-upgradeable/utils/CountersUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/access/OwnableUpgradeable.sol";
import "@openzeppelin/contracts-upgradeable/token/ERC20/ERC20Upgradeable.sol";
import "@openzeppelin/contracts-upgradeable/token/ERC20/utils/SafeERC20Upgradeable.sol";
import "./libs/IAccessControler.sol";

contract Supernova is OwnableUpgradeable {
    using CountersUpgradeable for *;

    using SafeERC20Upgradeable for ERC20Upgradeable;
    address private _MTT;

    address ORACLE;
    IM87 public m87Token;
    MTTToken public mttToken;
    IAccessControler IAC;
    uint _fee;
    uint16 USERS_ID;

    uint TreasuryStore;
    address[] TreasuryTokenStore;
    uint Reward_ETH_1;
    uint Reward_ETH_2;
    IDao _dao;

    address private M87;
    struct WithdrawRewards {
        uint amount;
        uint date;
        address token;
        uint currentProposal;
    }
    struct Rewards {
        uint amount;
        uint date;
        uint currentProposal;
    }
    struct Dark {
        uint amount;
        uint date;
        uint current_propsal;
        uint goal_propsal;
    }
    event ORACLECALLER(
        address oracle,
        address[] _addressP,
        address[] _addressH,
        bool status
    );
    event ADDTOTREASURY(uint amount, uint date);
    event ORACLEEVENT(address _m, address _v, string name);
    event REMOVETOTREASURY(uint amount, uint date);
    event NewEvent(string description, address[] _a);
    event ADDTOKENTOTREASURY(uint amount, uint date, address token);
    event REMOVETOKENTOTREASURY(uint amount, uint date, address token);
    event ChangeFee(uint fee);
    event dropReward(address oldAddress, address newAddress);
    address[] private Powehi;
    address[] private Halo;
    uint[] private Powehi_i;
    uint[] private Halo_i;
    string private _DIRCECT;
    mapping(address => bool) private powehi_list;
    //MTT POOL
    mapping(address => mapping(uint => Rewards)) _PoolRewards_1;
    mapping(address => uint[]) _IdRewards_1;
    //MOT POOL
    mapping(address => mapping(uint => Rewards)) _PoolRewards_2;
    mapping(address => uint[]) _IdRewards_2;
    //
    mapping(address => uint) _raisTokenKeeper;


    mapping(address => bool) _DarkList;
    mapping(address => Dark) _DarkListStore;

    modifier AccessControler() {
        require(IAC.getAccess());
        _;
    }

    modifier OnlyOracle() {
        require(msg.sender == ORACLE, "Not a ORACLE");
        _;
    }

    function geter() public view returns (address, address) {
        return (msg.sender, ORACLE);
    }

    function setup(
        address _m87,
        address _ORACLE,
        address _Dao,
        address _mtt,
        string memory _dir,
        address _AC
    ) public initializer {
        IAC = IAccessControler(_AC);
        _DIRCECT = _dir;
        m87Token = IM87(_m87);
        mttToken = MTTToken(_mtt);

        M87 = _m87;
        _fee = 5300000000000000;
        ORACLE = _ORACLE;
        _dao = IDao(_Dao);
        _MTT = _mtt;

        USERS_ID = 0;
        TreasuryStore = 0;
        Reward_ETH_1 = 0;
        Reward_ETH_2 = 0;

        __Ownable_init();
    }

    function changeOracle(address _address) public onlyOwner returns (bool) {
        if (_address == address(0)) {
            revert("_oracle is zero address");
        }
        ORACLE = _address;

        return true;
    }

    function InjectHalo(
        address[] memory _addresses
    ) public OnlyOracle returns (bool) {
        for (uint i = 0; i < _addresses.length; i++) {
            if (_addresses[i] == address(0)) {
                revert("address can not be zero address");
            }
        }
        Halo = _addresses;
        emit NewEvent("change Halo", _addresses);
        return true;
    }

    function ListHalo() public view returns (address[] memory) {
        return Halo;
    }

    function HaloCounter(uint256 index) public view returns (address) {
        return Halo[index];
    }

    function InjectPowehi(
        address[] memory _addresses
    ) public OnlyOracle returns (bool) {
        for (uint i = 0; i < _addresses.length; i++) {
            if (_addresses[i] == address(0)) {
                revert("address can not be zero address");
            }
        }
        Powehi = _addresses;
        emit NewEvent("change Powehi", _addresses);
        return true;
    }

    function ListPowehi() public view returns (address[] memory) {
        return Powehi;
    }

    function PowehiCounter(uint256 index) public view returns (address) {
        return Powehi[index];
    }

    function returnContractCurrentId() public view returns (uint) {
        return USERS_ID;
    }

    function setFee(uint _newFee) public OnlyOracle {
        _fee = _newFee;
        emit ChangeFee(_newFee);
    }

    function IdReward_1(address _ask) public view returns (uint[] memory) {
        return _IdRewards_1[_ask]; // user 1 1% //user 2  5% =>  x y z  8% 1
    }

    function Reward_1(
        address _ask,
        uint _id
    ) public view returns (Rewards memory) {
        return _PoolRewards_1[_ask][_id]; //4  22% 2
    }



    function PutoutReward_1(
        address _hsh,
        address _ask,
        uint _amount
    ) public AccessControler {
        uint[] storage _int = _IdRewards_1[_ask];
        _int.push(USERS_ID);
        (uint _c, uint _f) = _dao.getCurrentStatus();
        _PoolRewards_1[_ask][USERS_ID] = Rewards(_amount, block.timestamp, _c);
        USERS_ID += 1;
        emit ORACLECALLER(ORACLE, Powehi, Halo, true);
    }

    function PutInReward_2(
        address _hsh,
        address _ask,
        uint _amount
    ) public AccessControler {
        uint[] storage _int = _IdRewards_2[_ask];
        _int.push(USERS_ID);
        (uint _c, uint _f) = _dao.getCurrentStatus();
        _PoolRewards_2[_ask][USERS_ID] = Rewards(_amount, block.timestamp, _c);
        USERS_ID += 1;
        emit ORACLECALLER(ORACLE, Powehi, Halo, true);
    }
      function PutInReward_1(
        address _ask,
        uint _amount
    ) public AccessControler {
        uint[] storage _int = _IdRewards_1[_ask];
        _int.push(USERS_ID);
        (uint _c, uint _f) = _dao.getCurrentStatus();
        _PoolRewards_1[_ask][USERS_ID] = Rewards(_amount, block.timestamp, _c);
        USERS_ID += 1;
        emit ORACLECALLER(ORACLE, Powehi, Halo, true);
    }

    function PutAndDropReward_1(
        address _ask,
        address _new,
        uint _userIndex
    ) external AccessControler {
        uint[] storage _int = _IdRewards_1[_ask];

        uint[] storage _int_new = _IdRewards_1[_new];
        _int_new.push(_int[_userIndex]);
        (uint _c, uint _f) = _dao.getCurrentStatus();
        _PoolRewards_1[_new][_int[_userIndex]] = Rewards(
            _PoolRewards_1[_ask][_int[_userIndex]].amount,
            block.timestamp,
            _c
        );
        delete _PoolRewards_1[_ask][_int[_userIndex]];
        _int[_userIndex] = _int[_int.length - 1];

        _int.pop();

        emit ORACLECALLER(ORACLE, Powehi, Halo, true);
    }

    function DropReward_1(
        address _hsh,
        address _ask,
        address _new,
        uint _amount,
        uint _userIndex
    ) external AccessControler {
        uint[] storage _int = _IdRewards_1[_ask];

        delete _PoolRewards_1[_ask][_int[_userIndex]];
        uint[] storage _int_new = _IdRewards_1[_new];
        _int_new.push(_int[_userIndex]);
        (uint _c, uint _f) = _dao.getCurrentStatus();
        _PoolRewards_1[_new][_int[_userIndex]] = Rewards(
            _amount,
            block.timestamp,
            _c
        );

        _int[_userIndex] = _int[_int.length - 1]; //_int[_userIndex - 1];
        _int.pop();
        emit ORACLECALLER(ORACLE, Powehi, Halo, true);
    }

    function DropReward_2(
        address _hsh,
        address _ask,
        address _new,
        uint _amount,
        uint _userIndex
    ) external AccessControler {
        uint[] storage _int = _IdRewards_2[_ask];

        delete _PoolRewards_2[_ask][_int[_userIndex]];
        uint[] storage _int_new = _IdRewards_2[_new];
        _int_new.push(_int[_userIndex]);
        (uint _c, uint _f) = _dao.getCurrentStatus();
        _PoolRewards_2[_new][_int[_userIndex]] = Rewards(
            _amount,
            block.timestamp,
            _c
        );

        _int[_userIndex] = _int[_int.length - 1]; //_int[_userIndex - 1];
        _int.pop();
        emit ORACLECALLER(ORACLE, Powehi, Halo, true);
    }

    function PutAndDropReward_2(
        address _hsh,
        address _ask,
        address _new,
        uint _userIndex
    ) public AccessControler {
        uint[] storage _int = _IdRewards_2[_ask];

          _PoolRewards_2[_new][_int[_userIndex]] = Rewards(
            _PoolRewards_2[_ask][_int[_userIndex]].amount,
            block.timestamp,
            _PoolRewards_2[_ask][_int[_userIndex]].currentProposal
        );
         delete _PoolRewards_2[_ask][_int[_userIndex]];
        uint[] storage _int_new = _IdRewards_2[_new];
        _int_new.push(_int[_userIndex]);
        _int[_userIndex] = _int[_int.length - 1];
        _int.pop();
        emit dropReward(_ask, _new);
    }




    function ball() public view returns (uint) {
        return address(this).balance;
    }

    function PutOutTokenTreasuryB(
        uint _amount,
        address _token,
        uint8 _q,
        uint reward_1,
        uint reward_2
    ) external AccessControler {
        if (_q == 1) {
            _raisTokenKeeper[_token] -= _amount;
        } else if (_q == 2) {
            _raisTokenKeeper[_token] += _amount;
            Reward_ETH_1 += reward_1;
            Reward_ETH_2 += reward_2;
        } else if (_q == 4) {
            Reward_ETH_1 += reward_1;
            Reward_ETH_2 += reward_2;
        }
    }

    function IdReward_2(address _ask) public view returns (uint[] memory) {
        return _IdRewards_2[_ask];
    }

    function Reward_2(
        address _ask,
        uint _id
    ) public view returns (Rewards memory) {
        return _PoolRewards_2[_ask][_id];
    }

    function Powehi_finder(address _address) public view returns (bool) {
        for (uint i = 0; i < Powehi.length; i++) {
            if (Powehi[i] == _address) {
                return true;
            }
        }
        return false;
    }

    function Halo_finder(address _address) public view returns (bool) {
        for (uint i = 0; i < Halo.length; i++) {
            if (Halo[i] == _address) {
                return true;
            }
        }
        return false;
    }

    function StakM87(
        uint _amount,
        bytes memory _v,
        address _a,
        uint256 nounce
    ) public returns (bool) {
        // //MTT
        if (ERC20Upgradeable(M87).balanceOf(msg.sender) < _amount) {
            revert("Insufficient inventory");
        }
        if (!VirgoVerifier(_a, _v, nounce)) {
            revert("It is not valid rapid Cline! ");
        }
        ERC20Upgradeable(M87).safeTransferFrom(msg.sender, ORACLE, _amount);

        mttToken.transfer(msg.sender, _amount);
        //dao status
        PutInReward_1( msg.sender, _amount);


        return true;
    }

    function PutDarkList(address user) public OnlyOracle {
        if (IERC20Upgradeable(_MTT).balanceOf(user) == 0) {
            revert("Insufficient balance");
        }

        if (_DarkList[user]) {
            revert("Already added");
        }

        _DarkList[user] = true;

        (uint _c, uint _f) = _dao.getCurrentStatus();
        _DarkListStore[user] = Dark(0, block.timestamp, _c, _c + _f);
