    function VirgoEDS(address adr, uint256 _u) public view returns (bytes32) {
        return keccak256(abi.encodePacked(_DIRCECT, _u, adr));
    }

    function VirgoEDSHash(bytes32 _messageHash) public pure returns (bytes32) {
        return
            keccak256(
                abi.encodePacked(
                    "\x19Ethereum Signed Message:\n32",
                    _messageHash
                )
            );
    }

    function VirgoVerifier(
        address _address,
        bytes memory signature,
        uint256 nounce
    ) public view returns (bool) {
        bytes32 messageHash = VirgoEDS(_address, nounce);
        bytes32 ethSMHash = VirgoEDSHash(messageHash);

        return address(Signerr(ethSMHash, signature)) == msg.sender;
    }

    function Signerr(
        bytes32 _ethSMHash,
        bytes memory _signature
    ) public pure returns (address) {
        (bytes32 r, bytes32 s, uint8 v) = Signature(_signature);

        return ecrecover(_ethSMHash, v, r, s);
    }

    function Signature(
        bytes memory sig
    ) public pure returns (bytes32 r, bytes32 s, uint8 v) {
        require(sig.length == 65, "invalid length");

        assembly {
            r := mload(add(sig, 32))

            s := mload(add(sig, 64))

            v := byte(0, mload(add(sig, 96)))
        }
    }

