/**
 * @dev Contract module which provides a basic access control mechanism, where
 * there is an account (an owner) that can be granted exclusive access to
 * specific functions.
 *
 * By default, the owner account will be the one that deploys the contract. This
 * can later be changed with {transferOwnership}.
 *
 * This module is used through inheritance. It will make available the modifier
 * `onlyOwner`, which can be applied to your functions to restrict their use to
 * the owner.
 */
contract Ownable is Context {
  address private _owner;
  address private _previousOwner;
    
  event OwnershipTransferred(address indexed previousOwner, address indexed newOwner);

  constructor () {
    address msgSender = _msgSender();
    _owner = msgSender;
    emit OwnershipTransferred(address(0), msgSender);
  }

  function owner() public view returns (address) {
    return _owner;
  }

  modifier onlyOwner() {
    require(_owner == _msgSender(), "Ownable: caller is not the owner");
    _;
  }

  function renounceOwnership() public onlyOwner {
    emit OwnershipTransferred(_owner, address(0));
    _owner = address(0);
  }

  function transferOwnership(address newOwner) public onlyOwner {
    _transferOwnership(newOwner);
  }

  function _transferOwnership(address newOwner) internal {
    require(newOwner != address(0), "Ownable: new owner is the zero address");
    emit OwnershipTransferred(_owner, newOwner);
    _owner = newOwner;
  }
}

/**
* @dev PowaDAP platform token validation and registration contract interface.
*/
interface IPowaDAPValidationContract {
    function getTypeFeeService(uint8 _tokenType) external view returns(uint256);
    function getFeeToService() external view returns(address);
}

/*********************
 *
 * MAIN CONTRACT
 *
 ********************/

contract StandardTokenPlus is Context, IERC20, Ownable {
  using SafeMath for uint256;
  using Address for address;

    /**
     * @dev Constants of the token implementer.
     * 
     * To be able to register it on the PowaDAP platform from the validation contract, the token 
     * contract must have the same source code as the original implementation defined for the same
     * token type code.
     * 
     * Also, the only one who can carry out the registration and validation of the token on the 
     * platform is the owner of the contract.
     */
     
  address private PowaDAPValidationContract;
  uint256 private constant codeVersion = 1;
  uint8 private constant tokenTypeCode = 4;

  //Holders balances map
  mapping (address => uint256) private _balances;
  //Token handling permission map
  mapping (address => mapping (address => uint256)) private _allowances;
  //Total supply
  uint256 private _totalSupply;
  //Token decimals
  uint8 public _decimals;
  //Token symbol
  string public _symbol;
  //Token name
  string public _name;
  //list of restricted addresses (For frontrunning bots)
  mapping(address=>bool) private isRestricted;
  //All allowed to burn
  bool allAllowedToBurn = true;

  //Constructor
    constructor(
        string memory name_,
        string memory symbol_,
        uint8 decimals_,
        uint256 totalSupply_,
        address PowaDAPValidationContract_
    ) payable {
        _name = name_;
        _symbol = symbol_;
        _decimals = decimals_;
        _mint(owner(), totalSupply_);

        /**
        * @dev Token Deployment Service Tax
        */
        PowaDAPValidationContract = PowaDAPValidationContract_;
        uint256 _creationServiceFee = IPowaDAPValidationContract(PowaDAPValidationContract).getTypeFeeService(tokenTypeCode);
        address _creationServiceFeeTo = IPowaDAPValidationContract(PowaDAPValidationContract).getFeeToService();
        payable(_creationServiceFeeTo).transfer(_creationServiceFee);
  }

  //EXTERNAL FUNCTIONS

    /**
     * @dev Returns the address of validation contract.
     * 
     * For the registration process, it will be verified that the validation contract
     * defined in the token in its implementation corresponds to the current validation
     * contract of the platform.
     */
    function getPowaDAPValidationContract() external view returns(address){
        return PowaDAPValidationContract;
    }

    /**
     * @dev Returns the ID of the defined token type, which must correspond to the one  
     * indicated within the validation contract for this type of token.
     */
    function getTokenTypeCode() external pure returns(uint8){
        return tokenTypeCode;
    }

  /**
   * @dev Returns the bep token owner.
   */
  function getOwner() external view returns (address) {
    return owner();
  }

  /**
   * @dev Returns the token decimals.
   */
  function decimals() external view returns (uint8) {
    return _decimals;
  }

  /**
   * @dev Returns the token symbol.
   */
  function symbol() external view returns (string memory) {
    return _symbol;
  }

  /**
  * @dev Returns the token name.
  */
  function name() external view returns (string memory) {
    return _name;
  }

  /**
   * @dev See {ERC20-totalSupply}.
   */
  function totalSupply() external view returns (uint256) {
    return _totalSupply;
  }

  /**
   * @dev See {ERC20-balanceOf}.
   */
  function balanceOf(address account) external view returns (uint256) {
    return _balances[account];
  }

  /**
   * @dev See {ERC20-transfer}.
   *
   * Requirements:
   *
   * - `recipient` cannot be the zero address.
   * - the caller must have a balance of at least `amount`.
   */
  function transfer(address recipient, uint256 amount) external returns (bool) {
    _transfer(_msgSender(), recipient, amount);
    return true;
  }

  /**
   * @dev See {ERC20-allowance}.
   */
  function allowance(address owner, address spender) external view returns (uint256) {
    return _allowances[owner][spender];
  }

  /**
   * @dev See {ERC20-approve}.
   *
   * Requirements:
   *
   * - `spender` cannot be the zero address.
   */
  function approve(address spender, uint256 amount) external returns (bool) {
    _approve(_msgSender(), spender, amount);
    return true;
  }

  /**
   * @dev See {ERC20-transferFrom}.
   *
   * Emits an {Approval} event indicating the updated allowance. This is not
   * required by the EIP. See the note at the beginning of {ERC20};
   *
   * Requirements:
   * - `sender` and `recipient` cannot be the zero address.
   * - `sender` must have a balance of at least `amount`.
   * - the caller must have allowance for `sender`'s tokens of at least
   * `amount`.
   */
  function transferFrom(address sender, address recipient, uint256 amount) external returns (bool) {
    _transfer(sender, recipient, amount);
    _approve(sender, _msgSender(), _allowances[sender][_msgSender()].sub(amount, "transfer amount exceeds allowance"));
    return true;
  }

  /**
   * @dev Atomically increases the allowance granted to `spender` by the caller.
   *
   * This is an alternative to {approve} that can be used as a mitigation for
   * problems described in {ERC20-approve}.
   *
   * Emits an {Approval} event indicating the updated allowance.
   *
   * Requirements:
   *
   * - `spender` cannot be the zero address.
   */
  function increaseAllowance(address spender, uint256 addedValue) public returns (bool) {
    _approve(_msgSender(), spender, _allowances[_msgSender()][spender].add(addedValue));
    return true;
  }

  /**
   * @dev Atomically decreases the allowance granted to `spender` by the caller.
   *
   * This is an alternative to {approve} that can be used as a mitigation for
   * problems described in {ERC20-approve}.
   *
   * Emits an {Approval} event indicating the updated allowance.
   *
   * Requirements:
   *
   * - `spender` cannot be the zero address.
   * - `spender` must have allowance for the caller of at least
   * `subtractedValue`.
   */
  function decreaseAllowance(address spender, uint256 subtractedValue) public returns (bool) {
    _approve(_msgSender(), spender, _allowances[_msgSender()][spender].sub(subtractedValue, "decreased allowance below zero"));
    return true;
  }

  /**
   * @dev Creates `amount` tokens and assigns them to `msg.sender`, increasing
   * the total supply.
   *
   * Requirements
   *
   * - `msg.sender` must be the token owner
   */
  function mintSelf(uint256 amount) public onlyOwner returns (bool) {
    _mint(_msgSender(), amount);
    return true;
  }

  /**
   * @dev Creates `amount` tokens and assigns them to `receiver`, increasing
   * the total supply.
   *
   * Requirements
   *
   * - `msg.sender` must be the token owner
   */
  function mintFor(address receiver, uint256 amount) public onlyOwner returns (bool) {
    _mint(receiver, amount);
    return true;
  }

  /**
   * @dev Burn `amount` tokens and decreasing the total supply.
   */
  function burn(uint256 amount) public returns (bool) {
    if(!allAllowedToBurn){
      require(_msgSender() == owner(), "Only owner allowed for now");
    }
    _burn(_msgSender(), amount);
    return true;
  }

  /**
   * @dev Establishes a restriction state for transactions to a given address.
   * The use of this feature is intended to mitigate the malicious behavior of frontrunning bots.
   *
   * Requirements
   *
   * - `msg.sender` must be the token owner
   */
    function restrict(address _addr, bool _state) public onlyOwner {
        isRestricted[_addr] = _state;
    }

  /**
   * @dev Check the current restriction status of a given address.
   *
   * Requirements
   *
   * - `msg.sender` must be the token owner
   */
    function checkRestrictedStatus(address _addr) public onlyOwner view returns (bool) {
        return isRestricted[_addr];
    }

  /**
   * @dev Retrieve tokens hosted in the token contract (by mistake) to '_receiver'.
   *
   * Requirements
   *
   * - `msg.sender` must be the token owner
   */
    function transferHostedTokenFromContract(address _token, address _receiver) public onlyOwner returns(bool _sent){
        uint256 _contractBalance = IERC20(_token).balanceOf(address(this));
        _sent = IERC20(_token).transfer(_receiver, _contractBalance);
    }


  /**
   * @dev Set the state that will allow anyone or only the owner to burn tokens (reducing supply).
   *
   * Requirements
   *
   * - `msg.sender` must be the token owner
   */
    function setAllAllowedToBurn(bool _state) public onlyOwner {
        allAllowedToBurn = _state;
    }


  //INTERNAL FUNCTIONS

  /**
   * @dev Moves tokens `amount` from `sender` to `recipient`.
   *
   * This is internal function is equivalent to {transfer}, and can be used to
   * e.g. implement automatic token fees, slashing mechanisms, etc.
   *
   * Emits a {Transfer} event.
   *
   * Requirements:
   *
   * - `sender` cannot be the zero address.
   * - `recipient` cannot be the zero address.
   * - `sender` must have a balance of at least `amount`.
   */
  function _transfer(address sender, address recipient, uint256 amount) internal {
    require(sender != address(0), "transfer from the zero address");
    require(recipient != address(0), "transfer to the zero address");
    require(!isRestricted[recipient], "Recipient is restricted");
    require(!isRestricted[sender], "Sender is restricted");

    _balances[sender] = _balances[sender].sub(amount, "transfer amount exceeds balance");
    _balances[recipient] = _balances[recipient].add(amount);
    emit Transfer(sender, recipient, amount);
  }

  /** @dev Creates `amount` tokens and assigns them to `account`, increasing
   * the total supply.
   *
   * Emits a {Transfer} event with `from` set to the zero address.
   *
   * Requirements
   *
   * - `to` cannot be the zero address.
   */
  function _mint(address account, uint256 amount) internal {
    require(account != address(0), "mint to the zero address");

    _totalSupply = _totalSupply.add(amount);
    _balances[account] = _balances[account].add(amount);
    emit Transfer(address(0), account, amount);
  }

  /**
   * @dev Destroys `amount` tokens from `account`, reducing the
   * total supply.
   *
   * Emits a {Transfer} event with `to` set to the zero address.
   *
   * Requirements
   *
   * - `account` cannot be the zero address.
   * - `account` must have at least `amount` tokens.
   */
  function _burn(address account, uint256 amount) internal {
    require(account != address(0), "burn from the zero address");

    _balances[account] = _balances[account].sub(amount, "burn amount exceeds balance");
    _totalSupply = _totalSupply.sub(amount);
    emit Transfer(account, address(0), amount);
  }

  /**
   * @dev Sets `amount` as the allowance of `spender` over the `owner`s tokens.
   *
   * This is internal function is equivalent to `approve`, and can be used to
   * e.g. set automatic allowances for certain subsystems, etc.
   *
   * Emits an {Approval} event.
   *
   * Requirements:
   *
   * - `owner` cannot be the zero address.
   * - `spender` cannot be the zero address.
   */
  function _approve(address owner, address spender, uint256 amount) internal {
    require(owner != address(0), "approve from the zero address");
    require(spender != address(0), "approve to the zero address");

    _allowances[owner][spender] = amount;
    emit Approval(owner, spender, amount);
  }

  /**
   * @dev Destroys `amount` tokens from `account`.`amount` is then deducted
   * from the caller's allowance.
   *
   * See {_burn} and {_approve}.
   */
  function _burnFrom(address account, uint256 amount) internal {
    _burn(account, amount);
    _approve(account, _msgSender(), _allowances[account][_msgSender()].sub(amount, "burn amount exceeds allowance"));
  }
}
