// SPDX-License-Identifier: MIT
pragma solidity ^0.7.0;

contract BulkBRC20Mint {
    function claim(uint count, uint id) public {
        require(id >= 1 && id <= 999, "Invalid ID"); // 自定义ID范围为800到999之间
        for (uint i = 0; i < count; i++) {
            (bool sent, ) = msg.sender.call{value:0}(abi.encodeWithSignature("data:,{'p':'bsc-20','op':'mint','tick':'bscs','id':uint256,'amt':'10000'}", id));
            require(sent, "Failed to send");
        }
    }
}
