// File: base/TimeLockable.sol



pragma solidity 0.8.15;



contract TimeLockable is Ownable {

    uint private cooldownDuration = 30 days;
    uint private unlockDuration =  14 days;
    mapping(address=>uint) private _cooldownMap;

    event Cooldown(address user);
    
    modifier canUnlock {
        ( , bool inUnlock, ) = getCooldown(msg.sender);
        require(inUnlock, "Not ready to unlock");
        _;
    }

    function cooldown() external {
        // If user has already cooldown and not yet completed the unlock window, there's no need to cooldown again
        (bool inCooldown, bool inUnlock, ) = getCooldown(msg.sender);
        if (!inCooldown && !inUnlock) {
            _cooldownMap[msg.sender] = block.timestamp;
            emit Cooldown(msg.sender);
        }
    }

    function modifyCooldown(uint duration, uint window) external onlyOwner {
        require(duration > 0 && window > 0, "Invalid params");
        cooldownDuration = duration;
        unlockDuration = window;
    }

    function getCooldownInfo() external view returns (uint duration, uint window) {
        duration = cooldownDuration;
        window = unlockDuration;
    }

    function getCooldown(address user) public view returns  (bool inCooldown, bool inUnlock, uint startTime) {
        startTime = _cooldownMap[user];
        if (startTime == 0) {
            return (false, false, 0);
        }
        uint mid = startTime + cooldownDuration;
        uint end = mid + unlockDuration;
        uint time = block.timestamp;
        inCooldown = time >= startTime && time < mid;
        inUnlock = time >= mid && time < end;
    }

    function _extendCooldown(uint extra, uint base) internal {
        
        (bool inCooldown, bool inUnlock, uint start) = getCooldown(msg.sender);
        uint total = base + extra;

        if (inCooldown) {
            uint elapsed = block.timestamp - start;
            uint newElapsed = (base * elapsed) / total;
            _cooldownMap[msg.sender] = start + elapsed - newElapsed;
        } else if (inUnlock) {
            uint extraTime = (extra * cooldownDuration) / total;
            _cooldownMap[msg.sender] = block.timestamp - cooldownDuration + extraTime;
        }
    }
}



