// File: base/TokenHelper.sol



pragma solidity 0.8.15;






contract TokenHelper {

    using SafeERC20 for IERC20;

    function _transferTokenIn(address token, uint amount) internal {
        IERC20(token).safeTransferFrom(msg.sender, address(this), amount); 
    }

    function _transferTokenOut(address token, uint amount, address to) internal  {
        IERC20(token).safeTransfer(to, amount); 
    }

    function _transferTokenFrom(address token, uint amount, address from, address to) internal {
        IERC20(token).safeTransferFrom(from, to, amount); 
    }

    function _burnTokenFrom(address token, address from, uint amount) internal {
        if (amount > 0) {
            ERC20Burnable(token).burnFrom(from, amount);
        }
    }

    function _burnToken(address token, uint amount) internal {
        if (amount > 0) {
            ERC20Burnable(token).burn(amount);
        }
    }

    function _getDpValue(address token) internal view returns (uint) {
        uint dp = IERC20Metadata(token).decimals();
        return 10 ** dp;
    }
}


// File: base/Stakable.sol



pragma solidity 0.8.15;




contract Stakable is TokenHelper, Ownable {

    struct Data {
        address stakingToken;
        address rewardToken;
        address rewardVault;
        uint rewardPerSec;
        uint startTime;
        uint endTime;
        uint lastRewardTime;    // Last timestamp for reward distribution.
        uint accRewardPerToken; // Accumulated Reward per token, times 1e12. See below.
        uint totalStaked;
    }

    struct StakeInfo {
        uint amount;            // Staked amount
        uint accruedReward;     // Whenever a user stake or unstake, the pending reward is accrued.
        uint rewardDebt;        // Whenever a user stake or unstake, the updatePool is called and the rewardDebt is set to that
                                // specific point after the stake amount is added or reduced.
    }

    Data public data;
    mapping(address => StakeInfo) public stakerMap;

    event SetupReward(uint startTime, uint endTime, uint rewardPerSec);
    event ModifyReward(uint endTime, uint rewardPerSec);
    event Stake(address indexed user, uint amount);
    event Unstake(address indexed user, uint amount);
    event ClaimReward(address indexed user, uint amount);
    event AdminTokenRecovery(address token, uint amount);
    event AdminRewardTokenWithdraw(uint amount);

    constructor (address stakingToken, address rewardToken, address rewardVault) {
        require(stakingToken != Constant.ZERO_ADDRESS && 
                rewardToken != Constant.ZERO_ADDRESS &&
                rewardVault != Constant.ZERO_ADDRESS, "Invalid Address");
        data.stakingToken = stakingToken;
        data.rewardToken = rewardToken;
        data.rewardVault = rewardVault;
    }

    function setupReward(uint startTime, uint endTime, uint rewardPerSec) external onlyOwner {
        require(startTime > block.timestamp && endTime > startTime && rewardPerSec > 0, "Invalid param");
        data.startTime = startTime;
        data.endTime = endTime;
        data.rewardPerSec = rewardPerSec;
        data.lastRewardTime = startTime;
        emit SetupReward(startTime, endTime, rewardPerSec);
    }

    // Use this to change the endTime or change rewardPerSec (0 to stop emission).
    function modifyReward(uint endTime, uint rewardPerSec) external onlyOwner {
        require(block.timestamp < endTime, "Too late");
        updatePool();
        data.endTime = endTime;
        data.rewardPerSec = rewardPerSec;
        emit ModifyReward(endTime, rewardPerSec);
    }

    function stake(uint amount) public virtual {
        require(amount > 0, "Invalid amount");
        updatePool();
        _transferTokenIn(data.stakingToken, amount);
        _updateStake(msg.sender, amount, true);
    }

    function unStake(uint amount) public virtual {
        require(amount > 0, "Invalid amount");
        updatePool();
        _transferTokenOut(data.stakingToken, amount, msg.sender);
        _updateStake(msg.sender, amount, false);
    }

    function claimReward() public virtual {
        updatePool();
        _updateStake(msg.sender, 0, true); // AccruedReward is updated.

        StakeInfo storage staker = stakerMap[msg.sender];
        uint reward = staker.accruedReward;
        if (reward > 0) {
            _transferTokenFrom(data.rewardToken, reward, data.rewardVault, msg.sender);
            staker.accruedReward = 0;
            emit ClaimReward(msg.sender, reward);
        }
    }

    function getRewardAmount(address user) public view returns (uint) {
        StakeInfo storage staker = stakerMap[user];
        uint accRewardPerToken = data.accRewardPerToken;
        uint supply = data.totalStaked;

        if (block.timestamp > data.lastRewardTime && supply != 0) {
            uint num = _getElapsedSinceUpdate();
            uint tokenReward = num * data.rewardPerSec;
            accRewardPerToken += ((tokenReward * Constant.E12)/supply);
        }
        return staker.accruedReward + ((staker.amount * accRewardPerToken) / Constant.E12) - staker.rewardDebt;
    }

    function getTotalStaked() external view returns (uint) {
        return data.totalStaked;
    }

    function updatePool() public {
        if (block.timestamp <= data.lastRewardTime) {
            return;
        }
        uint supply = data.totalStaked;
        if (supply == 0) {
            data.lastRewardTime = block.timestamp;
            return;
        }
        uint num = _getElapsedSinceUpdate();
        uint tokenReward = num * data.rewardPerSec;
        data.accRewardPerToken += ((tokenReward * Constant.E12) / supply);
        data.lastRewardTime = block.timestamp;
    }

    function emergencyRewardWithdraw(uint amount) external onlyOwner {
        _transferTokenOut(data.rewardToken, amount, owner());
        emit AdminRewardTokenWithdraw(amount);
    }

    function recoverWrongTokens(address token, uint amount) external onlyOwner {
        require(token != data.stakingToken, "Cannot be staked token");
        require(token != data.rewardToken, "Cannot be reward token");
        _transferTokenOut(token, amount, owner());
        emit AdminTokenRecovery(token, amount);
    }

    // PRIVATE 
    function _updateStake(address user, uint amount, bool add) private {
        StakeInfo storage staker = stakerMap[user];
        if (staker.amount > 0) {
            uint pending = ((staker.amount * data.accRewardPerToken) / Constant.E12)  - staker.rewardDebt;
            if (pending > 0) {
                staker.accruedReward += pending;
            }
        }
        if (add) {
            staker.amount += amount;
            data.totalStaked += amount;
            emit Stake(user, amount);
        } else {
            require(staker.amount >= amount, "Withdraw amount exceeded");
            staker.amount -= amount;
            data.totalStaked -= amount;
            emit Unstake(user, amount);
        }
        staker.rewardDebt = (staker.amount * data.accRewardPerToken) / Constant.E12; // Call AFTER the staker.amount is updated
    }

    function _getElapsedSinceUpdate() private view returns (uint) {
        
        if (data.lastRewardTime == 0) {
            return 0; // Not setup yet
        }
        uint from = data.lastRewardTime;
        uint to = block.timestamp;

        if (to <= data.endTime) {
            return to - from;
        } else if (from >= data.endTime) {
            return 0;
        } else {
            return data.endTime - from;
        }
    }
}
