// File: chain-native/token/stkYAY.sol



pragma solidity 0.8.15;




contract stkYAY is ERC20Snapshot, Stakable, TimeLockable {
    
    constructor(address stakingToken, address rewardToken, address rewardVault) 
        ERC20("Staked YAY", "stkYAY") 
        Stakable(stakingToken, rewardToken, rewardVault) {
    }

    function stake(uint amount) public override {
        super.stake(amount);

        // To prevent users from having multiple addresses, pre-cooldown and stake/unstake different addresses to register,
        // every stake will extend the unlock time proportionally base on pre and post stake total amount. So after adding
        // additional stake, a user won't be able to unstake immediately without having some cooldown.
        _extendCooldown(amount, balanceOf(msg.sender));
        _mint(msg.sender, amount);
    }

    function unStake(uint amount) public override canUnlock {
        super.unStake(amount);
        _burn(msg.sender, amount);
    }

    function snapshot() public onlyOwner {
        _snapshot();
    }

    function _beforeTokenTransfer(address from, address to, uint amount) internal override(ERC20Snapshot) {
        // Disallow transfering between valid users. Mint and Burn is ok. 
        require(from == address(0) || to == address(0), "User transfer not allowed");
        super._beforeTokenTransfer(from, to, amount);
    }   
}
