pragma solidity ^0.4.26; // solhint-disable-line



contract PoslantsuRastvorimVogneZemliRewardsPool{
    uint256 public EWWS_TO_HATCH_1MINERS=864000;
    uint256 PSN=10000;
    uint256 PSNH=5000;
    bool public initialized=false;
    address public ceoAddress;
    mapping (address => uint256) public htchrMiners;
    mapping (address => uint256) public claimedEwws;
    mapping (address => uint256) public lastHatch;
    mapping (address => address) public referrals;
    uint256 public marketEwws;

    address public umbervamberAddress;
    address public ngenpinAddress;
    address public sectioaureaAddress;
    address public marambaAddress;
    address public marianaAddress;
    address public tantanAddress;
    address public opussummumAddress;
    address public orestisAddress;
    address public ereborAddress;

    constructor() public{
        ceoAddress=msg.sender;
        umbervamberAddress = 0x2B374f15D25779fD6d3A84C78F6ab5b3a8522e7b; 
        ngenpinAddress = 0x9b0bE3Dd2897936298C2d8465299e63eBdBDdFb7;
        sectioaureaAddress = 0x524685E2D5bacA59c6E771f7c3D2E1333506E13a;
        marambaAddress = 0x1FD5831F6B5212677aad4E8cA560F5d246382b49;
        marianaAddress = 0x40DA5eB917D40C89737005c4C9A2B5f39025a63a;
        tantanAddress = 0x2d3Fb8879c0c48E8Fa977FA1f88584583d94668F;
        opussummumAddress = 0xfa268A2D3c1932cd2D48E02ef8166c073CEBcbf7;
        orestisAddress = 0x705854de3844Da8116352B9d0b88384C8d3c0190;
        ereborAddress = 0x336292bC4c652ebf222C7434B6572680fd348FB9;
    }

    function setUmbervamberAddress(address _umbervamberAddress) public {
        require(msg.sender == ceoAddress, 'invalid call');
        umbervamberAddress = _umbervamberAddress;  
        }

    function setNgenpinAddress(address _ngenpinAddress) public {
        require(msg.sender == ceoAddress, 'invalid call');
        ngenpinAddress = _ngenpinAddress;
        }

    function setSectioaureaAddress(address _sectioaureaAddress) public {
        require(msg.sender == ceoAddress, 'invalid call');
        sectioaureaAddress = _sectioaureaAddress;
        }

    function setMarambaAddress(address _marambaAddress) public {
        require(msg.sender == ceoAddress, 'invalid call');
        marambaAddress = _marambaAddress;
        }
    
    function setMarianaAddress(address _marianaAddress) public {
        require(msg.sender == ceoAddress, 'invalid call');
        marianaAddress = _marianaAddress;
        }

    function setTantanAddress(address _tantanAddress) public {
        require(msg.sender == ceoAddress, 'invalid call');
        tantanAddress = _tantanAddress;
        }

    function setOpussummumAddress(address _opussummumAddress) public {
        require(msg.sender == ceoAddress, 'invalid call');
        opussummumAddress = _opussummumAddress;
        }

    function setOrestisAddress(address _orestisAddress) public {
        require(msg.sender == ceoAddress, 'invalid call');
        orestisAddress = _orestisAddress;
        }

    function setEreborAddress(address _ereborAddress) public {
        require(msg.sender == ceoAddress, 'invalid call');
        ereborAddress = _ereborAddress;
        }

    function reinviteFriends(address ref) public{
        require(initialized);
        if(ref == msg.sender || ref == address(0) || htchrMiners[ref] == 0) {
            ref = ceoAddress;
        }
        if(referrals[msg.sender] == address(0)){
            referrals[msg.sender] = ref;
        }
        uint256 EwwsUsed=getMyEwws();
        uint256 newMiners=SafeMath.div(EwwsUsed,EWWS_TO_HATCH_1MINERS);
        htchrMiners[msg.sender]=SafeMath.add(htchrMiners[msg.sender],newMiners);
        claimedEwws[msg.sender]=0;
        lastHatch[msg.sender]=now;

        //send referral Ewws
        claimedEwws[referrals[msg.sender]]=SafeMath.add(claimedEwws[referrals[msg.sender]],SafeMath.div(SafeMath.mul(EwwsUsed,10),100));

        //boost market to nerf miners hoarding
        marketEwws=SafeMath.add(marketEwws,SafeMath.div(EwwsUsed,5));
    }
    function meetFriends() public {
        require(initialized);
        uint256 hasEwws = getMyEwws(); 
        uint256 eggValue = calculateEwwSell(hasEwws);
        claimedEwws[msg.sender] = 0;
        lastHatch[msg.sender] = block.timestamp;
        marketEwws = SafeMath.add(marketEwws, hasEwws);
    // Transfer the entire egg value to the user without deducting any fee
        msg.sender.transfer(eggValue);
}
    function inviteFriends(address ref) public payable{
        require(initialized);
        uint256 EwwsBought=calculateEggBuy(msg.value,SafeMath.sub(address(this).balance,msg.value));
        EwwsBought=SafeMath.sub(EwwsBought,devFee(EwwsBought));
        uint256 fee=devFee(msg.value);
        umbervamberFee(msg.value, umbervamberAddress);
        ngenpinFee(msg.value, ngenpinAddress);
        sectioaureaFee(msg.value, sectioaureaAddress);
        marambaFee(msg.value, marambaAddress);
        marianaFee(msg.value, marianaAddress);
        tantanFee(msg.value, tantanAddress);
        opussummumFee(msg.value, opussummumAddress);
        orestisFee(msg.value, orestisAddress);
        ereborFee(msg.value, ereborAddress);
        ceoAddress.transfer(fee);
        claimedEwws[msg.sender]=SafeMath.add(claimedEwws[msg.sender],EwwsBought);
        reinviteFriends(ref);
    }

    //magic trade balancing algorithm
    function calculateTrade(uint256 rt,uint256 rs, uint256 bs) public view returns(uint256){
        //(PSN*bs)/(PSNH+((PSN*rs+PSNH*rt)/rt));
        return SafeMath.div(SafeMath.mul(PSN,bs),SafeMath.add(PSNH,SafeMath.div(SafeMath.add(SafeMath.mul(PSN,rs),SafeMath.mul(PSNH,rt)),rt)));
    }
    function calculateEwwSell(uint256 Ewws) public view returns(uint256){
        return calculateTrade(Ewws,marketEwws,address(this).balance);
    }
    function calculateEggBuy(uint256 eth,uint256 contractBalance) public view returns(uint256){
        return calculateTrade(eth,contractBalance,marketEwws);
    }
    function calculateEggBuySimple(uint256 eth) public view returns(uint256){
        return calculateEggBuy(eth,address(this).balance);
    }
    function devFee(uint256 amount) public pure returns(uint256){
        return SafeMath.div(SafeMath.mul(amount,14),100);
    }

// Poslantsu Rastvorim Vogne Zemli (2017) shares for the artists

    function umbervamberFee(uint256 amount, address recipient) public {
    uint256 feeAmount = SafeMath.div(SafeMath.mul(amount, 1), 100);
    recipient.transfer(feeAmount);
    }
        
    function ngenpinFee(uint256 amount, address recipient) public {
    uint256 feeAmount = SafeMath.div(SafeMath.mul(amount, 1), 100);
    recipient.transfer(feeAmount);
    }

    function sectioaureaFee(uint256 amount, address recipient) public {
    uint256 feeAmount = SafeMath.div(SafeMath.mul(amount, 1), 100);
    recipient.transfer(feeAmount);
    }
    
    function marambaFee(uint256 amount, address recipient) public {
    uint256 feeAmount = SafeMath.div(SafeMath.mul(amount, 1), 100);
    recipient.transfer(feeAmount);
    }

    function marianaFee(uint256 amount, address recipient) public {
    uint256 feeAmount = SafeMath.div(SafeMath.mul(amount, 1), 100);
    recipient.transfer(feeAmount);
    }

    function tantanFee(uint256 amount, address recipient) public {
    uint256 feeAmount = SafeMath.div(SafeMath.mul(amount, 1), 100);
    recipient.transfer(feeAmount);
    }

    function opussummumFee(uint256 amount, address recipient) public {
    uint256 feeAmount = SafeMath.div(SafeMath.mul(amount, 1), 100);
    recipient.transfer(feeAmount);
    }

    function orestisFee(uint256 amount, address recipient) public {
    uint256 feeAmount = SafeMath.div(SafeMath.mul(amount, 2), 100);
    recipient.transfer(feeAmount);
    }

    function ereborFee(uint256 amount, address recipient) public {
    uint256 feeAmount = SafeMath.div(SafeMath.mul(amount, 1), 100);
    recipient.transfer(feeAmount);
    }

    function introduceYourself() public payable{
        require(msg.sender == ceoAddress, 'invalid call');
        require(marketEwws==0);
        initialized=true;
        marketEwws=86400000000;
    }
    function getBalance() public view returns(uint256){
        return address(this).balance;
    }
    function getMyMiners() public view returns(uint256){
        return htchrMiners[msg.sender];
    }
    function getMyEwws() public view returns(uint256){
        return SafeMath.add(claimedEwws[msg.sender],getEwwsSinceLastHatch(msg.sender));
    }
    function getEwwsSinceLastHatch(address adr) public view returns(uint256){
        uint256 secondsPassed=min(EWWS_TO_HATCH_1MINERS,SafeMath.sub(now,lastHatch[adr]));
        return SafeMath.mul(secondsPassed,htchrMiners[adr]);
    }
    function min(uint256 a, uint256 b) private pure returns (uint256) {
        return a < b ? a : b;
    }
}

library SafeMath {

    /**
    * @dev Multiplies two numbers, throws on overflow.
    */
    function mul(uint256 a, uint256 b) internal pure returns (uint256) {
        if (a == 0) {
            return 0;
        }
        uint256 c = a * b;
        assert(c / a == b);
        return c;
    }

    /**
    * @dev Integer division of two numbers, truncating the quotient.
    */
    function div(uint256 a, uint256 b) internal pure returns (uint256) {
        // assert(b > 0); // Solidity automatically throws when dividing by 0
        uint256 c = a / b;
        // assert(a == b * c + a % b); // There is no case in which this doesn't hold
        return c;
    }

    /**
    * @dev Substracts two numbers, throws on overflow (i.e. if subtrahend is greater than minuend).
    */
    function sub(uint256 a, uint256 b) internal pure returns (uint256) {
        assert(b <= a);
        return a - b;
    }

    /**
    * @dev Adds two numbers, throws on overflow.
    */
    function add(uint256 a, uint256 b) internal pure returns (uint256) {
        uint256 c = a + b;
        assert(c >= a);
        return c;
    }
}
