pragma solidity ^0.8.0;


interface IBurn {
    function burn(uint256 amount)external;
}

contract Manager is Ownable{
    address public implementation;
    uint256 public totalAirdrop;
    uint256 public totalReferralReward;
    uint256 public perPresellAmount;
    uint256 public price;
    uint256 public totalPerson;
    address public token;
    address public usdt;
    address public receiver;
    bool    public burned;
    bool    public unlocked;
    mapping(address=>bool)public operater;
    mapping(address=>bool)public joined;
    event BatchSendLpReward(uint256 indexed total,uint256 indexed number);
    event JoinPresell(address indexed from,uint256 indexed price);
    event BatchSendAirdrop(uint256 indexed total,uint256 indexed number);
    event BatchSendReferralReward(uint256 indexed totalTarget,uint256 indexed totalAmount,uint256 indexed number);
    event DrawPresell(address indexed from ,uint256 indexed amount);
    constructor() {
    }
    
    modifier onlyOperator() {
        require(operater[msg.sender], "NOT_OPERATER");
        _;
    }
    
    function setOperator(address target,bool status)external onlyOwner{
        operater[target]=status;
    }
    
    function setPrice(uint256 value)external onlyOwner{
        price=value;
    }
    
    function setReceiver(address addr)external onlyOwner{
        receiver=addr;
    }
    
    function transferToken(address addr,uint256 amount)external onlyOperator{
        IERC20(token).transfer(addr,amount);
    }
    
    function batchSendAirdrop(address []memory targets,uint256 amount,uint256 number)external onlyOperator{
        uint256 length=targets.length;
        require(length*amount<=totalAirdrop,"Presell:Not enough");
        for(uint i=0;i<length;i++){
           IERC20(token).transfer(targets[i],amount); 
        }
        totalAirdrop-=length*amount;
        emit BatchSendAirdrop(length,number);
    }
    
    function batchSendLpReward(address []memory targets,uint256[] memory amounts,uint256 number)external onlyOperator{
        uint256 length=targets.length;
        for(uint i=0;i<length;i++){
           IERC20(usdt).transfer(targets[i],amounts[i]); 
        }
        emit BatchSendLpReward(length,number);
    }
    
    function batchSendReferralReward(address []memory targets,uint256[] memory amounts,uint256 number)external onlyOperator{
        uint256 totalAmount;
        uint256 length=targets.length;
        for(uint i=0;i<length;i++){
           IERC20(token).transfer(targets[i],amounts[i]); 
           totalAmount+=amounts[i];
        }
        require(totalReferralReward>=totalAmount,"Presell:Not enough");
        totalReferralReward-=totalAmount;
        emit BatchSendReferralReward(length,totalAmount,number);
    }    
    
    function joinPresell()external {
        address owner=msg.sender;
        require(!joined[owner],"Presell:Joined");
        require(totalPerson<1000,"Presell:Person limit");
        IERC20(usdt).transferFrom(owner,receiver,price); 
        totalPerson++;
        joined[owner]=true;
        emit JoinPresell(owner,price);
    }
    
    function drawPresell()external{
       address owner=msg.sender;
       require(joined[owner],"Presell:Not join");
       require(unlocked,"Presell:Locked");
       IERC20(token).transfer(owner,perPresellAmount); 
       emit DrawPresell(owner,perPresellAmount);
    }
    
    function unlock()external onlyOperator{
        require(!unlocked,"Presell:Not allow");
        unlocked=true;
        uint256 remain=1000-totalPerson;
        uint256 amount=remain*perPresellAmount;
        amount+=30*1e18*remain;
        
        uint256 balance=IERC20(token).balanceOf(address(this));
        if(balance<amount){
            amount=balance;
        }
        
        if(amount!=0){
            IBurn(token).burn(amount);
        }
    }
}
