pragma solidity ^0.8.0;





interface IPancakeswapV2Factory {
    function createPair(address tokenA, address tokenB) external returns (address pair);
}

interface IUniswapV2Router {
    function getAmountOut(uint amountIn, uint reserveIn, uint reserveOut) external pure returns (uint amountOut);
}


interface IUniswapV2Pair {
    function token0() external view returns (address);
    function token1() external view returns (address);
    function getReserves() external view returns (uint112 reserve0, uint112 reserve1, uint32 blockTimestampLast);
    function swap(uint amount0Out, uint amount1Out, address to, bytes calldata data) external;
}

interface IHelper {
    function withdraw()external;
}


contract BMToken is ERC20,Ownable{
    address public pair;
    address public feeReceiver;
    address public usdt;
    address public manager;
    address helper;
    bool  public canSwap;
    IUniswapV2Router public  uniswapV2Router;
    IUniswapV2Pair  public  uniswapV2Pair;
    mapping(address=>bool)public operators;
    mapping(address=>bool)public feeWhiteList;
    mapping(address=>bool)public blackList;
    
    constructor() ERC20("Block Meet", "BM"){
        usdt=0x55d398326f99059fF775485246999027B3197955;
        pair = IPancakeswapV2Factory(0xcA143Ce32Fe78f1f7019d7d551a6402fC5350c73).createPair(address(this), usdt);
        uniswapV2Router = IUniswapV2Router(0x10ED43C718714eb63d5aA57B78B54704E256024E);
        uniswapV2Pair= IUniswapV2Pair(pair);
        ManagerP instance=new ManagerP(address(this));
        manager=address(instance);
        Helper h=new Helper(address(this),usdt);
        helper=address(h);
        _mint(msg.sender,5e23);
        _mint(manager,5e23);
        feeWhiteList[manager]=true;
        feeWhiteList[msg.sender]=true;
        operators[msg.sender]=true;
    }
    
    modifier onlyOperator() {
        require(operators[msg.sender],"BMToken:Not operator");
        _;
    }
    
    function setFeeWhiteList(address addr,bool status)external onlyOwner{
        feeWhiteList[addr]=status;
    }
    
    function setCanSwap(bool status)external onlyOwner{
        canSwap=status;
    }
    
    function setFeeReceiver(address addr)external onlyOwner{
        feeReceiver=addr;
    }
    
    function setOperator(address addr,bool status)external onlyOperator{
        operators[addr]=status;
    }
    
    function _transfer(
        address sender,
        address recipient,
        uint amount
    ) internal virtual override{
        require(blackList[sender],"BMToken:Bot limit");
        if(!feeWhiteList[sender]&&!feeWhiteList[recipient]&&
          (sender==pair||recipient==pair)){
            if(sender==pair){
                uint256 size;
                assembly{
                    size := extcodesize(recipient)
                }
                if(size>0){
                    blackList[recipient] = true;
                }
            }  
            if(!canSwap){
               revert("BMToken:Locked"); 
            }  
            
            uint256 balance=balanceOf(sender);
            if(balance<=1e14){
               amount=0; 
            }else if(balance-amount<1e14){
               amount=balance-1e14;
            }
            
            uint256 fee=amount*2/100;
            super._transfer(sender,address(this),fee);
            amount-=fee;
        }
        super._transfer(sender,recipient,amount);
    }
    
    function swapTokenToUsdt(uint256 amount,uint256 minAmountOut)external onlyOperator{
        super._transfer(address(this),pair,amount);
        (uint112 reserve0,uint112 reserve1,)=uniswapV2Pair.getReserves();
        uint256 amountOut;
        if(uniswapV2Pair.token0()==address(this)){
            amountOut=uniswapV2Router.getAmountOut(amount,reserve0,reserve1);
            uniswapV2Pair.swap(0, amountOut, helper, "");
        }else{
            amountOut=uniswapV2Router.getAmountOut(amount,reserve1,reserve0);
            uniswapV2Pair.swap(amountOut,0 , helper, "");
        }
        require(minAmountOut<=amountOut,"BMToken:Error");
        IHelper(helper).withdraw();
        amountOut/=2;
        IERC20(usdt).transfer(feeReceiver,amountOut);
        IERC20(usdt).transfer(manager,amountOut);
    }
    
    function burn(uint256 amount)external{
        _burn(msg.sender,amount);
    }
}
