interface Irelation {
    function relations(address user) external view returns (address);
}

contract RabbitTracker is OwnableUpgradeable {


    mapping(address=>address) private uplinesraw;
    mapping(address=>bool) public is_blacks;
    mapping(address=>bool) public is_excluded;
    uint public directFee;
    uint public managerFee;
    address public _manager;
    address public _marketing;
    uint public starttime;
    uint public de;
    address public _pair;
    using EnumerableSet for *;
    mapping(address=>EnumerableSet.AddressSet) directsusers;
    mapping(address=>EnumerableSet.AddressSet) indirectsusers;
    Irelation public _upline;

    function inilialize(address manager) public initializer {
        __Ownable_init();
        _manager=manager;
        is_excluded[_manager]=true;
        setFee(100,2,2);
        starttime=1735664461;
    }
    function setIrelation(address add) public onlyOwner {
        _upline=Irelation(add);
    }
    function uplines(address input) public view returns(address) {
        address upline=_upline.relations(input);
        if(upline==address(0))
            return _marketing;
        return upline;
    }

    function setManager(address m) public onlyOwner {
        _manager=m;
    }
    function setMarketing(address m) public onlyOwner {
        _marketing=m;
    }
    function settStart(uint time) public onlyOwner {
        starttime=time;
    }
    function setPair(address add) public onlyOwner {
        _pair=add;
    }
    

    function check(address from,address to) public view  returns(bool flag){ 
        // require(!is_blacks[from] && !is_blacks[to],"[rabbit:]:black");//检查黑名单
        // require(block.timestamp>=starttime || is_excluded[from] || is_excluded[to],"[rabbit]: no open");//检查是否开盘
        flag = false ;
        if(is_blacks[from] && is_blacks[to]){
            flag=true;
            return flag;
        }
        if(is_excluded[from] || is_excluded[to]){
            return flag;
        }
        bool f=(_pair==from) || (to==from);

        if(block.timestamp<=starttime && f){
            flag=true;
        }
        return flag;
    }

    function setFee(uint d,uint df,uint mf) public onlyOwner {
        de=d;
        directFee=df;
        managerFee=mf;
    }
    //检查是否为黑名单,绑定上级
    event bind(address indexed user,address indexed upline);
    function bindupline(address upline) public {
        if (uplinesraw[msg.sender]!=address(0) || upline==_msgSender()) {
            return;//只能绑定一次
        }
        //检查循环
        address u=uplinesraw[upline];
        while (u!=address(0)) {
            if(u==_msgSender()) {
                //有循环
                revert("circle bind");
            }
            u=uplinesraw[u];
        }
        uplinesraw[msg.sender]=upline;
        adduplineList(msg.sender);
        emit bind(_msgSender(),upline);
    }
    //添加列表
    function adduplineList(address user) public {
        if(uplinesraw[user]!=address(0)) {
            //加直推
            directsusers[uplinesraw[user]].add(user);
        }
        //加间推
        if(uplinesraw[uplinesraw[user]]!=address(0)) {
            indirectsusers[uplinesraw[uplinesraw[user]]].add(user);
        }
    }
    //获取直推与间推列表
    function getdirectList(address user) public view returns(address[] memory directList, address[] memory indirectList) {
        directList= directsusers[user].values();
        indirectList=indirectsusers[user].values();
    }
    //获取间推
    // function getindirectList(address user) public view returns(address[] memory) {
    //     return indirectsusers[user].values();
    // }
    // //获取总人数
    // function getNum(address user) public view returns(uint num) {
    //     directsusers[user]
    // }
    //计算手续费,
    //设置黑名单
    function setBlack(address[] memory users,bool flag) public onlyOwner {
        for(uint i=0;i<users.length;i++) {
            is_blacks[users[i]]=flag;
        }
    }

    //设置白名单
    function setExcluded(address[] memory users,bool flag) public onlyOwner {
        for(uint i=0;i<users.length;i++) {
            is_excluded[users[i]]=flag;
        }
    }
}
