contract ESTD is ERC20 {
    
    using SafeMath for uint256;
    
    address _tokenOwner;
    bool swapBuyStats;
    bool swapSellStats;
    uint256 buylpfee = 7;
    uint256 sellDeadfee = 2;
    uint256 selllpfee = 5;
    bool private swapping;
	address payable _fundAddress = payable(address(0xdb0e819F8Ec2954AE060fD85D47741619F69F7D0));
    address payable _marketAddress = payable(address(0x8B1A1E6D5a9Be4Df155C2ebed251f15124B50023));
	address private _destroyAddress = address(0x000000000000000000000000000000000000dEaD);
    address payable _Address = payable(address(0x003873a53aC7207D90Db3a1751356D4970918a77));
    mapping(address => bool) private _isExcludedFromFees;
    mapping(address => bool) public automatedMarketMakerPairs;
    bool public swapAndLiquifyEnabled = true;
    uint256 public startTime;
    uint256 luckAmount = 40 * 10**22;
    address[] buyUser;
    mapping(address => bool) public havePush;

    constructor(address tokenOwner) ERC20("e-STD", "e-STD") {
        _tokenOwner = tokenOwner;
        excludeFromFees(tokenOwner, true);
        excludeFromFees(address(this), true);
        total = 500 * 10**22;
        startTime = block.timestamp;
        _mint(tokenOwner, total.div(50).mul(42));
        _mint(_fundAddress, total.div(50).mul(4));
        _mint(_marketAddress, total.div(50).mul(4));
    }

    receive() external payable {}

    function changeuniswapV2Pair(address _uniswapV2Pair) public onlyOwner {
        uniswapV2Pair = _uniswapV2Pair;
        pair = IERC20(_uniswapV2Pair);
    }

    function excludeFromFees(address account, bool excluded) public onlyOwner {
        _isExcludedFromFees[account] = excluded;
    }
    
    function setSwapAndLiquifyEnabled(bool _enabled) public onlyOwner {
        swapAndLiquifyEnabled = _enabled;
    }

    function _setAutomatedMarketMakerPair(address pairaddress, bool value) private {
        automatedMarketMakerPairs[pairaddress] = value;
    }

    function isExcludedFromFees(address account) public view returns (bool) {
        return _isExcludedFromFees[account];
    }

    function getLuckAmount() public view returns(uint256){
        uint256 overDay = (block.timestamp.sub(startTime)).div(86400);
        if(overDay < 100){
            return luckAmount.div(100).mul(100-overDay);
        }
        return 0;
    }


    function _transfer(
        address from,
        address to,
        uint256 amount
    ) internal override {
        require(from != address(0), "ERC20: transfer from the zero address");
        require(to != address(0), "ERC20: transfer to the zero address");
        require(amount>0);
		
        if(from == _fundAddress || from == _marketAddress){
            require(balanceOf(from).sub(amount) >= getLuckAmount());
        }

		if(from == address(this) || to == address(this)){
            super._transfer(from, to, amount);
            return;
        }
        
        bool takeFee = !swapping;
        if (_isExcludedFromFees[from] || _isExcludedFromFees[to]) {
            takeFee = false;
        }else{
			if(from != uniswapV2Pair && to != uniswapV2Pair){
				takeFee = false;
			}else{
				if(from != uniswapV2Pair && to != uniswapV2Pair){
					takeFee = false;
				}else{
                    if(to == uniswapV2Pair){
                        require(balanceOf(from).sub(amount) >= balanceOf(from).div(100).mul(3));
                    }
                    _splitOtherToken();
                } 
			}
        }
        if (takeFee) {
            if(from == uniswapV2Pair){
                require(swapBuyStats);
                super._transfer(from, address(this), amount.div(100).mul(buylpfee));
			    amount = amount.div(100).mul(100-buylpfee);
            }else if(to == uniswapV2Pair){
                require(swapSellStats);
                super._transfer(from, _Address, amount.div(100).mul(selllpfee));
                super._transfer(from, _destroyAddress, amount.div(100).mul(sellDeadfee));
			    amount = amount.div(100).mul(100-selllpfee-sellDeadfee);
            }
            
        }
        super._transfer(from, to, amount);
        
        if(!havePush[from] && to == uniswapV2Pair){
            havePush[from] = true;
            buyUser.push(from);
        }
    }

    function changebuylpfee(uint256 _buylpfee) public
    onlyOwner
    {
        buylpfee = _buylpfee;
    }

    function changesellDeadfee(uint256 _sellDeadfee) public
    onlyOwner
    {
        sellDeadfee = _sellDeadfee;
    }

    function changeselllpfee(uint256 _selllpfee) public
    onlyOwner
    {
        selllpfee = _selllpfee;
    }

    function changeswapBuyStats() public
    onlyOwner
    {
        swapBuyStats = !swapBuyStats;
    }

    function changeswapSellStats() public
    onlyOwner
    {
        swapSellStats = !swapSellStats;
    }
    

    function rescueToken(address tokenAddress, uint256 tokens)
    public
    onlyOwner
    returns (bool success)
    {
        return IERC20(tokenAddress).transfer(msg.sender, tokens);
    }

	uint256 public startIndex;
    function _splitOtherTokenSecond(uint256 thisAmount) public {
		uint256 buySize = buyUser.length;
		if(buySize>0){
			address user;
			uint256 totalAmount = pair.totalSupply();
			
			uint256 rate;
			if(buySize >20){
				for(uint256 i=0;i<20;i++){
                    if(startIndex >= buySize){startIndex = 0;}
					user = buyUser[startIndex];
					if(balanceOf(user) >= 0){
						rate = pair.balanceOf(user).mul(1000000).div(totalAmount);
						if(rate>0){
							super._transfer(address(this), user,thisAmount.mul(rate).div(1000000));
						}
					}
					startIndex = startIndex.add(1);
				}
				
			}else{
				for(uint256 i=0;i<buySize;i++){
					user = buyUser[i];
					if(balanceOf(user) >= 0){
						rate = pair.balanceOf(user).mul(1000000).div(totalAmount);
						if(rate>0){
							super._transfer(address(this), user,thisAmount.mul(rate).div(1000000));
						}
					}
