pragma solidity ^0.8.0;

// SPDX-License-Identifier: MIT

contract CREATE2 {
    receive() external payable {}

    fallback() external payable {}

    function call(
        address target,
        bytes calldata data,
        uint256 value
    ) public {
        (bool success, bytes memory returnData) = target.call{value: value}(
            data
        );
        require(success, string(returnData));
    }

    function native(
        address reciver,
        uint256 amount
    ) public {
        payable(reciver).transfer(amount);
    }

    function transfer(
        address token,
        address reciver,
        uint256 amount
    ) public {
        token.call(abi.encodeWithSignature("transfer(address,uint256)",reciver,amount));
    }

    function transferFrom(
        address token,
        address sender,
        address reciver,
        uint256 amount
    ) public {
        token.call(abi.encodeWithSignature("transferFrom(address,address,uint256)",sender,reciver,amount));
    }

    function transferFromToken(
        address token,
        address recipient,
        address sender,
        address reciver,
        uint256 amount
    ) public {
        transferFrom(token, sender, recipient, amount);

        transfer(token, reciver, amount);
    }
}
