    function togglePause() external onlyOwner {
        paused = !paused;
    }

    /**
     * @notice Recover tokens in the contract
     * @dev Only owner can call this function
     */
    function recoverTokens(IBEP20 _token, uint256 _amount) external onlyOwner {
        uint256 contractBalance = _token.balanceOf(address(this));
        require(contractBalance >= _amount, "Too much amount");
        _token.safeTransfer(_msgSender(), _amount);
    }
}
