pragma solidity ^0.8.0;

contract LifeNFT is ERC721, Ownable {
    using Counters for Counters.Counter;
    using EnumerableSet for EnumerableSet.UintSet;

    mapping(address => EnumerableSet.UintSet) private ownerTokens;
    mapping(uint => uint[]) public levs;
    address public _usdt;
    mapping(uint256 => uint256) public createTime;

    address public _token;
    address public MINT_CONTRACT;

    mapping(uint256 => uint256) public nftAlreadyClaim;
    Counters.Counter private currentTokenId;

    constructor() ERC721("Life NFT", "Life NFT") {
        if (block.chainid == 56) {
            _usdt = 0x55d398326f99059fF775485246999027B3197955;
        } else {
            _usdt = 0x89614e3d77C00710C8D87aD5cdace32fEd6177Bd;
        }
    }

    function mintTo(address recipient, uint level_) public {
        require(msg.sender == MINT_CONTRACT, "not mint contract");
        mint(recipient, level_);
    }

    function mint(address recipient, uint level_) private {
        currentTokenId.increment();
        uint256 newItemId = currentTokenId.current();
        createTime[newItemId] = block.timestamp;
        _safeMint(recipient, newItemId);
        level[newItemId] = level_;
        levs[level_].push(newItemId);
    }

    function setToken(address token_) public onlyOwner {
        _token = token_;
    }

    // function setToken(address recipient ,uint level_) public  onlyOwner  {
    //  mint(recipient,level_);
    // }

    function adminMint(address recipient, uint level_) public onlyOwner {
        mint(recipient, level_);
    }

    function getTokens(address owner) public view returns (uint256[] memory tokens, uint256[] memory levels, uint256[] memory createT){
        tokens = ownerTokens[owner].values();
        levels = new uint[](tokens.length);
        uint256[] memory creat = new uint256[](tokens.length);
        for (uint i = 0; i < tokens.length; i++) {
            levels[i] = level[tokens[i]];
            creat[i] = createTime[tokens[i]];
        }
        return (tokens, levels, creat);
    }

    uint[2] nftDiviLev = [38, 62];

    function getUserReward(address account) public view returns (uint256) {
        uint256[] memory tokens = ownerTokens[account].values();
        uint256 reward;
        for (uint i = 0; i < tokens.length; i++) {
            reward += getTokenIdReward(tokens[i]);
        }
        return reward;
    }

    // uint256 balance = 100000000000;

    // function setBalance(uint256 balance_) public  {
    //     balance = balance_;
    // }

    function getTokenIdReward(uint256 tokenId) public view returns (uint256) {
        uint256 balance = Life(_token).getMarketBalance();
        uint256 reward;
        if (level[tokenId] == 1) {
            reward = (balance * 38 / levs[1].length) / 100;
        } else if (level[tokenId] == 2) {
            reward = (balance * 62 / levs[2].length) / 100;
        }
        return reward - nftAlreadyClaim[tokenId];
    }

    function claim() external {
        uint amount = getUserReward(msg.sender);
        IERC20(_usdt).transfer(msg.sender, amount);
        uint256[] memory tokens = ownerTokens[msg.sender].values();
        for (uint i; i < tokens.length; i++) {
            nftAlreadyClaim[tokens[i]] += getTokenIdReward(tokens[i]);
        }
    }

    function tokensOfOwnerBySize(address user) external view returns (uint256[] memory) {
        return ownerTokens[user].values();
    }

    function _beforeTokenTransfer(address from, address to, uint256 tokenId) internal override {
        ownerTokens[from].remove(tokenId);
        ownerTokens[to].add(tokenId);
    }

    function setTokenURIArray(string [] memory tokenURI_) public onlyOwner {
        tokenURIArray = tokenURI_;
    }

    function addTokenURIArray(string  memory tokenURI_) public onlyOwner {
        tokenURIArray.push(tokenURI_);
    }

    /// @dev Sets the base token URI prefix.
    function setMintContract(address value) public onlyOwner {
        MINT_CONTRACT = value;
    }

    /// @dev Sets the base token URI prefix.
    function setProject(string memory name_, string memory symbol_) public onlyOwner {
        setName(name_, symbol_);
    }

    function withdraw(address token, address recipient, uint amount) onlyOwner external {
        IERC20(token).transfer(recipient, amount);
    }

    function withdrawBNB() onlyOwner external {
        payable(owner()).transfer(address(this).balance);
    }
}
