    "contracts/SuspendMaster.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.18;

import "@openzeppelin/contracts/access/extensions/AccessControlEnumerable.sol";
import "@openzeppelin/contracts/utils/Pausable.sol";

interface ISymmio {
    function setSuspendedAddress(
        address user,
        bool isSuspended
    ) external;
}

contract SuspendMaster is Pausable, AccessControlEnumerable {
    address public symmioAddress;

    bytes32 public constant SUSPENDER_ROLE = keccak256("SUSPENDER_ROLE");
    bytes32 public constant UNSUSPENDER_ROLE = keccak256("UNSUSPENDER_ROLE");
    bytes32 public constant PAUSER_ROLE = keccak256("PAUSER_ROLE");

    constructor(address admin, address symmioAddress_) {
        symmioAddress = symmioAddress_;
        _grantRole(DEFAULT_ADMIN_ROLE, admin);
    }

    function setSymmioAddress(address symmioAddress_) external onlyRole(DEFAULT_ADMIN_ROLE) {
        symmioAddress = symmioAddress_;
    }

    function suspend(address[] memory wallets) external whenNotPaused onlyRole(SUSPENDER_ROLE) {
        for (uint256 i = 0; i < wallets.length; i++) {
            address user = wallets[i];
            ISymmio(symmioAddress).setSuspendedAddress(user, true);
        }
    }

    function unsuspend(address[] memory wallets) external whenNotPaused onlyRole(UNSUSPENDER_ROLE) {
        for (uint256 i = 0; i < wallets.length; i++) {
            address user = wallets[i];
            ISymmio(symmioAddress).setSuspendedAddress(user, false);
        }
    }

    function pause() external onlyRole(PAUSER_ROLE) {
        _pause();
    }

    function unpause() external onlyRole(DEFAULT_ADMIN_ROLE) {
        _unpause();
    }
}
