    "contracts/StakingV2.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.19;

import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/extensions/IERC20Permit.sol";

contract StakingV2 is Ownable {
    address public immutable nfp;

    uint256 public startTime;
    uint256 public endTime;

    uint256 public stakingAmount = 1288 * 1e18;

    mapping(address => uint256) public staked;

    event Staked(address user);
    event Withdrawn(address user);

    constructor(
        address _nfp,
        uint256 _startTime,
        uint256 _endTime
    ) Ownable(msg.sender) {
        if (_nfp == address(0)) {
            revert("0 address");
        }
        nfp = _nfp;
        startTime = _startTime;
        endTime = _endTime;
    }

    function changeStakingAmount(uint256 _amount) external onlyOwner {
        if (block.timestamp >= startTime) {
            revert("Started");
        }
        stakingAmount = _amount;
    }

    function stake() public {
        if (block.timestamp >= startTime) {
            revert("Started");
        }
        // check if already staked
        if (staked[msg.sender] > 0) {
            revert("Already staked");
        }
        staked[msg.sender] = stakingAmount;
        IERC20(nfp).transferFrom(msg.sender, address(this), stakingAmount);
        emit Staked(msg.sender);
    }

    function stakeWithPermit(
        uint256 deadline,
        uint8 v,
        bytes32 r,
        bytes32 s
    ) external {
        IERC20Permit(nfp).permit(
            msg.sender,
            address(this),
            stakingAmount,
            deadline,
            v,
            r,
            s
        );
        stake();
    }

    function withdraw() external {
        if (block.timestamp < endTime) {
            revert("Not ended");
        }

        if (staked[msg.sender] == 0) {
            revert("No staking");
        }

        uint256 amount = staked[msg.sender];
        staked[msg.sender] = 0;
        IERC20(nfp).transfer(msg.sender, amount);
        emit Withdrawn(msg.sender);
    }
}
