    "contracts/TRT_STAKING.sol": {
      "content": "// SPDX-License-Identifier: MIT
pragma solidity ^0.8.0;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";
import "@openzeppelin/contracts/token/ERC20/utils/SafeERC20.sol";
import "@openzeppelin/contracts/access/Ownable.sol";
import "@openzeppelin/contracts/utils/math/SafeMath.sol";
import "@openzeppelin/contracts/token/ERC20/extensions/IERC20Metadata.sol";
import "@openzeppelin/contracts/security/ReentrancyGuard.sol";

contract TRT_STAKING is Ownable, ReentrancyGuard {
    using SafeERC20 for IERC20;
    using SafeMath for uint256;

    // treasury wallet
    address public treasuryWallet;

    struct Stake {
        uint256 amount;
        uint256 startTime;
        uint256 endTime;
        uint256 rewardClaimed;
        bool active;
    }

    struct Pool {
        uint256 stakingCap;
        uint256 totalStaked;
        uint256 rewardRate;
        uint256 lockDays;
        uint256 stakingTokenDecimals;
        uint256 rewardTokenDecimals;
        address stakingAddress;
        address rewardAddress;
    }

    mapping(uint256 => Pool) private pools;
    mapping(uint256 => mapping(address => Stake[])) public userStakes; // Separate mapping for user stakes
    mapping(address => uint256) public totalStakedbyuser;
    uint256 public nextPoolId;
    uint256 public totalStaked;

    event Staked(
        address indexed user,
        uint256 indexed poolId,
        uint256 stakeId,
        uint256 amount,
        uint256 endTime
    );
    event Withdrawn(
        address indexed user,
        uint256 indexed poolId,
        uint256 stakeId,
        uint256 amount
    );
    event RewardClaimed(
        address indexed user,
        uint256 indexed poolId,
        uint256 amount
    );

    /**
     * @dev Contract constructor.
     * @param _treasuryWallet The address of the treasury wallet.
     */
    constructor(address _treasuryWallet) {
        treasuryWallet = _treasuryWallet;
    }

    /**
     * @dev Transfers a given amount of currency.
     * @param _currency The address of the currency token.
     * @param _from The address from which the transfer is made.
     * @param _to The address to which the transfer is made.
     * @param _amount The amount to transfer.
     */
    function transferCurrency(
        address _currency,
        address _from,
        address _to,
        uint256 _amount
    ) internal {
        if (_amount == 0) {
            return;
        }
        safeTransferERC20(_currency, _from, _to, _amount);
    }

    /**
     * @dev Transfer `amount` of ERC20 token from `from` to `to`.
     * @param _currency The address of the ERC20 token.
     * @param _from The address from which the transfer is made.
     * @param _to The address to which the transfer is made.
     * @param _amount The amount to transfer.
     */

    function safeTransferERC20(
        address _currency,
        address _from,
        address _to,
        uint256 _amount
    ) internal {
        if (_from == _to) {
            return;
        }

        if (_from == address(this)) {
            IERC20(_currency).safeTransfer(_to, _amount);
        } else {
            IERC20(_currency).safeTransferFrom(_from, _to, _amount);
        }
    }

    /**
     * @dev Creates a new staking pool.
     * @param _stakingCap The maximum amount that can be staked in the pool.
     * @param _rewardRate The reward rate for staking.
     * @param _stakingToken The address of the staking ERC20 token.
     * @param _rewardToken The address of the reward ERC20 token.
     * @param _lockDays The lock-up period for staking in days.
     */
    function createPool(
        uint256 _stakingCap,
        uint256 _rewardRate,
        address _stakingToken,
        address _rewardToken,
        uint256 _lockDays
    ) external onlyOwner {
        pools[nextPoolId] = Pool({
            stakingCap: _stakingCap,
            totalStaked: 0,
            rewardRate: _rewardRate,
            stakingAddress: _stakingToken,
            rewardAddress: _rewardToken,
            stakingTokenDecimals: IERC20Metadata(_stakingToken).decimals(),
            rewardTokenDecimals: IERC20Metadata(_rewardToken).decimals(),
            lockDays: _lockDays
        });

        nextPoolId++;
    }

    /**
     * @dev Stakes a given amount in a specific pool.
     * @param _poolId The ID of the staking pool.
     * @param _amount The amount to stake.
     */
    function stake(uint256 _poolId, uint256 _amount) external nonReentrant {
        require(_poolId < nextPoolId, "Invalid pool ID");
        Pool storage pool = pools[_poolId];
        require(
            pool.totalStaked.add(_amount) <= pool.stakingCap,
            "Exceeds staking cap"
        );

        transferCurrency(
            pool.stakingAddress,
            msg.sender,
            address(this),
            _amount
        );

        Stake memory newStake = Stake({
            amount: _amount,
            startTime: block.timestamp,
            endTime: block.timestamp + pool.lockDays * 1 days,
            rewardClaimed: 0,
            active: true
        });

        userStakes[_poolId][msg.sender].push(newStake);
        pool.totalStaked = pool.totalStaked.add(_amount);
        totalStakedbyuser[msg.sender] = totalStakedbyuser[msg.sender].add(
            _amount
        );
        totalStaked = totalStaked.add(_amount);
        uint256 stakeId = userStakes[_poolId][msg.sender].length - 1;

        emit Staked(
            msg.sender,
            _poolId,
            stakeId,
            _amount,
            block.timestamp + pool.lockDays * 1 days
        );
    }

    /**
     * @dev Ends a specific stake in a pool, claiming the rewards.
     * @param _poolId The ID of the staking pool.
     * @param _stakeId The ID of the stake to end.
     */
    function EndStake(uint256 _poolId, uint256 _stakeId) external nonReentrant {
        require(_poolId < nextPoolId, "Invalid pool ID");
        require(
            _stakeId < userStakes[_poolId][msg.sender].length,
            "Invalid stake ID"
        );
        Stake storage userStake = userStakes[_poolId][msg.sender][_stakeId];
        Pool storage pool = pools[_poolId];
        require(userStake.active, "Stake is not active"); // Add check for active stake
        require(block.timestamp >= userStake.endTime, "Lockup period not over");
        userStake.active = false;
        uint256 reward = calculateReward(_poolId, msg.sender, _stakeId);
        userStake.rewardClaimed = reward;
        _mintRewards(pool.rewardAddress, msg.sender, reward);
        uint256 amountToWithdraw = userStake.amount;
        pool.totalStaked = pool.totalStaked.sub(amountToWithdraw);
        totalStakedbyuser[msg.sender] = totalStakedbyuser[msg.sender].sub(
            amountToWithdraw
        );
        totalStaked = totalStaked.sub(amountToWithdraw);

        transferCurrency(
            pool.stakingAddress,
            address(this),
            msg.sender,
            amountToWithdraw
        );
        emit Withdrawn(msg.sender, _poolId, _stakeId, amountToWithdraw);
    }

    /**
     * @dev Withdraws a specific stake in a pool without claiming rewards.
     * @param _poolId The ID of the staking pool.
     * @param _stakeId The ID of the stake to withdraw.
     */
    function withdraw(uint256 _poolId, uint256 _stakeId) external nonReentrant {
        require(_poolId < nextPoolId, "Invalid pool ID");
        require(
            _stakeId < userStakes[_poolId][msg.sender].length,
            "Invalid stake ID"
        );
        Pool storage pool = pools[_poolId];
        Stake storage userStake = userStakes[_poolId][msg.sender][_stakeId];
        require(userStake.active, "Stake is not active"); // Add check for active stake
        userStake.active = false;
        require(block.timestamp >= userStake.endTime, "Lockup period not over");
        uint256 amountToWithdraw = userStake.amount;
        pool.totalStaked = pool.totalStaked.sub(amountToWithdraw);
        totalStakedbyuser[msg.sender] = totalStakedbyuser[msg.sender].sub(
            amountToWithdraw
        );
        totalStaked = totalStaked.sub(amountToWithdraw);
        transferCurrency(
            pool.stakingAddress,
            address(this),
            msg.sender,
            amountToWithdraw
        );
        emit Withdrawn(msg.sender, _poolId, _stakeId, amountToWithdraw);
    }

    /**
     * @dev Calculates the rewards for a specific stake.
     * @param _poolId The ID of the staking pool.
     * @param _user The address of the staker.
     * @param _stakeIndex The index of the stake in the user's stakes array.
     * @return reward The calculated reward for the stake.
     */
    function calculateReward(
        uint256 _poolId,
        address _user,
        uint256 _stakeIndex
    ) internal view virtual returns (uint256) {
        Pool storage pool = pools[_poolId];
        Stake storage userStake = userStakes[_poolId][_user][_stakeIndex];

        require(
            _stakeIndex < userStakes[_poolId][_user].length,
            "Invalid stake index"
        );
        uint256 elapsedTime = (block.timestamp - userStake.startTime);

        // Ensure that elapsedTime does not exceed the lockup period
        elapsedTime = elapsedTime > pool.lockDays * 1 days
            ? pool.lockDays * 1 days
            : elapsedTime;
        uint256 reward = userStake
            .amount
            .mul(elapsedTime)
            .mul(pool.rewardRate)
            .mul(10 ** pool.rewardTokenDecimals)
            .div(365 days)
            .div(100)
            .div(10 ** pool.stakingTokenDecimals); // Use reward token decimals from the pool // Use staking token decimals from the pool

        return reward;
    }

    // Function to update the treasuryWallet address
    function setTreasuryWallet(address _newTreasuryWallet) external onlyOwner {
        require(
            _newTreasuryWallet != address(0),
            "Invalid treasury wallet address"
        );
        treasuryWallet = _newTreasuryWallet;
    }

    /// @dev Mint/Transfer ERC20 rewards to the staker.
    function _mintRewards(
        address _rewardToken,
        address _staker,
        uint256 _rewards
    ) internal {
        IERC20 rewardToken = IERC20(_rewardToken);
        require(
            rewardToken.allowance(treasuryWallet, address(this)) >= _rewards,
            "Insufficient allowance"
        );
        require(
            rewardToken.balanceOf(treasuryWallet) >= _rewards,
            "Insufficient balance"
        );
        transferCurrency(_rewardToken, treasuryWallet, _staker, _rewards);
    }

    /**
     * @dev Retrieves information about a specific stake.
     * @param _poolId The ID of the staking pool.
     * @param _user The address of the staker.
     * @param _stakeIndex The index of the stake in the user's stakes array.
     * @return amount The staked amount.
     * @return startTime The start time of the stake.
     * @return endTime The end time of the stake.
     * @return rewardClaimed The amount of rewards claimed for the stake.
     */
    function getStakeInfo(
        uint256 _poolId,
        address _user,
        uint256 _stakeIndex
    )
        external
        view
        returns (
            uint256 amount,
            uint256 startTime,
            uint256 endTime,
            uint256 rewardClaimed
        )
    {
        require(
            _stakeIndex < userStakes[_poolId][_user].length,
            "Invalid stake index"
        );

        Stake memory stakeInfo = userStakes[_poolId][_user][_stakeIndex];

        return (
            stakeInfo.amount,
            stakeInfo.startTime,
            stakeInfo.endTime,
            stakeInfo.rewardClaimed
        );
    }

    /**
     * @dev Retrieves the total number of stakes for a specific user in a pool.
     * @param _poolId The ID of the staking pool.
     * @param _user The address of the staker.
     * @return The total number of stakes.
     */
    function getStakeList(
        uint256 _poolId,
        address _user
    ) external view returns (uint256) {
        return userStakes[_poolId][_user].length;
    }

    /**
     * @notice Get information about a specific pool.
     * @param _poolId The ID of the pool to retrieve information for.
     * @return stakingCap The maximum amount that can be staked in the pool.
     * @return totalstaked The total amount currently staked in the pool.
     * @return rewardRate The rate at which rewards are distributed.
     * @return lockDays The number of days tokens are locked in the pool.
     * @return stakingTokenDecimals The number of decimals for the staking token.
     * @return rewardTokenDecimals The number of decimals for the reward token.
     * @return stakingAddress The address of the staking token.
     * @return rewardAddress The address of the reward token.
     */
    function getPoolInfo(
        uint256 _poolId
    )
        external
        view
        returns (
            uint256 stakingCap,
            uint256 totalstaked,
            uint256 rewardRate,
            uint256 lockDays,
            uint256 stakingTokenDecimals,
            uint256 rewardTokenDecimals,
            address stakingAddress,
            address rewardAddress
        )
    {
        require(_poolId < nextPoolId, "Invalid pool ID");

        Pool storage pool = pools[_poolId];

        return (
            pool.stakingCap,
            pool.totalStaked,
            pool.rewardRate,
            pool.lockDays,
            pool.stakingTokenDecimals,
            pool.rewardTokenDecimals,
            pool.stakingAddress,
            pool.rewardAddress
        );
    }
}
