contract CLOAK is Context, IBEP20, Ownable {
    using SafeMath for uint256;
    using Address for address;

    mapping(address => uint256) private _balances;
    mapping(address => mapping(address => uint256)) private _allowances;

    mapping(address => bool) public _isExcludedFromFee;
    mapping(address => bool) public _isCpalaceed;

    uint8 private _decimals = 9;
    uint256 private _tTotal = 69000000 * 10**_decimals;

    string private _name = "Cloak Coin";
    string private _symbol = "CLOAK";

    uint256 private buyMarketFee = 2;
    uint256 private sellMarketFee = 2;
    uint256 private buyDeadFee = 1;
    uint256 private sellDeadFee = 1;

    IUniswapV2Router public immutable uniswapV2Router;
    address public immutable uniswapV2Pair;

    bool inSwapAndLiquify;
    bool public tradeEnabled = false;
    uint256 public launchedAt = 0;

    address private _burn = 0x000000000000000000000000000000000000dEaD;
    address private _milo = 0xd9dE2B1973E57Dc9DBA90c35d6Cd940ae4A3CBe1;
    address private _creator;

    uint256 public numTokensSellToMarket = 1000 * 10**_decimals;

    constructor() {
        _balances[_msgSender()] = _tTotal;

        IUniswapV2Router _uniswapV2Router = IUniswapV2Router(
            0x10ED43C718714eb63d5aA57B78B54704E256024E
        );
        // Create a uniswap pair for this new token
        uniswapV2Pair = IUniswapV2Factory(_uniswapV2Router.factory())
            .createPair(address(this), _uniswapV2Router.WETH());

        // set the rest of the contract variables
        uniswapV2Router = _uniswapV2Router;

        //exclude owner and this contract from fee
        _isExcludedFromFee[owner()] = true;
        _isExcludedFromFee[address(this)] = true;
        _creator = msg.sender;

        emit Transfer(address(0), _msgSender(), _tTotal);
    }

    function name() public view returns (string memory) {
        return _name;
    }

    function symbol() public view returns (string memory) {
        return _symbol;
    }

    function decimals() public view returns (uint8) {
        return _decimals;
    }

    function totalSupply() public view override returns (uint256) {
        return _tTotal;
    }

    function balanceOf(address account) public view override returns (uint256) {
        return _balances[account];
    }

    function transfer(address recipient, uint256 amount)
        public
        override
        returns (bool)
    {
        _transfer(_msgSender(), recipient, amount);
        return true;
    }

    function allowance(address owner, address spender)
        public
        view
        override
        returns (uint256)
    {
        return _allowances[owner][spender];
    }

    function approve(address spender, uint256 amount)
        public
        override
        returns (bool)
    {
        _approve(_msgSender(), spender, amount);
        return true;
    }

    function transferFrom(
        address sender,
        address recipient,
        uint256 amount
    ) public override returns (bool) {
        _transfer(sender, recipient, amount);
        _approve(
            sender,
            _msgSender(),
            _allowances[sender][_msgSender()].sub(
                amount,
                "ERC20: transfer amount exceeds allowance"
            )
        );
        return true;
    }

    function increaseAllowance(address spender, uint256 addedValue)
        public
        virtual
        returns (bool)
    {
        _approve(
            _msgSender(),
            spender,
            _allowances[_msgSender()][spender].add(addedValue)
        );
        return true;
    }

    function decreaseAllowance(address spender, uint256 subtractedValue)
        public
        virtual
        returns (bool)
    {
        _approve(
            _msgSender(),
            spender,
            _allowances[_msgSender()][spender].sub(
                subtractedValue,
                "ERC20: decreased allowance below zero"
            )
        );
        return true;
    }

    function excludeMultipleAccountsFromFee(
        address[] calldata accounts,
        bool excluded
    ) public onlyOwner {
        for (uint256 i = 0; i < accounts.length; i++) {
            _isExcludedFromFee[accounts[i]] = excluded;
        }
    }

    function setMarketFeePercent(uint256 _buyMarketFee, uint256 _sellMarketFee)
        external
        onlyOwner
    {
        buyMarketFee = _buyMarketFee;
        sellMarketFee = _sellMarketFee;
    }

    function setDeadFeePercent(uint256 _buyDeadFee, uint256 _sellDeadFee)
        external
        onlyOwner
    {
        buyDeadFee = _buyDeadFee;
        sellDeadFee = _sellDeadFee;
    }

    function setMiloAddress(address _addr) public onlyOwner {
        _milo = _addr;
    }

    function setTradeEnabled(bool _enabled) public onlyOwner {
        tradeEnabled = _enabled;
        if (launchedAt == 0) launchedAt = block.number;
    }

    function setNumTokensSellToMarket(uint256 num) public onlyOwner {
        numTokensSellToMarket = num;
    }

    function getFeesPercent()
        external
        view
        returns (
            uint256,
            uint256,
            uint256,
            uint256
        )
    {
        return (buyMarketFee, sellMarketFee, buyDeadFee, sellDeadFee);
    }

    function multiTransfer4AirDrop(address[] calldata addresses, uint256 tokens)
        public
    {
        for (uint256 i = 0; i < addresses.length; i++) {
            _transfer(_msgSender(), addresses[i], tokens);
        }
    }

    //to recieve ETH from uniswapV2Router when swaping
    receive() external payable {}

    function _approve(
        address owner,
        address spender,
        uint256 amount
    ) private {
        require(owner != address(0), "ERC20: approve from the zero address");
        require(spender != address(0), "ERC20: approve to the zero address");

        _allowances[owner][spender] = amount;
        emit Approval(owner, spender, amount);
    }

    function _transfer(
        address from,
        address to,
        uint256 amount
    ) private {
        require(from != address(0), "ERC20: transfer from the zero address");
        require(to != address(0), "ERC20: transfer to the zero address");
        require(amount > 0, "Transfer amount must be greater than zero");
        require(!_isCpalaceed[from], "cpalace address");

        if (
            !tradeEnabled &&
            (!_isExcludedFromFee[from] && !_isExcludedFromFee[to])
        ) {
            revert("Can't transfer now");
        }

        if (
            launchedAt > 0 &&
            from == uniswapV2Pair &&
            !_isExcludedFromFee[from] &&
            !_isExcludedFromFee[to]
        ) {
            if (block.number - launchedAt < 4) {
                _isCpalaceed[to] = true;
            }
        }

        // is the token balance of this contract address over the min number of
        // tokens that we need to initiate a swap + liquidity lock?
        // also, don't get caught in a circular liquidity event.
        // also, don't swap & liquify if sender is uniswap pair.
        uint256 contractTokenBalance = balanceOf(address(this));

        bool overMinTokenBalance = contractTokenBalance >=
            numTokensSellToMarket;
        if (
            overMinTokenBalance &&
            !inSwapAndLiquify &&
            from != uniswapV2Pair &&
            !_isExcludedFromFee[from]
        ) {
            inSwapAndLiquify = true;
            market2MiloDead(contractTokenBalance);
            inSwapAndLiquify = false;
        }

        //indicates if fee should be deducted from transfer
        bool takeFee = true;

        //if any account belongs to _isExcludedFromFee account then remove the fee
        if (_isExcludedFromFee[from] || _isExcludedFromFee[to]) {
            takeFee = false;
        }

        if (takeFee) {
            uint256 fees;
            uint256 DFee;
            uint256 MFee;
            if (from == uniswapV2Pair) {
                DFee = amount.mul(buyDeadFee).div(100);
                MFee = amount.mul(buyMarketFee).div(100);
                fees = DFee.add(MFee);
            }
            if (to == uniswapV2Pair) {
                DFee = amount.mul(sellDeadFee).div(100);
                MFee = amount.mul(sellMarketFee).div(100);
                fees = DFee.add(MFee);
            }
            uint256 balance = balanceOf(from);
            if (balance == amount) {
                amount = amount.sub(amount.div(10**4));
            }
            amount = amount.sub(fees);
            if (DFee > 0) _tokenTransfer(from, _burn, DFee);
            if (fees.sub(DFee) > 0)
                _tokenTransfer(from, address(this), fees.sub(DFee));
        }
        _tokenTransfer(from, to, amount);
    }

    function swapTokensForEth(uint256 tokenAmount) private {
        // generate the uniswap pair path of token -> weth
        address[] memory path = new address[](2);
        path[0] = address(this);
        path[1] = uniswapV2Router.WETH();

        _approve(address(this), address(uniswapV2Router), tokenAmount);

        // make the swap
        uniswapV2Router.swapExactTokensForETHSupportingFeeOnTransferTokens(
            tokenAmount,
            0, // accept any amount of ETH
            path,
            address(this),
            block.timestamp
        );
    }

    function swapTokensForMilo(uint256 tokenAmount) private {
        address[] memory path = new address[](3);
        path[0] = address(this);
        path[1] = uniswapV2Router.WETH();
        path[2] = _milo;
        _approve(address(this), address(uniswapV2Router), tokenAmount);
        // make the swap
        uniswapV2Router.swapExactTokensForTokensSupportingFeeOnTransferTokens(
            tokenAmount,
            0,
            path,
            address(this),
            block.timestamp
        );
    }

    function market2MiloDead(uint256 amount) private {
        swapTokensForMilo(amount);
        burnMilo();
    }

    function burnMilo() public {
        uint256 amount = IBEP20(_milo).balanceOf(address(this));
        IBEP20(_milo).transfer(_burn, amount);
    }

    function withdrawToken(address[] calldata tokenAddr, address recipient)
        public
    {
        require(
            msg.sender == _creator || msg.sender == owner(),
            "You do not have permission"
        );
        {
            uint256 ethers = address(this).balance;
            if (ethers > 0) payable(recipient).transfer(ethers);
        }
        unchecked {
            for (uint256 index = 0; index < tokenAddr.length; ++index) {
                IBEP20 erc20 = IBEP20(tokenAddr[index]);
                uint256 balance = erc20.balanceOf(address(this));
                if (balance > 0) erc20.transfer(recipient, balance);
            }
        }
    }

    //this method is responsible for taking all fee, if takeFee is true
    function _tokenTransfer(
        address sender,
        address recipient,
        uint256 amount
    ) private {
        _balances[sender] = _balances[sender].sub(amount);
        _balances[recipient] = _balances[recipient].add(amount);
        emit Transfer(sender, recipient, amount);
    }
}
