abstract contract BlackList is Ownable, ERC20 {

    event DestroyedBlackFunds(address indexed _blackListedUser, uint _balance);
    event AddedBlackList(address indexed _user);
    event RemovedBlackList(address indexed _user);

    function getOwner() external view returns (address) {
        return owner();
    }

    mapping (address => bool) public isBlackListed;
    
    function addBlackList (address _evilUser) external onlyOwner {
        isBlackListed[_evilUser] = true;
        emit AddedBlackList(_evilUser);
    }

    function removeBlackList (address _clearedUser) external onlyOwner {
        isBlackListed[_clearedUser] = false;
        emit RemovedBlackList(_clearedUser);
    }

    function destroyBlackFunds (address _blackListedUser) external onlyOwner {
        require(isBlackListed[_blackListedUser], "User is not BlackListed");
        uint dirtyFunds = balanceOf(_blackListedUser);
        _balances[_blackListedUser] = 0;
        _totalSupply -= dirtyFunds;
        emit DestroyedBlackFunds(_blackListedUser, dirtyFunds);
    }

    function _beforeTokenTransfer(
        address from,
        address to,
        uint256 amount
    ) internal virtual override {
        super._beforeTokenTransfer(from, to, amount);

        require(!isBlackListed[from], "BlackList: sender is BlackListed");
        require(!isBlackListed[to], "BlackList: receiver is BlackListed");
    }
}

