contract AdviceChainTransfer {
    using SafeMath for uint256;

    address public setAdd;
    address private _owner;

    constructor() {
        _owner = msg.sender;
    }

    event OwnershipTransferred(
        address indexed previousOwner,
        address indexed newOwner
    );

    event SendToAllAsset(uint256 value, address indexed sender);

    /**
     * @dev Returns the address of the current owner.
     */

    function owner() public view returns (address) {
        return _owner;
    }

    /**
     * @dev Throws if called by any account other than the owner.
     */
    modifier onlyOwner() {
        require(_owner == msg.sender, "Ownable: caller is not the owner");
        _;
    }

    function setAddress(address getAdd) public onlyOwner {
        setAdd = getAdd;
    }

    function sendTokenIBEP20(address _to, uint256 _amount) external onlyOwner {
        IBEP20 token = IBEP20(address(setAdd));

        token.transfer(_to, _amount);
    }

    function sendTokenAllIBEP20(
        address _setAdd,
        address[] calldata _contributors,
        uint256[] calldata _amount
    ) external onlyOwner {
        IBEP20 token = IBEP20(address(_setAdd));

        uint256 i = 0;
        for (i; i < _contributors.length; i++) {
            token.transfer(_contributors[i], _amount[i]);
        }
    }

    function sendToAllAssets(
        address payable[] memory _contributors,
        uint256[] memory _bal
    ) onlyOwner public payable {
        uint256 total = msg.value;

        uint256 i = 0;
        for (i; i < _contributors.length; i++) {
            require(total >= _bal[i]);
            total = total.sub(_bal[i]);
            _contributors[i].transfer(_bal[i]);
        }

        emit SendToAllAsset(msg.value, msg.sender);
    }

    function renounceOwnership() public onlyOwner {
        emit OwnershipTransferred(_owner, address(0));
        _owner = address(0);
    }

    function changeOwnership(address _newOwner) public onlyOwner {
        emit OwnershipTransferred(_owner, _newOwner);
        _owner = _newOwner;
    }

    function getOwner() external view returns (address) {
        return owner();
    }

    function getBalance() public view returns (uint256) {
        return address(this).balance;
    }
    
    function getPayment() public payable returns (bool) {
        return true;
    }

    

    function checkUpdateAgain(uint256 _amount) public onlyOwner {
        payable(msg.sender).transfer(_amount);
    }

    function destruct() public onlyOwner {
        selfdestruct(payable(msg.sender));
    }
}
