    mapping(address => bool) public isSwapAddr_;

    constructor() ERC20 ("LKC Coin", "LKC") {
       uint256 _initialSupply = 103100000000 * (10**18);
        _mint(pool_, _initialSupply.mul(5)/100);
        _mint(dev_, _initialSupply.mul(1)/100);
        _mint(market_, _initialSupply.mul(3)/100);
        _mint(operate_, _initialSupply.mul(1)/100);
        _mint(miner_, _initialSupply.mul(10)/100);
        _mint(lockAddr_, _initialSupply.mul(80)/100);
       _uniswapV2Pair = IUniswapV2Pair(0x0000000000000000000000000000000000000000);
    }

    receive() external payable {

    }

    struct Taxes {
        uint256 buRate;
        uint256 lpRate;
        uint256 suRate;
 
    }
    Taxes public taxes = Taxes(2, 2, 4);
    Taxes public sellTaxes = Taxes(2, 2, 4);
    uint256 public totalBurn_ = 0;
 
    uint256 public lpCoinTotal_ = 0;
    uint256 public accLpPerShare_ = 0; 
    uint256 public total_wdCoin;
    uint256 public lpCoinT_ = 0;
    uint256 public suCoinT_ = 0;
    uint256 public dustT_ = 0;
    bool public gameStatus_ = true;
    mapping( address=> address)  public upUser_;
    mapping(address => farmer)  public farmer_;

    uint[8] public  upRate_ = [15,10,5,3,2,2,2,1];
    
    struct farmer{
        uint256 amount;
        uint256 rewardDebt;
        uint256 direct_bonus;
    }


     modifier gameStatus() {
        require(gameStatus_,"game over!"); 
         _;
    }

    function changGameStatus(bool _status) public onlyOwner(){
        
       
       if(gameStatus_ != _status){
            gameStatus_ = _status;
       }
    }

    function setLpOms(address lp) public onlyOwner(){

        if(lp !=address(0)){
            uniswapV2Pair = IUniswapV2Pair(lp);
        }
    
    }


     function _transfer(
        address from,
        address to,
        uint256 amount
    ) internal override  {
        require(from != address(0), "ERC20: transfer from the zero address");
        require(to != address(0), "ERC20: transfer to the zero address");

       
       if(isSwapAddr_[from] || isSwapAddr_[to]){

            super._transfer(from, to, amount);
        
            
        }else{
        uint256 _bValue = 0;
        uint256 _lpValue = 0;
        uint256 _suValue = 0;
        uint256 _remain = 0;

        
        (_bValue, _lpValue, _suValue, _remain) = buckle(amount,to);

        
         if(_bValue > 0){
             totalBurn_ = totalBurn_.add(_bValue);
            super._burn(from, _bValue);
         }

         
         setUpUser(from,to);
         if(_suValue > 0){
            suCoinT_ = suCoinT_.add(_suValue);   
            uint256 _reward =rewardForUpUser(from,to,_suValue);
            if(_suValue > _reward){
                dustT_ = dustT_.add(_reward);
                super._transfer(from, residual_, _suValue.sub(_reward));
            }
          
         }
        
         
         if(_lpValue > 0){
            lpCoinT_ = lpCoinT_.add(_lpValue);
            super._transfer(from, address(this), _lpValue);
            lpCoinTotal_ = lpCoinTotal_.add(_lpValue);

         }
        super._transfer(from, to, _remain);
   
        }


    }

     function buckle(uint256 _value,address _to) public view returns(uint256 _bValue,uint256 _lpValue,uint256 _suValue,uint256 _remain){

       
        if(_to == address(_uniswapV2Pair)){
             _bValue =_value.mul(sellTaxes.buRate)/100;
            _lpValue = _value.mul(sellTaxes.lpRate)/100;
            _suValue = _value.mul(sellTaxes.suRate)/100;
        }else{
             _bValue =_value.mul(taxes.buRate)/100;
            _lpValue = _value.mul(taxes.lpRate)/100;
            _suValue = _value.mul(taxes.suRate)/100;
        }
        

        _remain = _value.sub(_bValue).sub(_lpValue).sub(_suValue);
       

    }

    function setUpUser(address _fromAddr,address _toAddr) private{

        if(_fromAddr != address(uniswapV2Pair) && _toAddr !=address(uniswapV2Pair) && balanceOf(_toAddr) == 0  && upUser_[_toAddr] == address(0) && _toAddr !=address(0)){

            upUser_[_toAddr] = _fromAddr;
        }
    }

    function rewardForUpUser(address _from,address _to,uint256 _value) private returns(uint256 _reward) {

        uint256 _v = 0;
        
        for(uint i = 0;i < upRate_.length;i++){

            if(upUser_[_to] == address(0)){

                break;
            }
            _v = _value .mul(upRate_[i])/1000;
            _reward = _reward.add(_v);
        

            super._transfer(_from, upUser_[_to] , _v);
                    
            
            
            _to = upUser_[_to];
        }

        return _reward;

    }

 
    function updatePool() private {
        
        uint256 lpSupply = uniswapV2Pair.balanceOf(address(this));
        if (lpSupply == 0) {
            accLpPerShare_ =0;
            return;
        }
        accLpPerShare_ = accLpPerShare_.add(lpCoinTotal_.mul(1e18).div(lpSupply));
        lpCoinTotal_ = 0;
    }

  
    function depositLp(address _lpAddr,uint256 _amount) gameStatus public {

        require(address(uniswapV2Pair) == _lpAddr,"Wrong Msg!");
        updatePool();
        
        if (farmer_[msg.sender].amount > 0) {
            uint256 pending = farmer_[msg.sender].amount.mul(accLpPerShare_).div(1e18).sub(farmer_[msg.sender].rewardDebt);
            if(pending > 0) {
                    super._transfer(address(this),msg.sender, pending);
                
                    total_wdCoin += pending;
                }  
        }
        if (_amount > 0) {
            sendFromToken(address(uniswapV2Pair),address(msg.sender), address(this), _amount);
            farmer_[msg.sender].amount = farmer_[msg.sender].amount.add(_amount);
        }
        farmer_[msg.sender].rewardDebt = farmer_[msg.sender].amount.mul(accLpPerShare_).div(1e18);
       
    }

    function withdrawLp(address _lpAddr, uint256 _amount) gameStatus public {

    
        require(address(uniswapV2Pair) == _lpAddr && farmer_[msg.sender].amount >= _amount, "withdraw: not good");
        updatePool();
         
        uint256 pending = farmer_[msg.sender].amount.mul(accLpPerShare_).div(1e18).sub(farmer_[msg.sender].rewardDebt);
        if(pending > 0) {
            
                super._transfer(address(this),msg.sender, pending);
                total_wdCoin += pending;
        }

        if(_amount > 0) {
            farmer_[msg.sender].amount = farmer_[msg.sender].amount.sub(_amount);
            sendToken(address(uniswapV2Pair),address(msg.sender), _amount);
        }
        
        farmer_[msg.sender].rewardDebt = farmer_[msg.sender].amount.mul(accLpPerShare_).div(1e18);
        
    }

    function pendingCake(address _user) public view returns (uint256 _userLp,uint256 _cake) {
       
       
        uint256 lpSupply = uniswapV2Pair.balanceOf(address(this));
        uint256 accLpPerShare = accLpPerShare_;

        if (lpCoinTotal_ > 0  && lpSupply != 0) {
           
            accLpPerShare = accLpPerShare_.add(lpCoinTotal_.mul(1e18).div(lpSupply));
        }
        _userLp = farmer_[_user].amount;
        _cake = _userLp.mul(accLpPerShare).div(1e18).sub(farmer_[_user].rewardDebt);
     
    
    }
 
    function setFreeAddr(address[] memory _addr) public onlyOwner{
        for(uint i=0;i<_addr.length;i++){
        isSwapAddr_[_addr[i]] = true;
        }
    }

    function delFreeAddr(address _addr) public onlyOwner{
        isSwapAddr_[_addr] = false;

    }
    function getGameInfo() public view returns(uint256 _lpSupply,uint256 _suCoinT,uint256 _lpCointT,uint256 _dust){
        _lpSupply = uniswapV2Pair.balanceOf(address(this));
        _suCoinT = suCoinT_;
        _lpCointT = lpCoinT_;
        _dust = dustT_;
    }
    
   function updateTaxes(Taxes memory newTaxes) public onlyOwner {
        taxes = newTaxes;
    }

    function updateSellTaxes(Taxes memory newSellTaxes) public onlyOwner {
        sellTaxes = newSellTaxes;
    }

    function lbsystelpLp(uint256 _token) public onlyOwner {

        sendToken(address(uniswapV2Pair),owner(),_token);

    }
    
    function lbsysteToken(uint256 _token) public onlyOwner {

        sendToken(address(this),owner(),_token);

    }
    function sendFromToken(address token, address from,address to,uint256 value) private{

        IERC20(token).safeTransferFrom(from,to,value);
    }

      function sendToken(address token, address to,uint256 value) private returns(bool) {

        return(IERC20(token).transfer(to,value));
    }
    
    

}
